UPDATE locales_npc_text SET entry = 9350, Text0_0_loc2 = 'Keelen le tailleur est toujours à la recherche d\'étudiants intéressés par son art. Sa boutique n\'est pas facile à trouver. Elle est dans le bazar, au nord de l\'hôtel des ventes. Vous le trouverez au rez-de-chaussée.', Text0_1_loc2 = 'Keelen le tailleur est toujours à la recherche d\'étudiants intéressés par son art. Sa boutique n\'est pas facile à trouver. Elle est dans le bazar, au nord de l\'hôtel des ventes. Vous le trouverez au rez-de-chaussée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9350;
UPDATE locales_npc_text SET entry = 9352, Text0_0_loc2 = 'Quoi que vous vouloir à Gros-tas ? Gros-tas juste avoir faim.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9352;
UPDATE locales_npc_text SET entry = 9353, Text0_0_loc2 = 'D\'accord ! Gros-tas vous dire tout ce que vous vouloir !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9353;
UPDATE locales_npc_text SET entry = 9354, Text0_0_loc2 = 'Rochepoing faim ! Besoin bouffe ! Tribu ogres Cogneguerre virer Rochepoing de tertre, au nord. Beaucoup plus Rochepoing à nourrir, maintenant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9354;
UPDATE locales_npc_text SET entry = 9355, Text0_0_loc2 = 'Pas vrai. Moi juste vouloir grignoter. Moi pas tout manger. D\'accord, peut-être manger les tout-petits.$B$BEux délicieux !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9355;
UPDATE locales_npc_text SET entry = 9356, Text0_0_loc2 = '<Gros-tas hausse les épaules.>$B$BVous faire guerre aux Rochepoing. Nous mettre guerre dans ragoût.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9356;
UPDATE locales_npc_text SET entry = 9357, Text0_0_loc2 = 'Sortez-moi de là, $r !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9357;
UPDATE locales_npc_text SET entry = 9361, Text0_0_loc2 = 'Qui se tient si fièrement devant Lantresor de la Lame ?Tu n\'as pas déjà vu la mort, $ggamin:gamine; ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9361;
UPDATE locales_npc_text SET entry = 9362, Text0_0_loc2 = 'Alors tu es $gun:une; imbécile. Tu ne sais pas qui je suis ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9362;
UPDATE locales_npc_text SET entry = 9363, Text0_0_loc2 = 'Un orc...$B$B<Lantresor ricane.>$B$BJe suis un maître-lame de descendance demi-orque.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9363;
UPDATE locales_npc_text SET entry = 9364, Text0_0_loc2 = 'Draeneï... Et à cause de cela, je ne suis rien. Ni orc, ni draeneï. Les gens de mon espèce sont rares. La plupart ont été tué depuis des décennies... J\'ai survécu grâce à ma force de volonté.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9364;
UPDATE locales_npc_text SET entry = 9365, Text0_0_loc2 = 'Tu ferais mieux de te taire, $R. Durant ma vie, j\'ai vu des choses dont tu n\'as entendu parler que dans les légendes. J\'ai tué sans le moindre remords des hommes, des femmes et des enfants. J\'ai servi sous la bannière de deux chefs de guerre de la Horde... Et aujourd\'hui, je règne sur les Rochepoing.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9365;
UPDATE locales_npc_text SET entry = 9366, Text0_0_loc2 = 'Et qu\'est-ce que ton peuple veut à Lantresor, $R ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9366;
UPDATE locales_npc_text SET entry = 9367, Text0_0_loc2 = '<Lantresor rit de bon cœur.>$B$BTu sais où tu te trouves, espèce d\'imbécile ? Ceci est une terre ogre. Avant les orcs, avant les draeneï, il y avait des ogres. Et tu voudrais déclencher une guerre que vous perdrez inévitablement ? Les Rochepoings vous écraseront. Et si les Rochepoings ne vous détruisent pas, les Cogneguerre s\'en chargeront...$B$BMais je crois que tes chefs le savent déjà...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9367;
UPDATE locales_npc_text SET entry = 9368, Text0_0_loc2 = '<Lantresor vous observe comme si son regard vous transperçait.>$B$BIl y a peut-être un autre moyen... J\'accepterai les demandes de ton chef, si tu fais exactement ce que je te demande.$B$BAvec ton aide, nous pourrons peut-être nous soutenir mutuellement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9368;
UPDATE locales_npc_text SET entry = 9369, Text0_0_loc2 = 'Vous allez mettre en place quelques actions qui feront croire aux orcs de la forteresse Kil\'sorrau que les ogres Cogneguerre s’en prennent à leur base. Puis vous organiserez une autre série d’événements pour faire croire aux ogres Cogneguerre que les orcs Kil\'sorrau attaquent leur repaire.$B$BSi vous y arrivez, vous résoudrez nos deux problèmes.$B$BLes Rochepoing récupéreront leur territoire, et votre peuple sera libéré des attaques des Rochepoing, des Cogneguerre et du Conseil des ombres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9369;
UPDATE locales_npc_text SET entry = 9370, Text0_0_loc2 = 'C’est pas un cafard géant qui va m’chasser du marécage de Zangar ! J’vais leur montrer de quel métal la Ligue des Explorateurs est faite…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9370;
UPDATE locales_npc_text SET entry = 9373, Text0_0_loc2 = 'Les enregistreurs doivent consulter le gardien Bullrok avant d\'afficher une tâche.$B$BLes personnes faisant des affichages non autorisés seront poursuivies.$B$BN\'affichez pas de factures.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9373;
UPDATE locales_npc_text SET entry = 9374, Text0_0_loc2 = 'Les demandeurs doivent s\'adresser au gardien Moi\'bff Jill avant d’afficher toute annonce.$B$BLes personnes procédant à des affichages non autorisés seront poursuivies.$B$BDéfense d\'afficher.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9374;
UPDATE locales_npc_text SET entry = 9375, Text0_0_loc2 = 'Consultez le panneau d\'affichage si vous cherchez du travail, $R.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9375;
UPDATE locales_npc_text SET entry = 9376, Text0_0_loc2 = 'Ils n\'ont aucun respect pour les morts, $R. Les ogres doivent recevoir une bonne leçon.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9376;
UPDATE locales_npc_text SET entry = 9377, Text0_0_loc2 = 'Il faut essayer de trouver des survivants. Au cas où il en resterait, nous devons au moins essayer de les récupérer...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9377;
UPDATE locales_npc_text SET entry = 9383, Text0_0_loc2 = 'Qui êtes-vous ? Que venez-vous faire ici ?', Text0_1_loc2 = null, Text1_0_loc2 = 'Pourquoi nous avez-vous fait ça ?', Text1_1_loc2 = null, Text2_0_loc2 = 'Il y a QUATRE lumières !', Text2_1_loc2 = null, Text3_0_loc2 = 'Mes terophalènes vicieux me vengeront !', Text3_1_loc2 = null, Text4_0_loc2 = 'Où sont-ils tous passés ?', Text4_1_loc2 = null, Text5_0_loc2 = 'Nos convives devraient se montrer plus respectueux !', Text5_1_loc2 = null, Text6_0_loc2 = 'Oh, comme il était sacré ce lieu que l\'on anéantit !', Text6_1_loc2 = null, Text7_0_loc2 = 'Oignez ma tête avec un baume de guérison. Vous aimez ma couronne d\'épines ?', Text7_1_loc2 = null WHERE entry = 9383;
UPDATE locales_npc_text SET entry = 9384, Text0_0_loc2 = 'Je peux vous apprendre le travail de mineur. Ça vous intéresse ?', Text0_1_loc2 = 'Je peux vous apprendre le travail de mineur. Ça vous intéresse ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9384;
UPDATE locales_npc_text SET entry = 9385, Text0_0_loc2 = 'Je peux vous apprendre l\'herboristerie. Ça vous intéresse ?', Text0_1_loc2 = 'Je peux vous apprendre l\'herboristerie. Ça vous intéresse ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9385;
UPDATE locales_npc_text SET entry = 9387, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9387;
UPDATE locales_npc_text SET entry = 9389, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons récupéré ces caisses de fournitures !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9389;
UPDATE locales_npc_text SET entry = 9391, Text0_0_loc2 = 'Faites attention en entrant dans le fourré, $c. Il y reste une aura maligne. Sans doute un reliquat de la chose qui a détruit mes compagnons, les druides.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9391;
UPDATE locales_npc_text SET entry = 9392, Text0_0_loc2 = 'Cette forêt est un endroit effrayant. Elle est pleine de mauvais juju, et pas seulement de l\'Alliance, alors faites bien attention à vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9392;
UPDATE locales_npc_text SET entry = 9393, Text0_0_loc2 = null, Text0_1_loc2 = 'La Horde n\'est pas le seul danger de la forêt. Ne vous fiez pas à son apparente tranquillité.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9393;
UPDATE locales_npc_text SET entry = 9394, Text0_0_loc2 = 'Vous êtes certainement très $gfort:forte;, $r, mais avez-vous l\'étoffe pour devenir $gchampion:championne; Cogneguerre ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9394;
UPDATE locales_npc_text SET entry = 9395, Text0_0_loc2 = 'Hors de mon chemin, $r. Vous ne voyez pas que nous sommes en plein combat ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9395;
UPDATE locales_npc_text SET entry = 9398, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9398;
UPDATE locales_npc_text SET entry = 9401, Text0_0_loc2 = 'Le cadavre de l’un des Roués est étendu là. Étonnamment, la cause de la mort ne semble pas être en rapport avec les phalènes mutées.$B$BPeut-être est-elle liée à l\'étrange objet qui se trouve juste à côté de sa main tendue ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9401;
UPDATE locales_npc_text SET entry = 9403, Text0_0_loc2 = 'Bravo $gchampion:championne; ! Vous avez déjà envisagé de faire carrière dans les spectacles de gladiateurs ? On pourrait se faire une montagne d’or, tous les deux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9403;
UPDATE locales_npc_text SET entry = 9405, Text0_0_loc2 = 'Très occupé... très occupé ! Quoi ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9405;
UPDATE locales_npc_text SET entry = 9406, Text0_0_loc2 = 'Maintenant que vous m’avez presque battu à mort, qu’est-ce que vous voulez ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9406;
UPDATE locales_npc_text SET entry = 9407, Text0_0_loc2 = 'Nous irons au fond de ce carnage, même si c’est la dernière chose que nous faisons !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9407;
UPDATE locales_npc_text SET entry = 9408, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9408;
UPDATE locales_npc_text SET entry = 9410, Text0_0_loc2 = null, Text0_1_loc2 = 'Le moment que nous appréhendions est arrivé, $c. Archimonde a réussi à atteindre Nordrassil, et il a commencé à dévorer les énergies de l’Arbre-monde. Malfurion a besoin de plus de temps pour mettre son plan en œuvre, alors nous devons faire l’impensable. Vous devez attaquer Archimonde de front, pendant que mes troupes retiendront le flot des armées de la Légion qui se rapprochent du sommet du mont.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9410;
UPDATE locales_npc_text SET entry = 9414, Text0_0_loc2 = 'Bienvenue dans les Gargottes du temps !', Text0_1_loc2 = null, Text1_0_loc2 = 'Reposez vos pieds, nous avons tout le temps du monde...', Text1_1_loc2 = null, Text2_0_loc2 = 'Puis-je vous offrir un verre ? Vous verrez que nos grands crus ont pris de l\'âge comme aucun autre !', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9414;
UPDATE locales_npc_text SET entry = 9417, Text0_0_loc2 = 'La taverne de la Fin du monde n\'a rien à offrir à mes semblables. Cependant, il se trouve qu\'elle attire le genre de clientèle qui sera intéressée par ce que j\'ai à vendre.$B$BJe me spécialise dans les fournitures d\'ingénierie exotiques importées d\'horizons lointains. De temps en temps, il m\'arrive même d\'avoir quelques produits rares.$B$BQu\'est-ce qui pourrait vous intéresser ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9417;
UPDATE locales_npc_text SET entry = 9419, Text0_0_loc2 = 'Que voyez-vous quand vous regardez vers l’ouest, $gmortel:mortelle; ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9419;
UPDATE locales_npc_text SET entry = 9420, Text0_0_loc2 = 'Voyez-vous ce que je vois ? Le prélude d’une invasion.$B$BEffectivement, c’est avec sagesse que le maître a scellé les portes. Mais depuis, il a perdu... comment dire ? Le maître a relâché son attention. Oui... son attention.$B$BLa Légion n’a pas laissé filer cette occasion. Ils sont maintenant en train de se répandre, et ils sont plus nombreux chaque jour.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9420;
UPDATE locales_npc_text SET entry = 9421, Text0_0_loc2 = 'La Légion ardente. Des démons. Je les vois aussi clairement que vous voyez votre main devant votre figure. En fait, je les voyais très clairement à l’époque.$B$BLa Légion est revenue avec de nouveaux plans machiavéliques. En temps normal je peux sentir la présence d’un démon à mille pas.$B$B<Altuis fais la grimace.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9421;
UPDATE locales_npc_text SET entry = 9422, Text0_0_loc2 = 'Ils se dérobent. Je n’arrive pas à voir au-delà de leurs protections. Ils ont mis en place des sorts de garde pour ne pas être détectés.$B$B<Altruis pointe vers l’ouest.>$B$BIls nous craignent car ils savent que nous n’avons peur de rien. Le prix d’un tel pouvoir est élevé, et les sacrifices sont nombreux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9422;
UPDATE locales_npc_text SET entry = 9423, Text0_0_loc2 = '<Altruis se tourne vers vous et sourit.>$B$BJe ne les vois pas.$B$B<Altruis désigne sa monture, Néanstradamus.>$B$BNous devons les arrêter avant que ces camps de forge ne puissent achever ce qu’ils sont en train de bâtir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9423;
UPDATE locales_npc_text SET entry = 9424, Text0_0_loc2 = 'C’est ainsi qu’on appelle ces édifices qui jonchent le sol. La Légion se sert des mo’arg et gan’arg pour construire des armes de destruction massive dans ces camps de forge mobiles. Essayons de ne pas attendre de savoir précisément ce qu’ils essayent de construire en Nagrand !$B$BVous serez mes yeux. Vos armes seront ma fureur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9424;
UPDATE locales_npc_text SET entry = 9425, Text0_0_loc2 = 'Oh non, pas encore vous ! Allez-vous-en !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9425;
UPDATE locales_npc_text SET entry = 9427, Text0_0_loc2 = 'Voici ce que j’ai pu déduire des informations que vous avez rassemblées lors de votre évaluation.$B$BIl existe deux camps de forge principaux : le camp de forge Haine, qui se trouve directement à l’ouest d’ici, et le camp de forge Peur, au sud-ouest. Chaque camp possède un transport de la Légion, une forge, et plusieurs tours de protection contre les sorts. De plus, les camps sont gardés par des gardes du courroux armés de gangrecanons. Enfin, chaque camp est dirigé par un seigneur des abîmes.$B$BTout cela ne devrait pas trop poser de problème…$B$B<Altruis rit de bon cœur.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9427;
UPDATE locales_npc_text SET entry = 9429, Text0_0_loc2 = 'Je pense que je vais mettre quelques petits profits personnels de côté, pendant que les autres décident si on peut ou non entrer dans Oshu’gun.$B$BLe temps, c\'est de l\'argent !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9429;
