UPDATE locales_npc_text SET entry = 9432, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9432;
UPDATE locales_npc_text SET entry = 9433, Text0_0_loc2 = 'Les ruines des Flèches jumelles ne sont pas encore sécurisées ! Mes ordres sont de ne distribuer les étendards de bataille que lorsque les deux balises sont allumées et que la zone est sécurisée. Allez là-bas prendre le contrôle de ces balises !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9433;
UPDATE locales_npc_text SET entry = 9435, Text0_0_loc2 = '<Sal’salabim grogne dans votre direction, puis se remet à boire son breuvage mousseux.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9435;
UPDATE locales_npc_text SET entry = 9437, Text0_0_loc2 = '<Le gardien Arblos vous regarde d’une drôle de manière, puis s’arrête un moment. Il éprouve clairement des difficultés à parler.>$B$BLe marécage de Zangar… Trop proche de la vérité.$B$B<Son visage reprend une expression démente.>$B$BIl est toujours en train de regarder ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9437;
UPDATE locales_npc_text SET entry = 9438, Text0_0_loc2 = 'Quoi ? Le marécage de Zangar ? Pourquoi est-ce que j’irais là-bas ?$B$B<Arblos semble pensif.>$B$BOui, le marécage de Zangar… Nous… Les druides vont bientôt comprendre pourquoi le niveau de l’eau baisse. Cela détruit tout !$B$B<Vous voyez la folie revenir dans les yeux du gardien.>$B$BNous a tous détruit ! Une lumière brillante ! BOUM !!', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9438;
UPDATE locales_npc_text SET entry = 9439, Text0_0_loc2 = 'Les elfes de sang Aile-de-feu… Pour une raison ou une autre, ils veulent nous arrêter.$B$B<Le gardien Arblos lutte visiblement pour garder sa raison encore quelques secondes.>$B$BIls… Ils ont envoyé l’un des Roués comme… comme émissaire… Mais il avait une bombe !$B$BIls sont morts, ils sont tous morts ! Ils ont tenté de fuir ! Les plus chanceux n’ont rien su.$B$BJe me trouvais à la limite du rayon de l’explosion. C’était horr… IL NOUS REGARDE ENCORE !$B$B<Arblos prend un air abattu et son regard semble vous traverser, comme si vous n’existiez pas.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9439;
UPDATE locales_npc_text SET entry = 9440, Text0_0_loc2 = 'QUOI QU\'VOUS VOULEZ !?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9440;
UPDATE locales_npc_text SET entry = 9441, Text0_0_loc2 = 'Fshhhhhhsssfhhhhh?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9441;
UPDATE locales_npc_text SET entry = 9442, Text0_0_loc2 = '<Floon émet une série d\'étranges cliquettements et gloussements.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9442;
UPDATE locales_npc_text SET entry = 9443, Text0_0_loc2 = 'Vous êtes sérieux ? Il m\'a déjà tué ! Qu\'est-ce qu\'il peut faire de plus ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9443;
UPDATE locales_npc_text SET entry = 9444, Text0_0_loc2 = 'Merci encore, $N, pour m’avoir aidé à découvrir ce qui s’était passé dans le fourré.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9444;
UPDATE locales_npc_text SET entry = 9451, Text0_0_loc2 = null, Text0_1_loc2 = 'Mon travail est de m’occuper de ces pauvres diables. J’accepte volontiers l’aide de ceux qui sont prêts à donner un coup de main, qu’il s’agisse de l’Aldor ou des Clairvoyants.$B$BNous manquons tellement de personnel que je ne chasserais même pas un ogre s’il savait comment poser un garrot. La politique de la ville ne m’intéresse pas.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9451;
UPDATE locales_npc_text SET entry = 9452, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous vous rendez compte ? Cet éthérien n’a même pas un œil de basilic sur lui !$B$BEt son chef a le culot de se faire appeler « le prince des contrebandiers » !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9452;
UPDATE locales_npc_text SET entry = 9457, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue, $N. Nous appartenons à l’Aldor. Nous sommes des prêtres de la Lumière et des serviteurs des Sha\'tar.$B$BVous êtes $gle bienvenu:la bienvenue; chez nous, et vous pouvez rester ici tant que vous le désirez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9457;
UPDATE locales_npc_text SET entry = 9458, Text0_0_loc2 = null, Text0_1_loc2 = 'La meilleure traduction pour Sha\'tar serait « nés de la lumière ». C\'est le nom que nous donnons aux naaru qui ont repris la ville de Shattrath aux agents d\'Illidan.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9458;
UPDATE locales_npc_text SET entry = 9459, Text0_0_loc2 = null, Text0_1_loc2 = 'Shattrath est surtout un lieu de rassemblement dans la guerre contre la Légion ardente et Illidan. Elle sert de sanctuaire à tous ceux qui tentent d’échapper à la violence qui nous entoure.$B$BNous serions des hypocrites si nous repoussions les réfugiés.$B$BSi votre question se rapporte aux Clairvoyants, il n’est pas de notre ressort de remettre en question la sagesse des naaru.$B$BCe sont des temps difficiles, $N. Nous avons besoin de tous les alliés que nous pouvons trouver.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9459;
UPDATE locales_npc_text SET entry = 9471, Text0_0_loc2 = 'Bienvenue au Bastion allérien, $c.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9471;
UPDATE locales_npc_text SET entry = 9472, Text0_0_loc2 = 'Soyez prudent, $C. La forêt est pleine de dangers cachés et d\'ennemis potentiels.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9472;
UPDATE locales_npc_text SET entry = 9477, Text0_0_loc2 = 'C\'est horrible. Tout simplement horrible.$B$BJe me fiche des épreuves que nous vivons ici, mais il faut faire quelque chose pour apaiser la douleur des morts du désert des Ossements !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9477;
UPDATE locales_npc_text SET entry = 9478, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9478;
UPDATE locales_npc_text SET entry = 9481, Text0_0_loc2 = 'Si vous vous attendez à un accueil officiel, ou quelque chose de ce genre, vous allez être $gdéçu:déçue;, $c. On se moque des traditions, ici. Tout ce qu’on demande, c’est que ça marche.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9481;
UPDATE locales_npc_text SET entry = 9482, Text0_0_loc2 = 'Vous repérez une proposition intéressante au milieu de tous les bouts de parchemin cloués à cet avis de recherche. Il s’agit d’aller tuer des fouette-radius dans le désert des Ossements.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9482;
UPDATE locales_npc_text SET entry = 9484, Text0_0_loc2 = null, Text0_1_loc2 = 'Non, ne vous en faites pas. Ça m\'arrive tout le temps.$B$B<Taela a l\'air à la fois amusée et mécontente.>$B$BJe suis une HAUTE elfe, pas une elfe de sang. Ne vous inquiétez pas, je ne vais pas sucer toute votre magie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9484;
UPDATE locales_npc_text SET entry = 9485, Text0_0_loc2 = 'Les fouette-radius, je les hais tous !$B$BSi je n\'avais pas perdu mon œil, et que je ne souffrais pas de ces évanouissements de temps en temps, je passerais ma vie à en abattre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9485;
UPDATE locales_npc_text SET entry = 9486, Text0_0_loc2 = 'J\'ai observé les Aldor qui travaillent aux murs. Ils vont reconstruire toute la ville, vous savez.$B$BIls travaillent bien plus vite que le vieux contremaître Oslow, là-bas à Comté-du-lac. Il n\'arrêtait pas de perdre ses outils dans le lac, ou bien les gnolls lui prenaient son matériel.$B$BJe devrais demander aux Aldor de lui envoyer de l\'aide. Le pont serait réparé en un rien de temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9486;
UPDATE locales_npc_text SET entry = 9487, Text0_0_loc2 = null, Text0_1_loc2 = 'Cette forêt est immense, $C. Laissez les chamans jouer avec les esprits et les guerriers avec leurs épées. Mon avenir est à Terokkar, pour le meilleur ou le pire.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9487;
UPDATE locales_npc_text SET entry = 9488, Text0_0_loc2 = 'Malukaz n\'arrête pas de parler de l\'importance historique de cette forêt, mais je n\'ai pas le temps de l\'intéresser à l\'histoire tant que nos problèmes actuels ne seront pas résolus.$B$BJe ne dis pas que son travail est dépourvu d\'importance, mais il faut que quelqu\'un se charge des défenses et de toute la logistique quotidienne.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9488;
UPDATE locales_npc_text SET entry = 9489, Text0_0_loc2 = 'Avant de traverser Nagrand et de venir dans cette forêt, je redoutais que nous ayons perdu une grande partie de notre culture originelle. Heureusement, j\'avais tort.$B$BIl y a tant à faire ici !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9489;
UPDATE locales_npc_text SET entry = 9490, Text0_0_loc2 = null, Text0_1_loc2 = 'Après l\'ouverture de la Porte des ténèbres, j\'ai appris quel fut le sort de notre peuple. D\'une certaine manière, mon exil m\'a évité de partager leur déchéance, mais voir les pérégrins se ranger aux côtés de Kael\'Thas…$B$BJe n\'aurai jamais cru mes anciens frères capables d\'un tel acte.$B$BJ\'ai longtemps rêvé de rentrer au pays. Ce n\'est plus possible. Cette forêt est le seul foyer qu\'il me reste.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9490;
UPDATE locales_npc_text SET entry = 9492, Text0_0_loc2 = null, Text0_1_loc2 = 'Lorsque je suis arrivée, j’espérais rencontrer celle à qui la ville doit son nom.$B$B<Ros’eleth regarde autour d’elle.>$B$BLa plupart des jeunes elfes sortent de leur phase « je veux être un pérégrin » lorsqu’ils entrent en apprentissage. En général, c’est pour entrer dans la phase « je veux être un magistère ».$B$BEntre nous, je ne suis jamais sortie de la première phase. Personne ne veut devenir couturière, mais comme disait ma mère, les pérégrins ne vont pas tout nus.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9492;
UPDATE locales_npc_text SET entry = 9493, Text0_0_loc2 = 'Vous avez vu comment Ros’eleth me regarde quand je passe chercher des affaires ? Lorsque les choses seront un peu calmées ici, je lui proposerai de faire une longue promenade dans les bois.$B$BPour une fois, je regrette toutes les tavernes de Hurlevent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9493;
UPDATE locales_npc_text SET entry = 9494, Text0_0_loc2 = '<Kaide chuchote.>$B$BSilence, espèce $gd\'empoté:empotée;, ou vous allez attirer les orcs et l\'Alliance !$B$BC\'est ça, hochez la tête, et lorsque nous aurons fini de parlez, fichez le camp et oubliez-moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9494;
UPDATE locales_npc_text SET entry = 9495, Text0_0_loc2 = 'Nous travaillons peut-être ensemble, mais cela rend la discrétion encore plus impérative. J\'insiste : surveillez vos gestes et vos actions.$B$BJe vous assure que nous sommes loin d\'être seuls ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9495;
UPDATE locales_npc_text SET entry = 9497, Text0_0_loc2 = 'Ouais, qu\'est-ce que vous voulez ? Vous ne voyez pas que je suis occupé ? Le conseil ne me paie pas à l\'heure !', Text0_1_loc2 = 'Ouais, qu\'est-ce que vous voulez ? Vous ne voyez pas que je suis occupée ? Le conseil ne me paie pas à l\'heure !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9497;
UPDATE locales_npc_text SET entry = 9498, Text0_0_loc2 = 'Et je fais quoi à votre avis ? Je répare cet endroit - et sans l\'aide de personne, je tiens à le dire - pour que nous puissions nous y installer.$B$BAvec une base d\'opérations ici en forêt, il sera plus facile de surveiller ce que mijotent Shattrath, le Bastion allérien, les Brise-pierres et les druides cénariens. Notre base d\'Auchindoun n\'est vraiment pas adaptée pour cela.$B$BEh, une minute. Je vous connais ?', Text0_1_loc2 = 'Et je fais quoi à votre avis ? Je répare cet endroit - et sans l\'aide de personne, je tiens à le dire - pour que nous puissions nous y installer.$B$BAvec une base d\'opérations ici en forêt, il sera plus facile de surveiller ce que mijotent Shattrath, le Bastion allérien, les Brise-pierres et les druides cénariens. Notre base d\'Auchindoun n\'est vraiment pas adaptée pour cela.$B$BEh, une minute. Je vous connais ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9498;
UPDATE locales_npc_text SET entry = 9502, Text0_0_loc2 = 'Oui ? Oh zut, maintenant, il faut que je recommence tout !', Text0_1_loc2 = 'Oui ? Oh zut, maintenant, il faut que je recommence tout !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9502;
UPDATE locales_npc_text SET entry = 9503, Text0_0_loc2 = 'Je m’exerce ! Bientôt, je serai assez avancé pour rejoindre les autres invocateurs et prendre mon tour dans le Labyrinthe des ombres à Auchindoun.$B$BJ’espère que j’aurai l’occasion d’invoquer quelque chose de gros ! D\'aussi gros que Marmon, peut-être, mais bien sûr je n\'en perdrais pas le contrôle !', Text0_1_loc2 = 'Je m’exerce ! Bientôt, je serai assez avancée pour rejoindre les autres invocateurs et prendre mon tour dans le Labyrinthe des ombres à Auchindoun.$B$BJ’espère que j’aurai l’occasion d’invoquer quelque chose de gros ! D\'aussi gros que Marmon, peut-être, mais bien sûr je n\'en perdrais pas le contrôle !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9503;
UPDATE locales_npc_text SET entry = 9505, Text0_0_loc2 = 'Qu’est-ce que vous vous imaginez que je peux faire pour vous ? Ne voyez-vous pas que je discute d’affaires importantes avec notre illustre chef ?$B$BParlez, et vite, ou je vous ferais battre, misérable !', Text0_1_loc2 = 'Qu’est-ce que vous vous imaginez que je peux faire pour vous ? Ne voyez-vous pas que je discute d’affaires importantes avec notre illustre chef ?$B$BParlez, et vite, ou je vous ferais battre, misérable !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9505;
UPDATE locales_npc_text SET entry = 9506, Text0_0_loc2 = 'Et bien, j’étais justement en train de faire à notre chef un rapport sur la façon dont tout se déroule conformément à nos plans. Ce n’est plus que l’affaire de quelques semaines avant que cet endroit ne soit remis en état.$B$BIl serait toutefois préférable que le Conseil des Ombres ne peuple le village qu’avec des orcs. Cela nous permettrait d’établir nos revendications ancestrales et de travailler en secret.$B$BAttendez une minute. Je vous connais ? Quel est votre nom ?', Text0_1_loc2 = 'Et bien, j’étais justement en train de faire à notre chef un rapport sur la façon dont tout se déroule conformément à nos plans. Ce n’est plus que l’affaire de quelques semaines avant que cet endroit ne soit remis en état.$B$BIl serait toutefois préférable que le Conseil des Ombres ne peuple le village qu’avec des orcs. Cela nous permettrait d’établir nos revendications ancestrales et de travailler en secret.$B$BAttendez une minute. Je vous connais ? Quel est votre nom ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9506;
