UPDATE locales_npc_text SET entry = 9517, Text0_0_loc2 = 'Je découvrirai ce que mijote ce mystérieux groupe quel qu\'en soit le prix !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9517;
UPDATE locales_npc_text SET entry = 9520, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous devons comprendre ce que ce sinistre équipage manigance à Grangol\'var !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9520;
UPDATE locales_npc_text SET entry = 9521, Text0_0_loc2 = 'Notre devoir par ici, $ggarçon:fillette;, c\'est de nous assurer que personne ne puisse nous surprendre.$B$BÇa implique d\'agir de façon proactive !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9521;
UPDATE locales_npc_text SET entry = 9522, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'ai dû faire une erreur quelque part, sans ça je n\'aurais pas été mutée ici.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9522;
UPDATE locales_npc_text SET entry = 9523, Text0_0_loc2 = 'On vous a dit qu\'ils avaient complètement perdu le contrôle de Marmon dans le Labyrinthe des ombres ?', Text0_1_loc2 = 'On vous a dit qu\'ils avaient complètement perdu le contrôle de Marmon dans le Labyrinthe des ombres ?', Text1_0_loc2 = 'On ne nous paye pas assez pour ça. Mais bon, le village en ruine, c\'est mieux que de devoir crapahuter dans Auchindoun.', Text1_1_loc2 = 'On ne nous paye pas assez pour ça. Mais bon, le village en ruine, c\'est mieux que de devoir crapahuter dans Auchindoun.', Text2_0_loc2 = 'J’ai signé parce qu’ils m’ont dit que ça payerait mes études à l’académie. D’après mon oncle, je pourrais y aller à la prochaine saison.', Text2_1_loc2 = 'J’ai signé parce qu’ils m’ont dit que ça payerait mes études à l’académie. D’après mon oncle, je pourrais y aller à la prochaine saison.', Text3_0_loc2 = 'Oh, salut, c\'est vous la nouvelle recrue ? Ravi de vous rencontrer !$B$BDes nouvelles du Labyrinthe des ombres à Auchindoun ? On a repris le contrôle de Marmon ?', Text3_1_loc2 = 'Oh, salut, c\'est vous la nouvelle recrue ? Ravie de vous rencontrer !$B$BDes nouvelles du Labyrinthe des ombres à Auchindoun ? On a repris le contrôle de Marmon ?', Text4_0_loc2 = 'Si ça vous tente, certains d\'entre nous iront boire un pot à Shattrath un peu plus tard.', Text4_1_loc2 = 'Si ça vous tente, certains d\'entre nous iront boire un pot à Shattrath un peu plus tard.', Text5_0_loc2 = 'Si ça vous dit de faire des parties de balle, nous jouons généralement du lundi au mercredi.$B$BJoignez-vous à nous après cette garde, on se fera quelques lancers.', Text5_1_loc2 = 'Si ça vous dit de faire des parties de balle, nous jouons généralement du lundi au mercredi.$B$BJoignez-vous à nous après cette garde, on se fera quelques lancers.', Text6_0_loc2 = 'J\'espère que notre mission de servir de tête de pont au Conseil des ombres sera couronnée de succès. Je sais que ça peut sembler bizarre, mais le Labyrinthe des ombres me manque un peu.', Text6_1_loc2 = 'J\'espère que notre mission de servir de tête de pont au Conseil des ombres sera couronnée de succès. Je sais que ça peut sembler bizarre, mais le Labyrinthe des ombres me manque un peu.', Text7_0_loc2 = '<Il se penche plus près.>$B$BJuste entre vous et moi, j\'envisage de « rater l\'appel » un de ces soirs. Vous voyez ce que je veux dire ?', Text7_1_loc2 = '<Elle se penche plus près.>$B$BJuste entre vous et moi, j\'envisage de « rater l\'appel » un de ces soirs. Vous voyez ce que je veux dire ?' WHERE entry = 9523;
