UPDATE locales_npc_text SET entry = 9586, Text0_0_loc2 = 'Je suis membre de la Ligue des explorateurs depuis des années, j’ai parcouru tout Azeroth, mais je n’ai jamais rien vu de comparable à Brume-sang.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9586;
