UPDATE locales_npc_text SET entry = 9597, Text0_0_loc2 = 'Moulin-de-Tarren se trouve derrière ces arbres. Les troubles à Fort-de-Durn devraient nous laisser un peu de temps pour trouver Taretha avant que la campagne entière ne soit envahie de soldats humains. Regardons dans cette grange, puis dirigeons-nous vers la ville.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9597;
