UPDATE locales_npc_text SET entry = 9602, Text0_0_loc2 = 'Voulez-vous parler de l\'auberge de Lune-d\'argent, ou du Repos du voyageur ?', Text0_1_loc2 = 'Voulez-vous parler de l\'auberge de Lune-d\'argent, ou du Repos du voyageur ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9602;
UPDATE locales_npc_text SET entry = 9603, Text0_0_loc2 = 'Le Repos du voyageur se trouve entre la promenade des Anciens et le Bazar. Vous pouvez vous y rendre depuis l\'un ou l\'autre de ces lieux.', Text0_1_loc2 = 'Le Repos du voyageur se trouve entre la promenade des Anciens et le Bazar. Vous pouvez vous y rendre depuis l\'un ou l\'autre de ces lieux.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9603;
UPDATE locales_npc_text SET entry = 9610, Text0_0_loc2 = null, Text0_1_loc2 = 'Merci d\'avoir aidé Thrall à s\'échapper, mes amis. Maitenant, j\'espère que vous allez pouvoir m\'aider aussi. Lorsque je me suis réveillée ce matin, j\'étais enfermée dans cette prison magique. Un sorcier bizarre m\'a dit que j\'allais rester là pendant un moment.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9610;
UPDATE locales_npc_text SET entry = 9613, Text0_0_loc2 = null, Text0_1_loc2 = 'Oui, mes amis. Cet homme n\'était pas un sorcier de Dalaran. Il portait des vêtements étrangers et il avait un drôle d\'accent. Il ne faisait que marmonner le temps par ci, le temps par là. Vous allez devoir retrouver ce sorcier et le convaincre de me libérer.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9613;
UPDATE locales_npc_text SET entry = 9614, Text0_0_loc2 = 'Je suis heureux que Taretha soit en vie. Maintenant il faut trouver un moyen de la libérer de ses liens magiques.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9614;
UPDATE locales_npc_text SET entry = 9615, Text0_0_loc2 = null, Text0_1_loc2 = 'En tant que représentante de la Ville basse, je suis là pour faire valoir le fait qu\'Auchindoun doit être purgé du Conseil des ombres qui l\'a envahi. Enfin, il le faut si nous nous voulons garder un espoir pour l\'avenir et empêcher l\'avènement d\'un cataclysme plus dévastateur encore que celui qui a vu la création du désert des Ossements !$B$BNos peuples à tous ont déjà beaucoup trop souffert. Je suis là pour mettre fin à cette souffrance !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9615;
UPDATE locales_npc_text SET entry = 9617, Text0_0_loc2 = 'Prenez un siège, $gmon frère:ma sœur;. Les périls du marécage de Zangar exigent beaucoup des $gvoyageurs:voyageuses;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9617;
UPDATE locales_npc_text SET entry = 9618, Text0_0_loc2 = 'Si vous voulez vous r\'poser ici, y suffit de d\'mander !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9618;
UPDATE locales_npc_text SET entry = 9619, Text0_0_loc2 = 'Merci de m\'avoir aidé à préparer ce baume, $N. Lorsqu\'il aura atténué la douleur, je verrai jusqu\'où je peux aller avec ma mauvaise jambe.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9619;
UPDATE locales_npc_text SET entry = 9620, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue à Garadar, $gétranger:étrangère;. Cherchez-vous un endroit où vous reposer ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9620;
UPDATE locales_npc_text SET entry = 9621, Text0_0_loc2 = 'Ah, un autre chaman à la recherche de l\'illumination, n\'est-ce pas ? Les esprits ne sont pas clairs ? Voulez-vous que je vous aide à distinguer ce qui vous attend ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9621;
UPDATE locales_npc_text SET entry = 9622, Text0_0_loc2 = 'Je suis Skaltesh, guide des chamans qui cherchent ici l\'illumination... mais je peux montrer une chose ou deux même à ceux qui suivent une voie différente. Voulez-vous voir ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9622;
UPDATE locales_npc_text SET entry = 9623, Text0_0_loc2 = 'Buvez cette fiole, et le brouillard se lèvera pendant quelque temps. Allez avec les esprits, mon $gami:amie;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9623;
