UPDATE locales_npc_text SET entry = 9625, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9625;
UPDATE locales_npc_text SET entry = 9626, Text0_0_loc2 = 'Pendant la fête du Voile d’hiver, Grandpère Hiver laisse des jouets pour tout le monde sous l’arbre de Forgefer !', Text0_1_loc2 = 'Pendant la fête du Voile d’hiver, Grandpère Hiver laisse des jouets pour tout le monde sous l’arbre de Forgefer !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9626;
UPDATE locales_npc_text SET entry = 9630, Text0_0_loc2 = 'Ma sœur Silanna refuse d\'admettre que ces bois sont de plus en plus dangereux. Mais qu\'elle le veuille ou non, dès que nous aurons chargé ce chariot, nous partons pour la ville et la sécurité de ses murs.$B$BÀ ce propos, on dirait que nous avons plus de sacs qu\'il ne nous en faut. Vous en auriez peut-être l\'usage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9630;
UPDATE locales_npc_text SET entry = 9631, Text0_0_loc2 = 'Je n\'ai pas trop l\'temps de bavarder, $r. Sauf si vous voulez bien m\'donner un coup de main, bien sûr.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9631;
UPDATE locales_npc_text SET entry = 9632, Text0_0_loc2 = 'Je n’vous remercierai jamais assez pour votre aide dans les mines. Maintenant, il n’y a plus qu’à convaincre les mineurs de s’remettre au travail.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9632;
UPDATE locales_npc_text SET entry = 9634, Text0_0_loc2 = '<Isfar soupire.>$B$BUne poignée de cabanes dans les arbres ne fait pas un vrai foyer, mais c\'était déjà une gigantesque amélioration après les salles des Sethekk.$B$BMême si nous avions tous le confort à Shattrath, je ne peux pas me résigner à rentrer à Skettis sans ma sœur. Elle est toujours retenue dans les salles des Sethekk, captive de notre frère aîné.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9634;
UPDATE locales_npc_text SET entry = 9636, Text0_0_loc2 = 'Voilà notre chance ! Libérez-moi de cette cage !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9636;
UPDATE locales_npc_text SET entry = 9637, Text0_0_loc2 = 'L\'arène des épreuves était autrefois un lieu plein de gloire et d\'honneur. Et puis le cartel de Gentepression est arrivé, avec ses chariots pleins d\'or et ses plumes plus affûtées que n\'importe quelle hache des orcs...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9637;
UPDATE locales_npc_text SET entry = 9638, Text0_0_loc2 = 'Les Sethekk pensent être les véritables et les plus loyaux serviteurs des arakkoa. Ne posez pas de questions à propos du maître, $r, car ses mystères sont réservés au peuple à plumes.$B$BLes Sethekk ont vu dans la destruction d\'Auchindoun le signe de l\'arrivée de notre maître sur cette planète. Ils sont partis pour les ruines en emportant les reliques de notre plus grand héros, Terokk.$B$BIls continuent à chercher le maître dans les ruines du temple, mais j\'en suis venue à voir au travers de leurs mensonges. Je ne suis plus l\'un des leurs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9638;
UPDATE locales_npc_text SET entry = 9639, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9639;
UPDATE locales_npc_text SET entry = 9640, Text0_0_loc2 = 'Le "roi-serre" Ikiss est l\'un des chefs des Sethekk, tout comme mon frère, le tisseur d\'ombre Syth.$B$BCe sont eux qui ont mené l\'exode depuis Skettis vers les ruines d\'Auchindoun. Au début, Ikiss était un chef charismatique, mais il a sombré dans la folie.$B$BMaintenant il proclame ouvertement qu\'il est Terokk réincarné ! Et il pense vraiment que les arakkoa vont se prosterner devant lui, leur chef. Pouvez-vous concevoir pareille arrogance ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9640;
UPDATE locales_npc_text SET entry = 9641, Text0_0_loc2 = 'C\'est le plus grand héros de mémoire d\'arakkoa.$B$BOn raconte que dans l\'antiquité, c\'est lui qui construisit la cité de Skettis de ses propres serres. Il était notre plus grand champion et défenseur, mais il nous a quitté quand nous nous sommes détourné de la vénération de Rukhmar.$B$BNous nous sommes mis à adorer un nouveau maître, qui nous promettait des pouvoirs inégalés et du prestige dans l\'univers.$B$BCertains prétendent que Terokk et Rukhmar sont une seule et même personne, et qu\'il nous a privé de sa présence lorsque nous nous sommes détourné de lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9641;
UPDATE locales_npc_text SET entry = 9642, Text0_0_loc2 = 'Tout en muscle et rien en cervelle, voilà le péon typique ! Très efficaces quand ils sont bien motivés, mais il faut savoir les démarrer au pied, si vous voyez c\'que j\'veux dire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9642;
UPDATE locales_npc_text SET entry = 9643, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous voyez quelque chose qui vous plaît, $C ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9643;
UPDATE locales_npc_text SET entry = 9644, Text0_0_loc2 = 'Si vous avez de l\'argent, j\'ai ce qu\'il vous faut.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9644;
UPDATE locales_npc_text SET entry = 9645, Text0_0_loc2 = null, Text0_1_loc2 = 'Furetez un peu et dites-moi s\'il y a quelque chose qui pourrait vous tenter.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9645;
UPDATE locales_npc_text SET entry = 9646, Text0_0_loc2 = null, Text0_1_loc2 = 'Prenez votre temps, examinez mes armes. Si vous en avez besoin, je répare aussi les armes et les armures.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9646;
UPDATE locales_npc_text SET entry = 9647, Text0_0_loc2 = 'J\'ai juste ce dont vous avez besoin pour lancer vos sorts.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9647;
UPDATE locales_npc_text SET entry = 9648, Text0_0_loc2 = 'Pour faire régner le calme en Nagrand, vous aurez besoin d’une lame aiguisée et d’une armure solide. Voulez-vous que je répare votre équipement avant que vous n’alliez dispenser la justice ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9648;
UPDATE locales_npc_text SET entry = 9649, Text0_0_loc2 = 'Cherchez-vous du poison ou des composants ? Si oui, vous êtes au bon endroit !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9649;
UPDATE locales_npc_text SET entry = 9650, Text0_0_loc2 = 'Je vends du matériel pour les artisans. Des trucs de base, rien de très élaboré.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9650;
UPDATE locales_npc_text SET entry = 9651, Text0_0_loc2 = null, Text0_1_loc2 = 'Avez-vous besoin de mettre votre familier à l\'écurie?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9651;
UPDATE locales_npc_text SET entry = 9652, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous êtes $gperdu:perdue; ? La plupart des marchands de la ville se trouvent près du centre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9652;
UPDATE locales_npc_text SET entry = 9653, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'ai des trucs et des machins, mais surtout des trucs.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9653;
UPDATE locales_npc_text SET entry = 9654, Text0_0_loc2 = 'Ça me rend fou. Je n\'arrive pas à atteindre ces fichus œufs !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9654;
UPDATE locales_npc_text SET entry = 9656, Text0_0_loc2 = null, Text0_1_loc2 = 'Pendant des années, le bastion de l\'Honneur s\'est dressé face aux horreurs de la péninsule des Flammes infernales. Maintenant que la Porte est rouverte et que nous recevons de l\'aide d\'Azeroth, nous allons peut-être sortir de cette impasse.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9656;
UPDATE locales_npc_text SET entry = 9657, Text0_0_loc2 = null, Text0_1_loc2 = 'Il est regrettable que les orcs de la citadelle des Flammes infernales fassent partie de nos nombreux ennemis dans cette péninsule.$B$BD\'un autre côté... quel adversaire pourrait mieux défier et mettre à l\'épreuve la Horde... que des orcs ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9657;
UPDATE locales_npc_text SET entry = 9659, Text0_0_loc2 = 'Ça fait plaisir de vous revoir, $N ! Encore quelques petits réglages et la trajectoire des bonds sera parfaite !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9659;
