UPDATE locales_npc_text SET entry = 9735, Text0_0_loc2 = 'Ho, regardez-moi ça. Comme on est grand et costaud... Tu crois p\'têt que t\'es assez coriace pour aller dans l\'arène de sang et y affronter le champion Cogneguerre ?$B$B<Le combattant d\'arène éclate de rire.>$B$BC\'est peu probable, mais si un jour tu veux tester ta force, l\'arène de sang se trouve loin au nord, contre les montagnes qui séparent Nagrand du marécage de Zangar. C\'est Gurgthock qui dirige le spectacle, dans les vieilles ruines du Crâne ricanant. Il distribue aussi les prix. En parlant de prix, il paraît que ceux de cette saison sont incroyables !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9735;
UPDATE locales_npc_text SET entry = 9738, Text0_0_loc2 = 'L\'interrogateur essaye de découvrir ce que ces elfes de sang font ici en Nagrand. D\'après ce que j\'ai entendu dire, ils en veulent après les résidus de poudre de cristal d\'Oshu\'gun, la montagne de diamant qui se trouve à l\'ouest.$B$BCertainement pour perpétrer un mauvais coup ou préparer une décoction...$B$BSi vous souhaitez aider notre cause et arrêter ces elfes de sang, le conflit est déjà engagé au nord-ouest, à Halaa. Les elfes de sang et les soldats de la Main d\'Argus s\'y battent ! Rejoignez-les et montrez-leur ce que vous valez !', Text0_1_loc2 = 'L\'interrogateur essaye de découvrir ce que ces elfes de sang font ici en Nagrand. D\'après ce que j\'ai entendu dire, ils en veulent après les résidus de poudre de cristal d\'Oshu\'gun, la montagne de diamant qui se trouve à l\'ouest.$B$BCertainement pour perpétrer un mauvais coup ou préparer une décoction...$B$BSi vous souhaitez aider notre cause et arrêter ces elfes de sang, le conflit est déjà engagé au nord-ouest, à Halaa. Les elfes de sang et les soldats de la Main d\'Argus s\'y battent ! Rejoignez-les et montrez-leur ce que vous valez !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9738;
UPDATE locales_npc_text SET entry = 9739, Text0_0_loc2 = 'Les draeneï sont revenus ! Un groupe s\'est jeté sur deux de mes meilleurs chevaucheurs de loups qui patrouillaient le long des routes de l\'ouest. Nous sommes sortis vainqueurs de cet engagement, bien entendu.$B$BJe ne suis pas sûr de savoir ce qui se passe, mais d\'après les rapports de mes éclaireurs, Halaa grouille d\'elfes de sang et de draeneï. J\'ai pas d\'affection particulière pour les elfes de sang, mais si je devais détester une seule chose, ce serait un draeneï. Si vous en voyez à Halaa, ou même certains de leurs alliés, bottez-leur le train pour moi.$B$BHalaa est vers l\'ouest... au cas où vous voudriez y aller tout de suite.', Text0_1_loc2 = 'Les draeneï sont revenus ! Un groupe s\'est jeté sur deux de mes meilleurs chevaucheurs de loups qui patrouillaient le long des routes de l\'ouest. Nous sommes sortis vainqueurs de cet engagement, bien entendu.$B$BJe ne suis pas sûre de savoir ce qui se passe, mais d\'après les rapports de mes éclaireurs, Halaa grouille d\'elfes de sang et de draeneï. J\'ai pas d\'affection particulière pour les elfes de sang, mais si je devais détester une seule chose, ce serait un draeneï. Si vous en voyez à Halaa, ou même certains de leurs alliés, bottez-leur le train pour moi.$B$BHalaa est vers l\'ouest... au cas où vous voudriez y aller tout de suite.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9739;
