UPDATE locales_npc_text SET entry = 9751, Text0_0_loc2 = 'Cet ancien panneau draeneï a été remis en état par les Kurenaï. Désormais, il sert à afficher des annonces et des avis de recherche.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9751;
UPDATE locales_npc_text SET entry = 9752, Text0_0_loc2 = 'Le chasseur des ombres Denjai se sert de ce panneau pour afficher des annonces importantes ainsi que le montant des primes pour les ennemis de Zabra\'jin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9752;
UPDATE locales_npc_text SET entry = 9755, Text0_0_loc2 = 'Aha, vous ne pouvez pas résister, hein ? Vous voulez devenir $gmeilleur:meilleure;, plus $gfort:forte;, plus rapide ? Toutes ces amulettes sont garanties. Efficacité to-ta-le ! Venez acheter l\'un de mes petits miracles ! Même deux fois plus chères, ce serait encore donné !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9755;
UPDATE locales_npc_text SET entry = 9766, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue. Désirez-vous vous reposer, ou peut-être manger un morceau ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9766;
UPDATE locales_npc_text SET entry = 9769, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous devons découvrir ce qui se cache derrière le mystère de ce cristal géant arrivé du firmament sur la crête Tombeciel.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9769;
UPDATE locales_npc_text SET entry = 9770, Text0_0_loc2 = 'Parfois, l\'approche la plus simple d\'un problème est la meilleure.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9770;
UPDATE locales_npc_text SET entry = 9771, Text0_0_loc2 = '« Si vous veniez à être capturés par la Légion, dites : « Xar il romath da tidesbi. » Ils vous tueront sur le champ pour avoir insulté leur dieu, ce qui vous épargnera d\'insupportables tortures, ou pire encore… »', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9771;
UPDATE locales_npc_text SET entry = 9774, Text0_0_loc2 = 'Nous sommes ici pour protéger les intérêts de Gadgetzan à Un’Goro, et cela n’est pas vos affaires.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9774;
UPDATE locales_npc_text SET entry = 9775, Text0_0_loc2 = null, Text0_1_loc2 = 'La péninsule des Flammes infernales est un royaume brisé - impitoyable et cruel envers ses habitants. N’oubliez pas de réparer votre matériel et de faire le plein de provisions avant de vous y aventurer.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9775;
UPDATE locales_npc_text SET entry = 9777, Text0_0_loc2 = 'Parlez à voix basse et gardez les yeux ouverts, $N. Nous sommes cernés par l\'ennemi, ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9777;
UPDATE locales_npc_text SET entry = 9778, Text0_0_loc2 = 'Vous devez placer les bombes incendiaires sur les tonneaux qui se trouvent dans les pavillons d’internement du donjon du Fort-de-Durn. Quand cinq bombes auront été allumées et que la diversion sera en place, trouvez Thrall dans les oubliettes du Fort-de-Durn, et libérez-le. Suivez-le et assurez-vous qu’il ne meure pas !$B$BAirain vous transportera au Fort-de-Durn quand vous serez $gprêt:prête;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9778;
UPDATE locales_npc_text SET entry = 9779, Text0_0_loc2 = 'Je peux vous conduire directement à Fort-de-Durn, si vous voulez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9779;
UPDATE locales_npc_text SET entry = 9780, Text0_0_loc2 = 'Erozion devrait vous remettre un paquet de bombes incendiaires avant que nous décollions vers Fort-de-Durn. Après tout, mieux ne vaut pas perdre de temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9780;
UPDATE locales_npc_text SET entry = 9781, Text0_0_loc2 = 'Horrible... absolument horrible !$B$BNous volions tranquillement depuis la Zone 52 à Raz-de-Néant quand ils nous ont abattus ! Je savais que nous n\'aurions pas dû prendre ce raccourci pour aller dans la forêt de Terokkar !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9781;
UPDATE locales_npc_text SET entry = 9784, Text0_0_loc2 = 'La Halte de l’expédition est le point de contact le plus proche de l’Alliance avec le front de la Légion. Au nord d’ici, il n’y a que des démons, des démons et encore des démons. Ne voyagez pas seul.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9784;
UPDATE locales_npc_text SET entry = 9787, Text0_0_loc2 = 'Les démons illidari me séquestrent ici à cause de ma connaissance des cristaux et reliques draeneï antiques.$B$BIls ont réduit beaucoup des miens en esclavage et nous font rechercher un puissant cristal… qui, selon toute probabilité, ne se trouve même pas ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9787;
UPDATE locales_npc_text SET entry = 9788, Text0_0_loc2 = 'Pour quoi faire ? Si les démons illidari ne me rattrapent pas, alors ce sera les nagas du marécage de Zangar.$B$BSe joindre à Illidan et ses alliés n\'aura en fait apporté que l\'esclavage à la plupart des tribus de Roués. Il n\'y a pas d\'espoir pour mon peuple.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9788;
UPDATE locales_npc_text SET entry = 9789, Text0_0_loc2 = 'Je ne prendrai plus jamais de raccourci, je le jure ! Comment étais-je censé savoir que le ciel au-dessus de la péninsule des Flammes infernales était si dangereux ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9789;
UPDATE locales_npc_text SET entry = 9800, Text0_0_loc2 = '<Une lumière apaisante emplit votre esprit à l\'approche du naaru. De doux tintements musicaux résonnent en vous et, bien qu\'aucun mot ne soit prononcé, un sentiment de sécurité vous est communiqué. Vous êtes $gle bienvenu:la bienvenue; à Shattrath.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9800;
UPDATE locales_npc_text SET entry = 9801, Text0_0_loc2 = 'Ne restez pas là, sauf si vous avez à faire avec la flotte.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9801;
UPDATE locales_npc_text SET entry = 9805, Text0_0_loc2 = 'Une vraie beauté, pas vrai ? Ciel\'ree... le loyal griffon de notre commandant. Kurdran, il tient audience dans le bastion. Et moi j\'m\'occupe de Ciel\'ree quand il est occupé. C\'t\'une magnifique créature, des plus fortes et farouches qui soient. Et elle, elle mourrait pour Kurdran, ouais. J\'ai jamais vu d\'être plus loyal qu\'elle.
C\'t\'un honneur de m\'occuper d\'elle, ouais.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9805;
