UPDATE locales_npc_text SET entry = 9931, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9931;
UPDATE locales_npc_text SET entry = 9932, Text0_0_loc2 = 'Il y a bien des années, mes hommes et moi avons suivi le sorcier Khadgar et le paladin Turalyon jusqu\'à ce monde. Notre seul objectif était d\'annihiler les orcs et leurs infectes lignées. Mais quelque chose a affreusement mal tourné dans les dernières heures de notre guerre.$B$BÀ l\'ultime instant, cette noire terre fut déchirée de part en part, et mes chers compagnons sombrèrent dans le vide.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9932;
UPDATE locales_npc_text SET entry = 9933, Text0_0_loc2 = 'Nous, les Fils de Lothar, sommes depuis restés ici… combattant… mourant… priant pour qu’un jour comme aujourd’hui arrive… Un jour où la Porte s’ouvrirait pour livrer passage à nos frères venus délivrer notre terre natale.$B$BNous sommes comblés, mais le devoir nous appelle. Il nous reste beaucoup à faire avant que nous puissions quitter ce monde et nous reposer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9933;
UPDATE locales_npc_text SET entry = 9934, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9934;
UPDATE locales_npc_text SET entry = 9935, Text0_0_loc2 = 'Un nouveau soldat qui part rejoindre le bastion de l’Honneur… Par la barbe de Kurdran, c’est le genre d’aide dont ils ont bien besoin, là-bas !', Text0_1_loc2 = 'Un nouveau soldat qui part rejoindre le bastion de l’Honneur… Par la barbe de Kurdran, c’est le genre d’aide dont ils ont bien besoin, là-bas !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9935;
UPDATE locales_npc_text SET entry = 9936, Text0_0_loc2 = 'C\'est ma passion, vous savez. Dans la vie ou dans la mort, j\'ai l\'armure dans la peau. Je vous en prie, $gétranger:étrangère;, traitez moi comme si j\'étais encore un draeneï vivant. Laissez-moi éprouver à nouveau ces sensations merveilleuses... Faites-moi l\'honneur de m\'autoriser à réparer votre équipement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9936;
UPDATE locales_npc_text SET entry = 9937, Text0_0_loc2 = 'Le Consortium se vengera des traîtres zaxxis !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9937;
UPDATE locales_npc_text SET entry = 9938, Text0_0_loc2 = 'Ah, un nouveau champion pour notre cause ? Qu\'en dites-vous, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9938;
UPDATE locales_npc_text SET entry = 9939, Text0_0_loc2 = '$N, les Fils de Lothar combattent ici, dans la péninsule des Flammes infernales, depuis deux décennies. Nous sommes harcelés de tous les côtés par les orcs indigènes et les démons infernaux de la Légion ardente et, bien que nos cœurs battent encore d\'une farouche énergie, les années de guerre ont réduit nos effectifs.$B$BVotre arrivée, et l\'afflux d\'une nouvelle génération de héros d\'Azeroth, sont vraiment de bonnes nouvelles !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9939;
UPDATE locales_npc_text SET entry = 9941, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9941;
UPDATE locales_npc_text SET entry = 9942, Text0_0_loc2 = 'Il y a vingt ans environ, l’Alliance a lancé la première expédition vers ce monde, dans l’espoir d’éradiquer les orcs à jamais. Ce fut… un terrible conflit. Bien des âmes courageuses ont péri pour assurer la sécurité d’Azeroth.$B$BMais certaines âmes restent… bloquées dans un combat éternel.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9942;
UPDATE locales_npc_text SET entry = 9943, Text0_0_loc2 = 'Il y a vingt ans environ, l’Alliance a lancé la première expédition vers ce monde, dans l’espoir d’éradiquer les orcs à jamais. Ce fut… un terrible conflit. Bien des âmes courageuses ont péri pour assurer la sécurité d’Azeroth.$B$BMerci, $N, d’avoir aidé ces âmes à trouver la paix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9943;
UPDATE locales_npc_text SET entry = 9944, Text0_0_loc2 = 'Les hommes et femmes du bastion de l\'Honneur ont perdu leur jeunesse et tant vu leur sang versé sur cette monstrueuse terre… et pourtant, leur courage reste au plus haut !$B$BEt maintenant, vous et vos troupes arrivées d\'Azeroth peuvent nous aider à inverser le cours de la bataille. Vous nous donnez l\'espoir de revoir un jour nos terres natales.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9944;
UPDATE locales_npc_text SET entry = 9947, Text0_0_loc2 = 'En tant que représentant du Consortium, je prête toujours une grande attention aux ramifications que mes interactions peuvent avoir sur nos contrats et nos affaires.$B$BIl est malheureux que les elfes de sang ne traitent pas leurs partenaires avec le même respect.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9947;
UPDATE locales_npc_text SET entry = 9948, Text0_0_loc2 = 'Mon petit frère que vous voyez là est en train de gravir les échelons du Consortium à grande vitesse. Bien sûr, il est bien trop modeste pour l\'admettre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9948;
UPDATE locales_npc_text SET entry = 9949, Text0_0_loc2 = 'Quand il dit « petit frère », il oublie de mentionner que nous sommes jumeaux. Il n\'a que quelques minutes de plus que moi, en fait.$B$BNe vous laissez pas avoir par ces histoires de « gravir les échelons ». Je n\'aurai jamais son envergure en tant que marchand.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9949;
UPDATE locales_npc_text SET entry = 9951, Text0_0_loc2 = 'Salut à vous, $r. Ce que je fais ici est une histoire pour un autre temps. Désolé, c’était une mauvaise plaisanterie...$B$BMon frère et moi sommes bloqués ici, entre notre époque et le présent. Nous n’osons pas partir, de peur de provoquer un paradoxe temporel. Nous attendons patiemment que les dragons nous replacent dans notre ligne temporelle normale.$B$BCela fait combien de temps ? Je ne suis plus très sûr. Demandez à mon frère.$B$BVous voulez du fromage ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9951;
UPDATE locales_npc_text SET entry = 9953, Text0_0_loc2 = 'Non, vous n’avez pas d’hallucinations auditives. J’ai réellement la voix d’une jeune femme orque. Mais vous savez, $gmon petit gars:ma petite dame;, les voyages dans le temps font subir des choses étranges à votre corps.$B$BAu fait, cela fait 250 ans.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9953;
UPDATE locales_npc_text SET entry = 9954, Text0_0_loc2 = 'Ouais, je suis coincé ici depuis aussi longtemps que je m’en souvienne.$B$BDans mon ancienne vie, j’étais armurier. Je peux peut-être vous réparer quelque chose ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9954;
UPDATE locales_npc_text SET entry = 9955, Text0_0_loc2 = 'Enfin, quelqu\'un est descendu pour me parler ! Vous voulez entendre quelques-uns de mes poèmes ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9955;
UPDATE locales_npc_text SET entry = 9956, Text0_0_loc2 = 'Sur la route j\'erre ! Un œil sur mes pieds ! Et les feuilles tombées, qui jonchent la terre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9956;
UPDATE locales_npc_text SET entry = 9958, Text0_0_loc2 = 'Cette plate-forme de téléportation éthérienne est couverte de boutons et manettes aux couleurs vives, mais semble être désaffectée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9958;
UPDATE locales_npc_text SET entry = 9959, Text0_0_loc2 = 'Tout est à vendre, mon $gami:amie; ! Grand ou petit, tout ce dont vous avez besoin, Hazzin peut vous le fournir.$B$BSi vous accordez une quelconque valeur à votre vie, ne vous approchez pas de la manaforge Ultris sans vous être $gréapprovisionné: réapprovisionnée;. Hazzin est votre dernière chance de le faire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9959;
UPDATE locales_npc_text SET entry = 9960, Text0_0_loc2 = 'Je suis parfaitement confiant : nous récupérerons ce que le prince-nexus Haramad désire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9960;
UPDATE locales_npc_text SET entry = 9962, Text0_0_loc2 = 'Les Gourmandises Fumebois ont ouvert une boutique à Orgrimmar et Forgefer. Je me demande ce qu\'ils auront imaginé cette année.', Text0_1_loc2 = 'Les Gourmandises Fumebois ont ouvert une boutique à Orgrimmar et Forgefer. Je me demande ce qu\'ils auront imaginé cette année.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9962;
UPDATE locales_npc_text SET entry = 9964, Text0_0_loc2 = 'Les druides célèbrent la Fête lunaire à Reflet-de-Lune. Si c\'est trop loin pour s\'y rendre à pied, ils y transportent les gens depuis les grandes villes.', Text0_1_loc2 = 'Les druides célèbrent la Fête lunaire à Reflet-de-Lune. Si c\'est trop loin pour s\'y rendre à pied, ils y transportent les gens depuis les grandes villes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9964;
UPDATE locales_npc_text SET entry = 9968, Text0_0_loc2 = null, Text0_1_loc2 = 'Je n\'ai jamais rien vu de semblable à ces dragons du néant. Ils semblent avoir hérité du tempérament de leur géniteur, mais sont devenus quelque chose de complètement différent.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9968;
UPDATE locales_npc_text SET entry = 9969, Text0_0_loc2 = 'Le sol de cet endroit est encore chaud du sang fraîchement versé. Ah, j’aurais donné un œil pour pouvoir être là quand les démons ont forcé le portail et affronté les héros d’Azeroth ! Quelle bataille !$B$BMais maintenant, des combats encore plus féroces font rage de l’autre côté !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9969;
UPDATE locales_npc_text SET entry = 9970, Text0_0_loc2 = 'Si vous avez fait tout le chemin jusqu’ici, ce doit être pour oser vous aventurer près de la manaforge Ara.$B$BVous voulez vendre ou acheter quelque chose avant d\'y aller ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9970;
UPDATE locales_npc_text SET entry = 9971, Text0_0_loc2 = 'Il n\'y a que trois sortes de gens en ce monde : les concurrents, les clients et les employés. Les concurrents doivent être écrasés impitoyablement, les clients gâtés, et les employés strictement encadrés.$B$BLes prestataires indépendants sont un mythe. Ils veulent être payés mieux qu\'un employé mais traités comme des clients. Il ne faut jamais faire confiance à quelqu\'un qui se désigne sous ce titre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9971;
UPDATE locales_npc_text SET entry = 9977, Text0_0_loc2 = 'Prenez garde, $gmortel:mortelle;, car au-delà de cette salle résident des dangers inconnus dans votre monde. Avancez prudemment…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9977;
UPDATE locales_npc_text SET entry = 9978, Text0_0_loc2 = 'Il est bon de vous revoir, $N. Voulez-vous que je vous conduise jusqu’au repaire du maître ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9978;
UPDATE locales_npc_text SET entry = 9979, Text0_0_loc2 = 'Le bassin d’Arathi a été envahi par les Profanateurs. Voulez-vous nous aider, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9979;
UPDATE locales_npc_text SET entry = 9980, Text0_0_loc2 = 'Il est bon de voir un ami de l’Aldor dans ces contrées reculées. Je crains ne plus pouvoir supporter encore longtemps les bavardages insolents de ces elfes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9980;
UPDATE locales_npc_text SET entry = 9981, Text0_0_loc2 = null, Text0_1_loc2 = 'Allez, $N, au bassin d’Arathi ! Tuez, TUEZ, comme vous n’avez encore jamais tué !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9981;
UPDATE locales_npc_text SET entry = 9982, Text0_0_loc2 = 'L\'entrée du goulet des Chanteguerres se trouve devant vous. Irez-vous y combattre cette misérable Alliance ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9982;
UPDATE locales_npc_text SET entry = 9983, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis heureuse de vous rencontrer, $c. En ce moment, la Horde est en train d’attaquer le goulet des Chanteguerres. Voulez-vous vous battre là-bas aux côtés de l’Alliance ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9983;
UPDATE locales_npc_text SET entry = 9984, Text0_0_loc2 = 'Mais qu’est-ce qu’vous faites ici à fainéanter ? Si vous alliez dans la vallée d’Alterac, au lieu de bavarder ? C’est là-bas qu’on a besoin de vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9984;
UPDATE locales_npc_text SET entry = 9985, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9985;
UPDATE locales_npc_text SET entry = 9986, Text0_0_loc2 = 'La forêt chante les germes du changement. Nos nouveaux amis, les draeneï, sont ici, et nous allons les aider à construire leur foyer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9986;
UPDATE locales_npc_text SET entry = 9988, Text0_0_loc2 = 'Qu\'est-ce que vous voulez ? Je ne suis pas du genre à bavarder.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9988;
UPDATE locales_npc_text SET entry = 9989, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis très occupée à négocier un pot-de-vin, en ce moment. Pourriez-vous revenir plus tard ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9989;
UPDATE locales_npc_text SET entry = 9990, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9990;
UPDATE locales_npc_text SET entry = 9991, Text0_0_loc2 = 'Un nouveau soldat qui part rejoindre le bastion de l’Honneur… Par la barbe de Kurdran, c’est le genre d’aide dont ils ont bien besoin, là-bas !', Text0_1_loc2 = 'Un nouveau soldat qui part rejoindre le bastion de l’Honneur… Par la barbe de Kurdran, c’est le genre d’aide dont ils ont bien besoin, là-bas !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9991;
UPDATE locales_npc_text SET entry = 9993, Text0_0_loc2 = 'Bienvenue à la Foudreflèche, $c.$B$BJe vous en prie, ne prenez pas ombrage de mon apparition par images interposées. J\'ai beaucoup d\'ennemis qui voudraient ma mort, il ne s\'agit que d\'une mesure de précaution.$B$BJe peux vous assurer que vous êtes parfaitement en sécurité ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9993;
UPDATE locales_npc_text SET entry = 9994, Text0_0_loc2 = 'Souhaitez-vous acheter quelque chose ? Ou vendre quelque chose ?$B$BNous sommes là pour ça, $gmon ami:mon amie;.$B$BS\'il vous plaît, qu\'est-ce qui vous ferait plaisir ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9994;
UPDATE locales_npc_text SET entry = 9995, Text0_0_loc2 = 'Vous êtes récemment $garrivé:arrivée; d’Azeroth, hein ? Vous feriez mieux de vous endurcir, $c… Il y a des périls sur cette terre qui ne ressemblent à rien de ce que la Horde a affronté jusqu’à maintenant. Je suis Nazgrel, le bras droit de notre puissant chef de guerre, Thrall. Il m’a honoré en me confiant la tâche de protéger cette courageuse expédition. Et par tous les esprits, c’est ce que je vais faire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9995;
UPDATE locales_npc_text SET entry = 9996, Text0_0_loc2 = 'Throm\'ka, $gmon frère:ma sœur; ! Bienvenue en Outreterre. La Horde a besoin de toutes les forces qu’elle peut rassembler sur ce monde brisé. Je vous promets que si vous êtes $gvenu:venue; chercher des batailles glorieuses, vous pourrez vous en donner à cœur joie ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9996;
UPDATE locales_npc_text SET entry = 9997, Text0_0_loc2 = 'Dépêchez-vous, $c. On a une guerre sur le feu, ici, au cas où vous ne l’auriez pas remarqué.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 9997;
UPDATE locales_npc_text SET entry = 10000, Text0_0_loc2 = 'Salutations, $gvoyageur:voyageuse;. Vous devriez écouter mes avertissements avant de vous aventurer dans le Noir Marécage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10000;
UPDATE locales_npc_text SET entry = 10001, Text0_0_loc2 = 'Ah, bon retour à vous, $ghéros:héroïne;. Revenez-vous pour protéger le Gardien une nouvelle fois ? N\'oubliez pas d\'emporter une chrono-balise !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10001;
UPDATE locales_npc_text SET entry = 10002, Text0_0_loc2 = 'Salutations, $N. Faites bien attention…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10002;
UPDATE locales_npc_text SET entry = 10003, Text0_0_loc2 = 'Quand notre chef de guerre a appris que la Porte des ténèbres avait été rouverte, il a immédiatement rassemblé ses conseillers pour préparer cette audacieuse expédition. Même si certains d’entre eux ont encore des doutes, Thrall espère que nous pourrons découvrir de nouvelles informations sur l’histoire de notre peuple. Il pense que la véritable histoire du peuple orc est cachée quelque part dans les ruines de ce monde.$B$BJe dois avouer que, dans mes heures les plus sombres... je crains que ce récit ne doive rester secret.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10003;
UPDATE locales_npc_text SET entry = 10004, Text0_0_loc2 = 'La Horde a pris de grands risques en s’aventurant en Outreterre, consacrant à ce projet des ressources, de la main-d’œuvre... Même la défense tactique de nos terres est en jeu. En dépit de tout cela, Thrall croit en la promesse de ce pays dévasté. Une étrange lueur brille dans ses yeux, ces derniers temps… comme s’il pressentait une révélation exaltante qu’il est le seul à voir.$B$BMais il reste mon chef de guerre et mon frère de sang. Je le suivrai jusqu’à travers les portes de l’abysse.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10004;
UPDATE locales_npc_text SET entry = 10005, Text0_0_loc2 = 'Throm’ka, $c. Vous devez être $gun nouveau:une nouvelle;.Vous semblez $gfrais:fraîche; et pas encore $gmarqué:marquée; par les horreurs de cette terre.$B$B<Le sergent Fracasse-crâne crache dans la poussière.>$B$BÇa ne va pas durer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10005;
UPDATE locales_npc_text SET entry = 10006, Text0_0_loc2 = 'Notre expédition a réussi à traverser la Porte et à implanter une nouvelle base appelée Thrallmar. Mais comme vous pouvez le voir, la Légion ardente s\'est avancée et nous a séparés de nos frères. Il est clair que les démons espèrent reprendre la Porte des ténèbres pour nous empêcher de faire venir des renforts d\'Azeroth.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10006;
UPDATE locales_npc_text SET entry = 10008, Text0_0_loc2 = 'Si on l\'aide un peu, je pense que Taerix est sur le point de faire un grand pas en avant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10008;
UPDATE locales_npc_text SET entry = 10009, Text0_0_loc2 = 'Mes frères à Hurlevent me disaient de ne pas m\'engager dans l\'armée... Je suis bien content de ne pas les avoir écoutés !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10009;
UPDATE locales_npc_text SET entry = 10010, Text0_0_loc2 = 'La halte du Fracas est un bel endroit pour un Marteau-hardi. Je n\'avais jamais passé autant de temps dans les airs !', Text0_1_loc2 = 'La halte du Fracas est un bel endroit pour un Marteau-hardi. Je n\'avais jamais passé autant de temps dans les airs !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10010;
UPDATE locales_npc_text SET entry = 10011, Text0_0_loc2 = null, Text0_1_loc2 = 'Les faucons-pérégrins sont de nobles créatures. Assurez-vous de toujours bien traiter le vôtre. Rien n’est plus dangereux qu’un faucon-pérégrin en colère.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10011;
UPDATE locales_npc_text SET entry = 10012, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis sûre qu\'avec notre aide, Taerix la botaniste réussira à trouver une solution à nos problèmes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10012;
UPDATE locales_npc_text SET entry = 10018, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10018;
UPDATE locales_npc_text SET entry = 10019, Text0_0_loc2 = 'Vous voulez m\'aider à détruire le portail dimensionnel dans ce camp de la Légion ardente au sud ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10019;
UPDATE locales_npc_text SET entry = 10020, Text0_0_loc2 = 'On fait du bon boulot ensemble, $N. Je n\'aurais jamais réussi à détruire ce portail dimensionnel sans vous !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10020;
UPDATE locales_npc_text SET entry = 10022, Text0_0_loc2 = null, Text0_1_loc2 = 'Ces gens sont arrivés de Shattrath il y a quelques nuits et n\'ont pas arrêté de se disputer depuis.$B$BJe suis contente de voir que ça ne vous a pas $gdétourné:détournée; d\'ici, je commençais à penser qu\'ils faisaient fuir les clients potentiels.', Text1_0_loc2 = null, Text1_1_loc2 = 'J\'espère que les arrivés de Shattrath vont arrêter de se disputer. Ils commencent à me taper sur les nerfs.$B$BQue puis-je faire pour vous ?', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10022;
UPDATE locales_npc_text SET entry = 10024, Text0_0_loc2 = 'Vous n\'avez pas encore assez de force pour combattre dans l\'Œil du cyclone. Revenez quand vous aurez acquis plus d\'expérience.', Text0_1_loc2 = 'Vous n\'avez pas encore assez de force pour combattre dans l\'Œil du cyclone. Revenez quand vous aurez acquis plus d\'expérience.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10024;
UPDATE locales_npc_text SET entry = 10025, Text0_0_loc2 = 'Les forces draeneï et leurs compagnons de l\'Alliance affrontent la Horde dans l\'Œil du cyclone en Outreterre. Voulez-vous les rejoindre pour tenter de faire basculer la bataille en notre faveur ?', Text0_1_loc2 = 'Les forces draeneï et leurs compagnons de l\'Alliance affrontent la Horde dans l\'Œil du cyclone en Outreterre. Voulez-vous les rejoindre pour tenter de faire basculer la bataille en notre faveur ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10025;
UPDATE locales_npc_text SET entry = 10026, Text0_0_loc2 = 'La Horde ne peut pas laisser le contrôle des cellules de mana de l\'Outreterre aux draeneï et à leur envahissants Alliés. Voulez-vous vous joindre à l\'assaut des elfes de sang sur l\'Œil du cyclone ?', Text0_1_loc2 = 'La Horde ne peut pas laisser le contrôle des cellules de mana de l\'Outreterre aux draeneï et à leur envahissants Alliés. Voulez-vous vous joindre à l\'assaut des elfes de sang sur l\'Œil du cyclone ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10026;
UPDATE locales_npc_text SET entry = 10027, Text0_0_loc2 = 'Bonjour, $c ! Vous venez pour une mission aérienne ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10027;
UPDATE locales_npc_text SET entry = 10031, Text0_0_loc2 = '« Si vous avez déjà visité le marécage de Zangar, vous y avez peut-être croisé un gars appelé Daniels. Vous savez, un type qui porte un casque bizarre et qui se fait appeler « le Spécialiste ».$B$BÉvidemment, c\'est moi qui lui ai tout appris.$B$BNe vous laissez pas abuser par mes beaux vêtements. Je reste un voleur, au fond de moi. Daniels et moi avons passé beaucoup de temps dans les tranchées ensemble, mais c\'était avant que je prenne ma retraite. Autant que je sache, il est toujours là-bas, pour leur en faire baver. »', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10031;
UPDATE locales_npc_text SET entry = 10032, Text0_0_loc2 = 'Jour après jour, tout ce que nous faisons, c\'est trimer dans cette mine. Un elfe de sang n\'est pas fait pour ce genre de travail manuel.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10032;
UPDATE locales_npc_text SET entry = 10039, Text0_0_loc2 = '$N, je vous en prie, veillez à annoncer notre succès à Gahruj.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10039;
UPDATE locales_npc_text SET entry = 10040, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'émissaire Taluun m\'a emmenée avec lui dans l\'espoir que je puisse expliquer les éléments de ce monde à nos nouveaux alliés… mais personne ne m\'écoute.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10040;
UPDATE locales_npc_text SET entry = 10041, Text0_0_loc2 = null, Text0_1_loc2 = 'Laissez-moi, $c. Je dois remplir mes obligations auprès de l\'ambassadeur Tristesoleil, aussi ennuyeuses soient-elles.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10041;
UPDATE locales_npc_text SET entry = 10042, Text0_0_loc2 = null, Text0_1_loc2 = 'Même si les Sha\'tar portent encore toute leur attention sur la vallée d\'Ombrelune et Illidan, nous ne pouvons ignorer la menace que représente Kael\'thas Haut-soleil.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10042;
UPDATE locales_npc_text SET entry = 10043, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'émissaire Taluun m\'a emmenée avec lui dans l\'espoir que je puisse expliquer les éléments de ce monde à nos nouveaux alliés. Pour l\'instant ils refusent de m\'écouter, mais peut-être que vous le voudrez ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10043;
UPDATE locales_npc_text SET entry = 10044, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10044;
UPDATE locales_npc_text SET entry = 10045, Text0_0_loc2 = '<À l\'approche de la console de contrôle, le cristal d\'accès que vous avez en votre possession commence à bourdonner et luire doucement.>$B$B<Un flot d\'informations déferle dans votre esprit et, soudainement, vous connaissez les niveaux de production d\'énergie de la manaforge, son niveau de stabilité et la distribution vers les différentes canalisations. La console attend vos ordres.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10045;
UPDATE locales_npc_text SET entry = 10047, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10047;
UPDATE locales_npc_text SET entry = 10051, Text0_0_loc2 = 'Le Gardelumière a deux rôles. Tout d\'abord, protéger les prêtres à tout prix et avant tout. Ensuite, détruire les ennemis de la Lumière où qu\'ils se cachent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10051;
UPDATE locales_npc_text SET entry = 10052, Text0_0_loc2 = 'Par la barbe de Muradin, $c. Cette terre est au bord de la destruction. Il est inimaginable que quelqu\'un ait pu survivre ici depuis la fin de la seconde guerre.', Text0_1_loc2 = 'Par la barbe de Muradin, $c. Cette terre est au bord de la destruction. Il est inimaginable que quelqu\'un ait pu survivre ici depuis la fin de la seconde guerre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10052;
UPDATE locales_npc_text SET entry = 10053, Text0_0_loc2 = 'Throm’ka, $c. C’est cette terre brisée que vous vous attendiez à voir ? Cela ne ressemble pas du tout aux histoires qu’on m’a racontées. C’est assez difficile de croire que mon peuple a pu prospérer dans cet endroit désolé.', Text0_1_loc2 = 'Throm’ka, $c. C’est cette terre brisée que vous vous attendiez à voir ? Cela ne ressemble pas du tout aux histoires qu’on m’a racontées. C’est assez difficile de croire que mon peuple a pu prospérer dans cet endroit désolé.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10053;
UPDATE locales_npc_text SET entry = 10054, Text0_0_loc2 = 'Je cherche quelqu\'un pour m\'aider avec un problème qui aurait dû être résolu depuis longtemps.$B$BVous semblez fiable…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10054;
UPDATE locales_npc_text SET entry = 10055, Text0_0_loc2 = 'Le travail que vous avez fait pour moi aux ruines de Farahlon m\'a permis de rattraper mon retard. Je rassemble mon équipe pour y retourner, maintenant.$B$BMerci, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10055;
UPDATE locales_npc_text SET entry = 10056, Text0_0_loc2 = 'La Légion, partout… Il faut s\'en débarrasser !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10056;
UPDATE locales_npc_text SET entry = 10057, Text0_0_loc2 = 'Grâce à vous, nous allons pouvoir poursuivre nos projets pour les ruines de Farahlon.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10057;
UPDATE locales_npc_text SET entry = 10058, Text0_0_loc2 = 'Que savez-vous des éthériens, créature de chair ?$B$BVous devez sans aucun doute nous voir tous sous le même jour et tout ignorer des luttes et conflits entre les diverses factions.$B$BPeut-être est-il temps que je me présente. Je suis le commandant Ameer, du Protectorat, et je suis également votre seul espoir de remédier à l\'invasion des créatures du Vide et de l\'Ethereum dans cette région.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10058;
UPDATE locales_npc_text SET entry = 10059, Text0_0_loc2 = 'L\'Ethereum constituait la classe dirigeante de mon monde natal, K\'aresh. Mon peuple fut malheureusement obligé d\'évacuer K\'aresh peu avant que le seigneur du Vide, Dimensius, ne lance ses armées contre notre terre et ne détruise tout ce qui se dressait sur son passage. Pour cela, l\'Ethereum jura d\'exercer sa vengeance. Depuis, ses membres sont sur la piste de Dimensius...$B$BLe Protectorat, quant à lui, ne fut pas créé en réaction à Dimensius... mais à l\'Ethereum.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10059;
UPDATE locales_npc_text SET entry = 10060, Text0_0_loc2 = 'L\'Ethereum s\'est assombri… perverti. La vengeance est devenu leur seul refuge, et quiconque se dresse sur leur chemin ou ne rejoint pas leur cause est considéré comme un ennemi. Beaucoup de ses membres ont bien vu que l\'Ethereum sombrait dans la folie et l\'ont quitté. Plusieurs princes-nexus du conseil de l\'Ethereum ont abandonné le groupe pour former d\'autres ordres, d\'autres factions aux buts différents. Le Consortium est un exemple d\'un de ces groupes dissidents. Le Protectorat en est un autre...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10060;
UPDATE locales_npc_text SET entry = 10061, Text0_0_loc2 = 'Pour nous, l\'Ethereum constitue autant que Dimensius une menace contre notre existence. Il faut les arrêter tous les deux…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10061;
UPDATE locales_npc_text SET entry = 10062, Text0_0_loc2 = 'Yo. Je suis Vixton Sifflepince, le vendeur officiel de la Fédération des Rencontres d\'Arène de Gentepression, Si votre réputation est à la hauteur, j’ai des marchandises qui vont vous faire baver d’envie…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10062;
UPDATE locales_npc_text SET entry = 10063, Text0_0_loc2 = 'Si vous ne vous sentez pas bien, passez voir Pilon. Il aura peut-être quelque chose pour le mal de l\'air.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10063;
UPDATE locales_npc_text SET entry = 10064, Text0_0_loc2 = '<L\'apprenti regarde de temps en temps par-dessus son épaule, en réaction à des sons que vous n\'entendez pas semble-t-il.>$B$BBonjour, $N. Désolé si j\'ai l\'air distrait, mais je suis en plein travail pour le fermier Natin. Je veux avoir fini de ferrer tous ses chevaux quand il rentrera de l\'hôtel de ville. Que puis-je faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10064;
UPDATE locales_npc_text SET entry = 10065, Text0_0_loc2 = 'La direction du Protectorat nous avait donné, à l\'agent Ya-six et à moi, une mission de haute importance ayant pour but d\'éradiquer tout signe de l\'invasion de créatures du Vide à Ultris. Il s\'agissait de frappes tactiques. Notre dernier coup avant de rentrer au poste de garde du Protectorat devait viser un seigneur du Vide appelé Arconus qui se gorgeait des créatures de chair de la région.$B$BMalheureusement, nous sommes arrivés un peu trop tard. Ya-six et moi avons été séparés lors de la fusillade avec les bêtes de chair. Maintenant, il se retrouve pris au piège, bloqué derrière les lignes ennemies.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10065;
UPDATE locales_npc_text SET entry = 10066, Text0_0_loc2 = 'Si la Lumière ne suffit pas à vous guider, $gmon ami:mon amie;, alors peut-être pourrai-je vous aider. Que cherchez-vous ?', Text0_1_loc2 = 'Si la Lumière ne suffit pas à vous guider, $gmon ami:mon amie;, alors peut-être pourrai-je vous aider. Que cherchez-vous ?', Text1_0_loc2 = 'Cette île est parfois étrange, n\'est-ce pas ? Puis-je faire quelque chose pour vous aider ?', Text1_1_loc2 = 'Cette île est parfois étrange, n\'est-ce pas ? Puis-je faire quelque chose pour vous aider ?', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10066;
