UPDATE locales_npc_text SET entry = 10071, Text0_0_loc2 = 'Les hippogriffes sont bien soignés près de l\'Exodar. Vous devriez vous y rendre si vous souhaitez vous envoler pour le Guet du sang.', Text0_1_loc2 = 'Les hippogriffes sont bien soignés près de l\'Exodar. Vous devriez vous y rendre si vous souhaitez vous envoler pour le Guet du sang.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10071;
UPDATE locales_npc_text SET entry = 10072, Text0_0_loc2 = 'La réserve de textiles de l\'Exodar, qui était jadis énorme, a dangereusement baissé. Votre réputation auprès des draeneï augmenterait si vous nous aidiez avec un don de tissu.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10072;
UPDATE locales_npc_text SET entry = 10073, Text0_0_loc2 = 'Funaam est notre officier du registre des guildes. Son bureau est installé dans l\'Exodar. Vous n\'avez qu\'à suivre la route, vous le trouverez en un rien de temps.', Text0_1_loc2 = 'Funaam est notre officier du registre des guildes. Son bureau est installé dans l\'Exodar. Vous n\'avez qu\'à suivre la route, vous le trouverez en un rien de temps.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10073;
UPDATE locales_npc_text SET entry = 10074, Text0_0_loc2 = 'Notre humble auberge est située au nord de Guet d\'azur ; Chellan y prendra bien soin de vous.', Text0_1_loc2 = 'Notre humble auberge est située au nord de Guet d\'azur ; Chellan y prendra bien soin de vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10074;
UPDATE locales_npc_text SET entry = 10075, Text0_0_loc2 = 'Esbina sera heureuse de prendre soin de votre compagnon animal, mon $gami:amie;. Elle est postée devant l\'auberge, à l\'intérieur de Guet d\'azur.', Text0_1_loc2 = 'Esbina sera heureuse de prendre soin de votre compagnon animal, mon $gami:amie;. Elle est postée devant l\'auberge, à l\'intérieur de Guet d\'azur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10075;
UPDATE locales_npc_text SET entry = 10076, Text0_0_loc2 = 'Ah, vous désirez vous entraîner ? Plusieurs instructeurs ont dressé leurs camps à Guet d\'azur... Que cherchez-vous exactement ?', Text0_1_loc2 = 'Ah, vous désirez vous entraîner ? Plusieurs instructeurs ont dressé leurs camps à Guet d\'azur... Que cherchez-vous exactement ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10076;
UPDATE locales_npc_text SET entry = 10077, Text0_0_loc2 = 'Le druidisme ? Personne par ici ne suit cette voie... Mais je crois avoir entendu parler de quelqu\'un qui pourrait correspondre près de l\'Exodar. Shalannius, il me semble.', Text0_1_loc2 = 'Le druidisme ? Personne par ici ne suit cette voie... Mais je crois avoir entendu parler de quelqu\'un qui pourrait correspondre près de l\'Exodar. Shalannius, il me semble.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10077;
UPDATE locales_npc_text SET entry = 10078, Text0_0_loc2 = 'Actéon est notre pisteur attitré, et il n\'est pas mauvais du tout. Les peaux qu\'il porte attestent de ses compétences. Son camp est au sud-est du Guet d\'azur.', Text0_1_loc2 = 'Actéon est notre pisteur attitré, et il n\'est pas mauvais du tout. Les peaux qu\'il porte attestent de ses compétences. Son camp est au sud-est du Guet d\'azur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10078;
UPDATE locales_npc_text SET entry = 10079, Text0_0_loc2 = 'Hein ? Vous voulez faire don d\'étoffe à Lune-d\'argent ?$B$BTout ce qui vous intéresse, c\'est avoir un faucon-pérégrin, n\'est-ce pas ? Très bien, très bien… Rendez ce service à la ville, et je m\'assurerai que votre réputation ici s\'améliore.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10079;
UPDATE locales_npc_text SET entry = 10081, Text0_0_loc2 = 'Si c\'est un maîtres des arcanes que vous voulez, ne cherchez pas plus loin que Semid. Il dispense ses enseignements aux côtés de Ruada, sur le côté est du Guet d\'azur.', Text0_1_loc2 = 'Si c\'est un maîtres des arcanes que vous voulez, ne cherchez pas plus loin que Semid. Il dispense ses enseignements aux côtés de Ruada, sur le côté est du Guet d\'azur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10081;
UPDATE locales_npc_text SET entry = 10083, Text0_0_loc2 = 'Tullas est une noble femme, et une loyale combattante de la Lumière. Elle dispense ses leçons dans l\'auberge.', Text0_1_loc2 = 'Tullas est une noble femme, et une loyale combattante de la Lumière. Elle dispense ses leçons dans l\'auberge.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10083;
UPDATE locales_npc_text SET entry = 10084, Text0_0_loc2 = 'Dans l\'auberge de Chellan, qui est petite mais confortable, vous trouverez le prêtre Guvan.', Text0_1_loc2 = 'Dans l\'auberge de Chellan, qui est petite mais confortable, vous trouverez le prêtre Guvan.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10084;
UPDATE locales_npc_text SET entry = 10085, Text0_0_loc2 = 'Vous êtes l\'$gun:une; de ces chamans ? Mmm. Je suppose que vous êtres libre de choisir votre voie… quelle qu\'elle soit. Tuluun tient un petit camp isolé au nord-ouest du Guet d\'azur.', Text0_1_loc2 = 'Vous êtes l\'$gun:une; de ces chamans ? Mmm. Je suppose que vous êtres libre de choisir votre voie… quelle qu\'elle soit. Tuluun tient un petit camp isolé au nord-ouest du Guet-d\'azur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10085;
UPDATE locales_npc_text SET entry = 10086, Text0_0_loc2 = 'Ruada forme les jeunes guerriers dans la partie est du Guet d\'azur. Si vous cherchez à vous entraîner à l\'art du combat, elle pourra vous aider.', Text0_1_loc2 = 'Ruada forme les jeunes guerriers dans la partie est du Guet d\'azur. Si vous cherchez à vous entraîner à l\'art du combat, elle pourra vous aider.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10086;
UPDATE locales_npc_text SET entry = 10087, Text0_0_loc2 = 'On trouve pas mal d\'artisans talentueux à Brume-azur. Que cherchez-vous ?', Text0_1_loc2 = 'On trouve pas mal d\'artisans talentueux à Brume-azur. Que cherchez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10087;
UPDATE locales_npc_text SET entry = 10088, Text0_0_loc2 = 'Daedal connaît bien l\'art de concocter des potions. Il travaille auprès de Fateema dans la partie ouest du Guet d\'azur.', Text0_1_loc2 = 'Daedal connaît bien l\'art de concocter des potions. Il travaille auprès de Fateema dans la partie ouest du Guet-d\'azur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10088;
UPDATE locales_npc_text SET entry = 10089, Text0_0_loc2 = 'Il n\'y a pas de forge au Guet d\'azur. Cependant, j\'ai vu quelques apprentis forgerons arriver de cet étrange campement situé sur la côte au sud. Vous devriez peut-être y jeter un œil.', Text0_1_loc2 = 'Il n\'y a pas de forge au Guet d\'azur. Cependant, j\'ai vu quelques apprentis forgerons arriver de cet étrange campement situé sur la côte au sud. Vous devriez peut-être y jeter un œil.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10089;
UPDATE locales_npc_text SET entry = 10090, Text0_0_loc2 = 'Nous ne disposons pas d\'un cuisinier, mais il y a quelque chose d\'étrange… Des odeurs délicieuses sont portées par le vent du sud. Suivez votre odorat, hein ?', Text0_1_loc2 = 'Nous ne disposons pas d\'un cuisinier, mais il y a quelque chose d\'étrange… Des odeurs délicieuses sont portées par le vent du sud. Suivez votre odorat, hein ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10090;
UPDATE locales_npc_text SET entry = 10091, Text0_0_loc2 = 'Nos enchanteurs passent leurs journées dans l\'Exodar, à travailler sur tous leurs projets. Nahogg entamera votre apprentissage.', Text0_1_loc2 = 'Nos enchanteurs passent leurs journées dans l\'Exodar, à travailler sur tous leurs projets. Nahogg entamera votre apprentissage.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10091;
UPDATE locales_npc_text SET entry = 10092, Text0_0_loc2 = 'L\'artificier du coin, Daelo, pourra vous enseigner les bases de l\'ingénierie. Vous pourrez peut-être aider nos techniciens à réparer cet engin de communication en panne !', Text0_1_loc2 = 'L\'artificier du coin, Daelo, pourra vous enseigner les bases de l\'ingénierie. Vous pourrez peut-être aider nos techniciens à réparer cet engin de communication en panne !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10092;
UPDATE locales_npc_text SET entry = 10093, Text0_0_loc2 = 'Fateema est notre anachorète, et sa participation aux soins prodigués aux victimes de la catastrophe est inestimable. Elle exerce son art dans la partie ouest du Guet.', Text0_1_loc2 = 'Fateema est notre anachorète, et sa participation aux soins prodigués aux victimes de la catastrophe est inestimable. Elle exerce son art dans la partie ouest du Guet.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10093;
UPDATE locales_npc_text SET entry = 10094, Text0_0_loc2 = 'On veut devenir $gpêcheur:pêcheuse; à la ligne ? Eh bien, Diktynna vit sur la rive du gué d\'Ammen, et sa canne et son filet nous ravitaillent tous. Je suis sûr qu\'un peu d\'aide lui serait utile.', Text0_1_loc2 = 'On veut devenir $gpêcheur:pêcheuse; à la ligne ? Eh bien, Diktynna vit sur la rive du gué d\'Ammen, et sa canne et son filet nous ravitaillent tous. Je suis sûre qu\'un peu d\'aide lui serait utile.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10094;
UPDATE locales_npc_text SET entry = 10095, Text0_0_loc2 = 'Heur étudie la flore locale depuis notre atterrissage. Il pourra vous guider. Vous le trouverez aux côtés de Daedal sur le bord ouest du Guet d\'azur.', Text0_1_loc2 = 'Heur étudie la flore locale depuis notre atterrissage. Il pourra vous guider. Vous le trouverez aux côtés de Daedal sur le bord ouest du Guet d\'azur.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10095;
UPDATE locales_npc_text SET entry = 10096, Text0_0_loc2 = 'Nos nouveaux alliés, les furbolgs Calmepins, nous ont dit être intéressés par les peaux que nous trouvons. Ils vivent au nord d\'ici. À votre place, j\'irais chercher des connaissances auprès d\'eux.', Text0_1_loc2 = 'Nos nouveaux alliés, les furbolgs Calmepins, nous ont dit être intéressés par les peaux que nous trouvons. Ils vivent au nord d\'ici. À votre place, j\'irais chercher des connaissances auprès d\'eux.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10096;
UPDATE locales_npc_text SET entry = 10097, Text0_0_loc2 = 'Dulvi est la femme qu\'il vous faut. Elle entraîne les prospecteurs débutants au centre du Guet.', Text0_1_loc2 = 'Dulvi est la femme qu\'il vous faut. Elle entraîne les prospecteurs débutants au centre du Guet.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10097;
UPDATE locales_npc_text SET entry = 10098, Text0_0_loc2 = 'Allez au repaire des Calmepins, mon $gami:amie;. Les furbolgs qui s\'y trouvent sont très coopératifs, et ils devraient avoir les connaissances que vous recherchez.', Text0_1_loc2 = 'Allez au repaire des Calmepins, mon $gami:amie;. Les furbolgs qui s\'y trouvent sont très coopératifs, et ils devraient avoir les connaissances que vous recherchez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10098;
UPDATE locales_npc_text SET entry = 10099, Text0_0_loc2 = 'Nous n\'avons pas de tailleur ici au Guet d\'azur. Mais vous pouvez essayer au campement qui se trouve sur la plage : on dirait qu\'il y a là-bas des commerçants venus de par-delà les mers.', Text0_1_loc2 = 'Nous n\'avons pas de tailleur ici au Guet d\'azur. Mais vous pouvez essayer au campement qui se trouve sur la plage : on dirait qu\'il y a là-bas des commerçants venus de par-delà les mers.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10099;
UPDATE locales_npc_text SET entry = 10100, Text0_0_loc2 = 'Vous chercher un lapidaire ? Nos joailliers sont dans l\'Exodar, ils font des merveilles à partir des matériaux qu\'ils ont découverts. Allez parler à Farii quand vous y serez.', Text0_1_loc2 = 'Vous chercher un lapidaire ? Nos joailliers sont dans l\'Exodar, ils font des merveilles à partir des matériaux qu\'ils ont découverts. Allez parler à Farii quand vous y serez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10100;
UPDATE locales_npc_text SET entry = 10101, Text0_0_loc2 = 'Vous voulez quoi ?! J\'ai entendu parler d\'une de ces étranges créatures draeneï, dans la vallée des Héros. Elle saura peut-être. Pas moi, en tout cas.', Text0_1_loc2 = 'Vous voulez quoi ?! J\'ai entendu parler d\'une de ces étranges créatures draeneï, dans la vallée des Héros. Elle saura peut-être. Pas moi, en tout cas.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10101;
UPDATE locales_npc_text SET entry = 10103, Text0_0_loc2 = 'L\'esprit des eaux nous a abandonné. Notre tribu est condamnée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10103;
UPDATE locales_npc_text SET entry = 10104, Text0_0_loc2 = 'Les vents ne parlent plus à notre tribu, $n. Mon peuple est perdu sans ses anciens pour mener les esprits à eux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10104;
UPDATE locales_npc_text SET entry = 10105, Text0_0_loc2 = 'Ces chaînes qui me lient m\'empêchent d\'apporter l\'esprit de feu à mon peuple, $N. Des âmes sans feu sont des âmes qui ne peuvent se rebeller contre leurs maîtres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10105;
UPDATE locales_npc_text SET entry = 10106, Text0_0_loc2 = 'Je ne sais pas ce qui vous prend de réclamer un chaman... mais j\'ai vu quelqu\'un qui correspond au profil. Il y a une femme qui se fait appeler long-voyante sur la rive, dans la vallée des Héros.', Text0_1_loc2 = 'Je ne sais pas ce qui vous prend de réclamer un chaman... mais j\'ai vu quelqu\'un qui correspond au profil. Il y a une femme qui se fait appeler long-voyante sur la rive, dans la vallée des Héros.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10106;
