UPDATE locales_npc_text SET entry = 10109, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10109;
UPDATE locales_npc_text SET entry = 10113, Text0_0_loc2 = 'Il s\'agit de la tour de Karazhan et résidence de mon maître, Medivh. En tant qu\'humble portier, je ne peux guère vous en dire plus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10113;
UPDATE locales_npc_text SET entry = 10114, Text0_0_loc2 = 'Toutes les demandes concernant Medivh sont à adresser à Moroes, le régisseur de la tour. Moi-même, je n\'en sais que très peu sur les affaires du Maître. Il me semble que plusieurs personnalités de la noblesse sont actuellement en audience avec Moroes dans la salle de banquet, si vous souhaitez vous renseigner plus avant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10114;
UPDATE locales_npc_text SET entry = 10115, Text0_0_loc2 = 'De la Conciergerie où vous vous trouvez actuellement, vous pouvez prendre trois directions. Les escaliers situés derrière moi mènent dans la grande salle de bal, où se tient actuellement une réception pour des invités. Je suis sûr que vous sentirez les écuries qui se trouvent à proximité, et l\'entrée du quartiers des domestiques se trouve juste à côté de la porte de la Conciergerie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10115;
UPDATE locales_npc_text SET entry = 10117, Text0_0_loc2 = 'Minuit est la monture personnelle d\'Attumen le Veneur. Je n\'ai jamais vu de jument aussi rapide, puissante ou dotée d\'un tel caractère. Et son cavalier est encore plus fantasque ! Tout le monde se tient à distance respectueuse, sous peine de recevoir un coup de sabot dans la tête de la part de Minuit ou une volée d\'Attumen.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10117;
UPDATE locales_npc_text SET entry = 10118, Text0_0_loc2 = 'Ces satanées araignées, ces chauves-souris qui n\'arrêtent jamais de crier et ces maudites bêtes démoniaques qui se sont toutes installées dans les quartiers des domestiques. On ne peut plus se déplacer en toute sécurité, surtout quand les gros sont de sortie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10118;
UPDATE locales_npc_text SET entry = 10119, Text0_0_loc2 = 'Euh oui, je n\'en ai jamais vu personnellement, mais parfois il m\'arrive de les entendre au loin. Gardez l\'œil ouvert si vous vous aventurez dedans.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10119;
UPDATE locales_npc_text SET entry = 10124, Text0_0_loc2 = 'C\'est par là… quelque part… je le sais. Quelque part dans cette bibliothèque se cachent les connaissances que je recherche…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10124;
UPDATE locales_npc_text SET entry = 10125, Text0_0_loc2 = null, Text0_1_loc2 = 'Cette tour… c\'est une folie, une pure folie. Ce n\'est pas une bibliothèque, c\'est une meule de foin géante remplie de livres et je pense que l\'aiguille n\'existe carrément pas ! Bon, où en étais-je ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10125;
UPDATE locales_npc_text SET entry = 10126, Text0_0_loc2 = 'Votre présence ici perturbe des recherches très importantes. Veuillez partir sur le champ. Bon, où en étais-je encore ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10126;
UPDATE locales_npc_text SET entry = 10127, Text0_0_loc2 = 'Ah, qu\'il est étrange de trouver des clients potentiels dans un tel endroit !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10127;
