UPDATE locales_npc_text SET entry = 10152, Text0_0_loc2 = 'Salutations, créature de chair. Je suis Navuud, chercheur en chef ici au poste de garde du Protectorat.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10152;
UPDATE locales_npc_text SET entry = 10153, Text0_0_loc2 = null, Text0_1_loc2 = 'Quitter Kael\'thas est la meilleure décision que nous ayons jamais prise. Ses fidèles sont trop aveugles ou trop effrayés pour se rendre compte où il les mène.$B$BMais ce n\'est pas notre cas.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10153;
UPDATE locales_npc_text SET entry = 10178, Text0_0_loc2 = 'Certains cherchent à mentir et à déformer la vérité. Les voyants, bien au contraire, font la lumière sur la vérité.$B$BLe but de cette bibliothèque, $N, est que tout le monde devienne un voyant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10178;
UPDATE locales_npc_text SET entry = 10179, Text0_0_loc2 = 'Chez les éthériens, certains sont désignés pour traiter spécifiquement avec la chair et les êtres qui en sont constitués. Ils sont connus sous le nom de manieurs de chair.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10179;
UPDATE locales_npc_text SET entry = 10180, Text0_0_loc2 = 'Cherchez-vous quelque chose en notre beau pays ?', Text0_1_loc2 = 'Cherchez-vous quelque chose en notre beau pays ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10180;
