UPDATE locales_npc_text SET entry = 10193, Text0_0_loc2 = 'Je connais un alchimiste du cru. Je crois que son nom est Sheynathren. La dernière fois que je l\'ai vu, il se dirigeait vers le havre de Saltheril, sans doute pour se rendre à l\'une de ses fêtes ridicules.', Text0_1_loc2 = 'Je connais un alchimiste du cru. Je crois que son nom est Sheynathren. La dernière fois que je l\'ai vu, il se dirigeait vers le havre de Saltheril, sans doute pour se rendre à l\'une de ses fêtes ridicules.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10193;
