UPDATE locales_npc_text SET entry = 10224, Text0_0_loc2 = 'Eh ouais, les muffins c\'est mon domaine !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10224;
UPDATE locales_npc_text SET entry = 10229, Text0_0_loc2 = 'Créature de chair, je suis le capitaine Saeed du Protectorat, et ces soldats qui m\'entourent sont mes vengeurs. Nous attendons les instructions de la créature de chair qui a détruit le conduit du Vide. Quand elle l\'ordonnera, nous nous ouvrirons un chemin jusqu\'au repaire de Dimensius et attendrons son signal pour porter le coup fatal !$B$BCela n\'a que trop duré... Le seigneur du Vide aura bientôt affaire à nos forces unies !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10229;
