UPDATE locales_npc_text SET entry = 10233, Text0_0_loc2 = '« Bienvenue à l\'Écodôme Terres-médianes, $gvoyageur:voyageuse;. Je tiens à vous avertir si vous souhaitez traiter avec ce gobelin, Shauly. Je ne crois pas avoir jamais entendu quelqu\'un utiliser l\'expression « mon pote » comme signe de ponctuation avant lui. »', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10233;
UPDATE locales_npc_text SET entry = 10234, Text0_0_loc2 = 'Le Bastion allérien est cerné de toute part par ses ennemis. Il est temps de passer à l\'attaque.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10234;
UPDATE locales_npc_text SET entry = 10236, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous venez par là, rendez-vous utile, parce que le traqueur des ombres, là, ne fait pas grand-chose.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10236;
UPDATE locales_npc_text SET entry = 10237, Text0_0_loc2 = null, Text0_1_loc2 = 'La bataille du mont Hyjal est l\'un des événements les plus surveillés de cette ligne temporelle. Si un intrus venait modifier son issue, toute l\'histoire postérieure en serait altérée.$B$BEn tant que premier compagnon de l\'Aspect, je suis bien plus sensible au flux du temps que les autres dragons de bronze. J\'ai récemment senti un léger clapotis provenant des événements qui entourent la bataille du mont Hyjal. Quelqu\'un ou quelque chose tente de modifier cette ligne temporelle, et il faut l\'en empêcher.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10237;
UPDATE locales_npc_text SET entry = 10239, Text0_0_loc2 = 'Que voulez-vous ? Parlez. Je ne vais pas laisser l\'un de mes elekk entre vos mains avant que vous n\'ayez fait preuve de votre amitié et votre dévouement envers mon peuple.', Text0_1_loc2 = 'Que voulez-vous ? Parlez. Je ne vais pas laisser l\'un de mes elekk entre vos mains avant que vous n\'ayez fait preuve de votre amitié et votre dévouement envers mon peuple.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10239;
UPDATE locales_npc_text SET entry = 10245, Text0_0_loc2 = null, Text0_1_loc2 = 'Nos progrès au Bosquet vivant ne doivent pas être remis en cause. De sa santé et de sa pousse dépendent toutes nos vies.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10245;
UPDATE locales_npc_text SET entry = 10246, Text0_0_loc2 = 'Si ce n\'est déjà fait, pensez à offrir vos services à Rina Sourcelune.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10246;
UPDATE locales_npc_text SET entry = 10247, Text0_0_loc2 = 'Si j\'ai bien entendu, vous faites déjà du bon travail pour Sylvanaar.$B$BContinuez comme ça et nous pourrons peut-être conserver nos foyers ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10247;
UPDATE locales_npc_text SET entry = 10249, Text0_0_loc2 = 'Allez vous reposer, $gvoyageur:voyageuse;. Nos feux sont à vous.', Text0_1_loc2 = 'Allez vous reposer, $gvoyageur:voyageuse;. Nos feux sont à vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10249;
UPDATE locales_npc_text SET entry = 10250, Text0_0_loc2 = 'C\'est ici que Gul\'dan rompit le lien entre les orcs et les esprits des éléments. L\'annihilation totale des draeneï ne suffisait pas à étancher son inextinguible soif de puissance. Il fallait aussi qu\'il détruise Draenor, qu\'il rase cette terre et en draine toute l\'énergie pour en faire usage à la guerre qu\'il menait.$B$BIl ne subsiste plus maintenant que les restes de sa folie.$B$BRegardez cet autel, $r : ce monde est à jamais hanté...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10250;
UPDATE locales_npc_text SET entry = 10251, Text0_0_loc2 = 'Je rends hommage à mes ancêtres selon l\'immémoriale tradition chamanique. Nous ne devons jamais oublier les sacrifices qu\'ils ont consentis afin que nous puissions être ici.$B$BEt nous devons, à notre tour, consentir les sacrifices nécessaires pour garantir l\'avenir de nos descendants.$B$BQu\'est-ce qui vous amène à moi, $C ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10251;
UPDATE locales_npc_text SET entry = 10252, Text0_0_loc2 = 'Il ne s\'est pas écoulé longtemps depuis que nous avons dû arracher le contrôle du bastion des Sire-tonnerre aux ogres de la région.$B$BVous êtes là pour nous aider à étayer nos défenses ? Je sais que Gor\'drek recherche de l\'aide.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10252;
UPDATE locales_npc_text SET entry = 10253, Text0_0_loc2 = 'Est-ce que la guerre vous plaît, $N ? N\'oubliez pas pourquoi nous sommes là… cela rend le combat et la mort beaucoup plus supportables.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10253;
UPDATE locales_npc_text SET entry = 10254, Text0_0_loc2 = 'Il y a deux boîtes aux lettres au Siège du naaru : une devant la banque, et l\'autre devant l\'hôtel des ventes.', Text0_1_loc2 = 'Il y a deux boîtes aux lettres au Siège du naaru : une devant la banque, et l\'autre devant l\'hôtel des ventes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10254;
UPDATE locales_npc_text SET entry = 10255, Text0_0_loc2 = 'La halte de l\'Expédition est un poste difficile à tenir, mais capital pour la défense du bastion de l\'Honneur. Les informations qui y sont collectées et l\'engagement de nos soldats contre la Légion et les orcs de Zeth\'Gor sont un apport inestimable à la campagne de l\'Alliance dans les Flammes infernales.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10255;
UPDATE locales_npc_text SET entry = 10256, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10256;
UPDATE locales_npc_text SET entry = 10257, Text0_0_loc2 = 'Sur la peau bien tendue entre les os figure un appel aux armes contre le défenseur principal de deux postes de l\'Alliance, Sylvanaar et le Bosquet vivant, à l\'ouest.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10257;
UPDATE locales_npc_text SET entry = 10258, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis Azimi, enchantée ! Je fais souvent le chemin jusqu\'ici pour vendre à boire et des fruits frais aux hommes. Avez-vous faim, ou peut-être soif ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10258;
UPDATE locales_npc_text SET entry = 10259, Text0_0_loc2 = 'Peuh ! Est-ce que j\'ai l\'air d\'avoir le temps de tailler le bout de gras ?$B$BHein ?$B$BFaites vite. Il y a des ogres à tuer !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10259;
UPDATE locales_npc_text SET entry = 10264, Text0_0_loc2 = 'Qu\'$gun:une:r; $r contribue aux intérêts de Sylvanaar, comme vous le faites, est une bonne chose. Nous avons accompli beaucoup en peu de temps ici, mais les forces qui nous cernent menacent notre mode de vie.$B$BCe que vous avez à dire m\'intéresse. Parlez, $c.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10264;
UPDATE locales_npc_text SET entry = 10266, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue au Refuge cénarien, $N. J\'ai pour ordres de vous proposer des denrées de base à la vente, si vous en avez besoin.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10266;
UPDATE locales_npc_text SET entry = 10269, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10269;
UPDATE locales_npc_text SET entry = 10270, Text0_0_loc2 = 'À la demande de l\'Alliance, Bronwyn et moi-même avons été dépêchés ici en tant que représentants de la Ligue des explorateurs pour étudier ces montagnes.$B$BDe vous à moi, après un long séjour à Silithus, nous avions surtout besoin de vacances, et cet endroit semble suffisamment agréable.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10270;
UPDATE locales_npc_text SET entry = 10271, Text0_0_loc2 = null, Text0_1_loc2 = 'Borgrim n\'a pas tout à fait tort. Nous avons sans doute besoin de faire une pause.$B$BMais maintenant que je suis ici, je suis impatiente de m\'y remettre et d\'étudier ce que cette terre a à nous offrir.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10271;
UPDATE locales_npc_text SET entry = 10272, Text0_0_loc2 = '$N, je suis heureux de vous voir. Nos alliés nains ont besoin de notre aide dans la vallée d\'Alterac. Êtes-vous $gprêt:prête; à les aider ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10272;
UPDATE locales_npc_text SET entry = 10275, Text0_0_loc2 = 'Les Fils de Lothar livrent une guerre sans fin. Je n\'arrive pas à croire que notre équipement reste en si bon état !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10275;
UPDATE locales_npc_text SET entry = 10276, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10276;
UPDATE locales_npc_text SET entry = 10277, Text0_0_loc2 = 'Throm\'ka, $N. Nous avons besoin $gde héros tels:d\'héroïnes telles; que vous au goulet des Chanteguerres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10277;
UPDATE locales_npc_text SET entry = 10278, Text0_0_loc2 = 'Nos compagnons de l\'Alliance aident la Ligue d\'Arathor à défendre le bassin d\'Arathi. Ils auraient bien besoin de votre aide contre les Profanateurs morts-vivants, $c.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10278;
UPDATE locales_npc_text SET entry = 10279, Text0_0_loc2 = null, Text0_1_loc2 = 'Nos amis de l\'Alliance s\'unissent pour combattre aux côtés des Sentinelles d\'Aile-d\'argent au goulet des Chanteguerre. Irez-vous là-bas les aider ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10279;
UPDATE locales_npc_text SET entry = 10280, Text0_0_loc2 = 'Salutations, $gvoyageur:voyageuse;. Je vous en prie, asseyez-vous auprès du feu pour vous réchauffer.$B$BAttention à la saillie brisée et aux bêtes affamées qui se trouvent plus bas !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10280;
UPDATE locales_npc_text SET entry = 10282, Text0_0_loc2 = null, Text0_1_loc2 = 'N\'oubliez jamais, $c. La guerre fait encore rage en Azeroth. Retournerez-vous aider la Horde dans la vallée d\'Alterac ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10282;
UPDATE locales_npc_text SET entry = 10283, Text0_0_loc2 = 'Throm\'ka, $c. Saviez-vous que notre combat contre les sentinelles d\'Aile-d\'argent continue encore et encore ? Votre aide là-bas serait la bienvenue…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10283;
UPDATE locales_npc_text SET entry = 10284, Text0_0_loc2 = 'J\'ai parcouru un long chemin à la recherche d\'un moyen de vaincre définitivement la Ligue d\'Arathor au bassin d\'Arathi, mais ce butor empagné d\'Adam Eternum me suit à la trace.
$B
Mais si les tueurs tels que vous décidaient de revenir en Azeroth, nous pourrions réussir à battre l\'Alliance une bonne fois pour toute !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10284;
UPDATE locales_npc_text SET entry = 10285, Text0_0_loc2 = null, Text0_1_loc2 = 'Comment que ça va ? T\'en as assez de l\'Outreterre et tu veux retourner à la vallée d\'Alterac pour en coller une bonne à la Horde ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10285;
UPDATE locales_npc_text SET entry = 10286, Text0_0_loc2 = 'Les sentinelles d\'Aile d\'argent continuent le combat contre la Horde au goulet des Chanteguerre, $c. Ce qu\'il reste à voir, c\'est si vous allez décider de retourner les aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10286;
UPDATE locales_npc_text SET entry = 10287, Text0_0_loc2 = 'Je suis venu en Outreterre à la recherche de puissants artefacts pour aider la Ligue d\'Arathor contre les Profanateurs au bassin d\'Arathi… Mais tout ce que j\'y ai trouvé, c\'est ce super pagne magique, un ridicule acolyte gnome et un mort-vivant égotiste qui me colle et n\'arrête pas de répéter que je lui ai piqué son château des ombres ou quelque chose comme ça.
$B
Quoi qu\'il en soit, voudriez-vous vous joindre au combat de la Ligue pendant que je continue mes recherches ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10287;
UPDATE locales_npc_text SET entry = 10288, Text0_0_loc2 = '<Oric donne l\'impression de n\'être là que pour une pitoyable tentative d\'effet comique et qu\'il ne faut pas lui prêter attention sous peine de l\'encourager à continuer.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10288;
UPDATE locales_npc_text SET entry = 10290, Text0_0_loc2 = '<C\'est un tigre de guerre géant, cuirassé et muni d\'une selle. C\'est déjà bien assez absurde comme ça, on ne va pas lui demander de parler, hein ?>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10290;
UPDATE locales_npc_text SET entry = 10291, Text0_0_loc2 = null, Text0_1_loc2 = 'Seuls les mages peuvent apprendre la magie des portails, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10291;
UPDATE locales_npc_text SET entry = 10292, Text0_0_loc2 = null, Text0_1_loc2 = 'En tant que mage, vous faites partie d\'une élite qui peut invoquer un portail capable de vous ramener ici même. Je peux vous apprendre comment si vous avez les compétences requises.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10292;
UPDATE locales_npc_text SET entry = 10293, Text0_0_loc2 = null, Text0_1_loc2 = 'La Société royale des apothicaires de Fossoyeuse tient beaucoup à venir en aide à nos alliés orcs ici en Outreterre. C\'est une occasion de renforcer les liens qui nous unissent et de trouver de nouveaux spécimens et mélanges à étudier.$B$BDes mélanges… comme par exemple des poisons.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10293;
UPDATE locales_npc_text SET entry = 10299, Text0_0_loc2 = 'Le poste de Blanc-relais est pour moi une halte imprévue, c\'est le moins qu\'on puisse dire. Mes clients comptent sur moi pour la livraison des marchandises, et je commence à me demander si j\'arriverai à temps avec tous ces retards.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10299;
UPDATE locales_npc_text SET entry = 10300, Text0_0_loc2 = null, Text0_1_loc2 = 'Hé, n\'ouvrez pas la cage ! D\'accord ?$B$BCes ogres me tiennent exactement comme je le veux.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10300;
UPDATE locales_npc_text SET entry = 10301, Text0_0_loc2 = 'On raconte que dans la péninsule des Flammes infernales, la chaleur et la poussière sont assez insupportables... Je n\'en sais rien moi-même, mais je fais confiance à ceux de mes alliés qui en sont sortis en vie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10301;
UPDATE locales_npc_text SET entry = 10302, Text0_0_loc2 = 'Revenir sur votre spécialité dans le travail du cuir élémentaire n\'est pas une chose à faire à la légère. Si vous choisissez d\'abandonner votre spécialité, vous oublierez tous les patrons liés à cette spécialité que vous aviez déjà appris !', Text0_1_loc2 = 'Revenir sur votre spécialité dans le travail du cuir élémentaire n\'est pas une chose à faire à la légère. Si vous choisissez d\'abandonner votre spécialité, vous oublierez tous les patrons liés à cette spécialité que vous aviez déjà appris !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10302;
UPDATE locales_npc_text SET entry = 10305, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10305;
UPDATE locales_npc_text SET entry = 10306, Text0_0_loc2 = 'Ces orcs sont vraiment impatients de faire valoir leurs revendications sur l\'Outreterre ! Si je vous disais combien ils me payent pour mon allégeance, vous pourriez me tuer de jalousie !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10306;
UPDATE locales_npc_text SET entry = 10307, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10307;
UPDATE locales_npc_text SET entry = 10308, Text0_0_loc2 = 'Si vous avez assez d\'or, je peux vous apprendre à chevaucher un faucon-pérégrin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10308;
