UPDATE locales_npc_text SET entry = 10312, Text0_0_loc2 = 'Êtes-vous $gprêt:prête; à entendre l\'histoire de la Formule de damnation, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10312;
UPDATE locales_npc_text SET entry = 10313, Text0_0_loc2 = 'Très bien. Les mots que vous recherchez portent un nom, $N. On les appelle la Formule de damnation. Il s\'agit des mots que Gul\'dan lui-même a prononcé pour déchirer cette terre. Du sort qui a rompu les derniers liens que gardaient les orcs avec les éléments de Draenor.$B$B<Oronok hoche la tête.>$B$BOui, c\'est cette formule que vous devez reconstituer. Ce sont les mots de cette formule que vous devrez au final prononcer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10313;
UPDATE locales_npc_text SET entry = 10314, Text0_0_loc2 = 'La Formule de damnation fut conservée dans les appartements privés de Gul\'dan à Karabor pendant vingt ans. Mmm, il est vrai que vous n\'avez aucune idée de ce qu\'est Karabor, n\'est-ce pas ?$B$BLe Temple noir…$B$BC\'est maintenant la demeure d\'Illidan Hurlorage.$B$BQuand Illidan prit le contrôle du Temple noir, il découvrit la formule. Il se rendit compte que ce monde avait déjà été ravagé, mais il pensa qu\'il serait sage de conserver la formule pour une utilisation ultérieure.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10314;
UPDATE locales_npc_text SET entry = 10315, Text0_0_loc2 = 'Lorsque vous vous tenez à l\'Autel de la damnation et voyez les esprits des damnés qui prient agenouillés, il y a un élément que vous ne voyez pas : moi, présent aux côtés de Gul\'dan.$B$B<Oronok hoche la tête.>$B$BJe vous ai dit avoir tenu bien des rôles, n\'est-ce pas ? Eh bien, l\'un de ces rôles était celui de lieutenant de Gul\'dan.$B$BJ\'ai accompli d\'horribles choses dans ma vie. J\'ai massacré hommes, femmes et enfants. J\'ai dansé dans le sang de draeneï innocents... et pourtant...$B$B<Oronok s\'étouffe.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10315;
UPDATE locales_npc_text SET entry = 10316, Text0_0_loc2 = 'Jamais je n\'oublierai ce jour, $R. Jamais. Bien que j\'avais depuis longtemps abandonné le chamanisme pour la magie de l\'ombre, je sentis la rupture. Une si profonde sensation de perte… Les mots ne suffiraient pas à vous l\'expliquer.$B$BQuand le lien avec les éléments fut rompu, tous les orcs de Draenor en furent altérés. Si le moindre doute sur la puissance de l\'ombre avait jamais existé, il fut balayé par le déferlement de la lave en fusion sur la terre déchirée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10316;
UPDATE locales_npc_text SET entry = 10317, Text0_0_loc2 = 'Une vague de dégoût me submergea. Une fois le rituel terminé, je courus chez moi retrouver mes enfants. Je rassemblai tout ce que mes bras pouvaient porter, et je partis avec mes garçons. Nous courûmes nous cacher dans les collines. Il n\'y avait vraiment pas d\'autre solution...$B$BIl ne s\'écoule pas un jour sans que je pense à la rupture. Il a fallu 20 ans pour rétablir un lien avec les éléments. Il a fallu 20 ans pour que les éléments commencent seulement à guérir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10317;
UPDATE locales_npc_text SET entry = 10318, Text0_0_loc2 = 'Comme je vous le disais, la formule fut découverte par Illidan quand il s\'empara du Temple noir. Il scinda le recueil en trois parties et en confia la garde à ses serviteurs.$B$BMes garçons… Ils ont grandi, maintenant. Quand j\'ai appris ce qu\'Illidan avait fait de la formule, je les ai envoyés en récupérer les différents fragments.$B$BIl faut que vous retrouviez mes enfants, $N. Il faut que vous les retrouviez, eux et la Formule de damnation.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10318;
UPDATE locales_npc_text SET entry = 10321, Text0_0_loc2 = 'Bienvenue à Shattrath, siège des Sha\'tar en Outreterre. Cherchez-vous des indications ?', Text0_1_loc2 = 'Bienvenue à Shattrath, siège des Sha\'tar en Outreterre. Cherchez-vous des indications ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10321;
UPDATE locales_npc_text SET entry = 10322, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est mon étude de cette culture archaïque qu\'incarnait le clan Sire-tonnerre qui m\'a amenée ici.$B$BLes membres du clan étaient faibles, et succombèrent à l\'illusion de puissance que leur donnait la transformation en gangr\'orcs par le sang de Mannoroth.$B$BJ\'ai lu tous leurs écrits. Je souhaite tout particulièrement retrouver un artefact, un tambour, une flèche et une tablette qui furent perdus quand nous avons écrasé les ogres de Flèchelame pour les repousser jusque dans leur bastion.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10322;
UPDATE locales_npc_text SET entry = 10323, Text0_0_loc2 = 'Le corps sans vie d\'Ar\'tor reste suspendu en l\'air par la magie démoniaque. Ils puisent en lui, même mort…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10323;
UPDATE locales_npc_text SET entry = 10324, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis heureuse que vous ayez décidé de défendre notre cause. Pour vous en récompenser, je vais imprégner une bague des résidus du Puits d\'éternité que vous m\'avez apportés.$B$BJe vous propose quatre voies différentes selon lesquelles libérer le pouvoir qu\'ils recèlent. Choisissez sagement, $N. Les sables du temps ne s\'écoulent aisément que dans une direction.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10324;
UPDATE locales_npc_text SET entry = 10327, Text0_0_loc2 = 'Je suis… mort.$B$BPourquoi suis-je encore là ? Ne devais-je pas m\'élever vers un grand terrain de chasse ?$B$BNon… Il faut d\'abord que j\'accomplisse ma mission. Avant de quitter ce monde, il faut retrouver la deuxième partie de la Formule de damnation.', Text0_1_loc2 = 'Je suis… morte.$B$BPourquoi suis-je encore là ? Ne devais-je pas m\'élever vers un grand terrain de chasse ?$B$BNon… Il faut d\'abord que j\'accomplisse ma mission. Avant de quitter ce monde, il faut retrouver la deuxième partie de la Formule de damnation.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10327;
UPDATE locales_npc_text SET entry = 10329, Text0_0_loc2 = 'Revenir sur votre spécialité dans la fabrication d\'épées n\'est pas une chose à faire à la légère. Si vous choisissez d\'abandonner votre spécialité, vous oublierez tous les plans qui y sont liés que vous aviez déjà appris !', Text0_1_loc2 = 'Revenir sur votre spécialité dans la fabrication d\'épées n\'est pas une chose à faire à la légère. Si vous choisissez d\'abandonner votre spécialité, vous oublierez tous les plans qui y sont liés que vous aviez déjà appris !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10329;
UPDATE locales_npc_text SET entry = 10338, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10338;
UPDATE locales_npc_text SET entry = 10339, Text0_0_loc2 = 'J’ai fait mon temps dans les tranchées, et j’ai passé de nombreuses années à former les jeunes. C’est bon de revenir au pays. Quoi qu’il lui soit arrivé, c’est mon foyer.', Text0_1_loc2 = null, Text1_0_loc2 = 'C’est un honneur que de s’occuper de ces bêtes intrépides qui s’interposent entre les membres de mon clan et la mort.', Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10339;
UPDATE locales_npc_text SET entry = 10341, Text0_0_loc2 = 'Les ogres, ils m\'ont enterré jusqu\'au cou et après, ils ont oublié de revenir.$B$BOuais, $gmon pote:ma p\'tite;, c\'est super-drôle. Marrez-vous un bon coup. Une tête fantomatique, pas de corps, tout ça...$B$BAh, si vous acceptez de m\'aider, j\'m\'arrangerai pour que ça vaille le coup.$B$BMais bon, si vous préférez rigoler…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10341;
UPDATE locales_npc_text SET entry = 10349, Text0_0_loc2 = '<Nuainn Aile-tempête hoche la tête.>$B$BAvec la Légion d\'un côté et les sbires d\'Illidan de l\'autre, on va avoir besoin de toutes nos forces pour défendre le bastion, et c\'est ce qu\'on va faire ! Tous les Marteaux-hardis dignes de ce nom préfèreront mourir plutôt que d\'abandonner notre foyer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10349;
UPDATE locales_npc_text SET entry = 10350, Text0_0_loc2 = 'Et on se tient droit, grunt ! T\'es sous les ordres de la Garde kor\'kron, maintenant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10350;
UPDATE locales_npc_text SET entry = 10352, Text0_0_loc2 = 'Cette terre porte encore les cicatrices du passé. Je me demande si elles s’effaceront jamais.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10352;
