UPDATE locales_npc_text SET entry = 10355, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10355;
UPDATE locales_npc_text SET entry = 10356, Text0_0_loc2 = 'Avancez avec circonspection, $r. Au-delà de ce pont, c\'est l\'enfer qui vous attend.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10356;
UPDATE locales_npc_text SET entry = 10359, Text0_0_loc2 = 'Le poste de Toshley est ce qui se fait de mieux ! La solitude et l\'abondance d\'étranges sources d\'énergie de l\'Outreterre forment un cadre idéal pour les expériences extrêmes !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10359;
UPDATE locales_npc_text SET entry = 10360, Text0_0_loc2 = 'Avez-vous rencontré mon clone - je veux dire, mon frère jumeau ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10360;
UPDATE locales_npc_text SET entry = 10363, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10363;
UPDATE locales_npc_text SET entry = 10365, Text0_0_loc2 = 'Avec l\'aide de mon frère Zap de Long-guet, j\'ai construit un imploseur dimensionnel ici-même dans la Zone 52. Qui aurait besoin d\'une fusée quand on peut voyager instantanément grâce à l\'ingénierie gobeline ! Possédez-vous les compétences nécessaires en ingénierie gobeline pour apprendre à construire votre propre balise d\'implosion ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10365;
UPDATE locales_npc_text SET entry = 10373, Text0_0_loc2 = 'Merci de m\'avoir tiré de là. Je vous en dois une !', Text0_1_loc2 = null, Text1_0_loc2 = 'Je devrais réussir à m\'en sortir maintenant. Merci !', Text1_1_loc2 = null, Text2_0_loc2 = 'Cet endroit est dément ! Je pourrais passer des années à analyser toute cette technologie !', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10373;
UPDATE locales_npc_text SET entry = 10378, Text0_0_loc2 = 'Juste un petit recueil de chant qui à mon avis pourrait vous faire plaisir. Il contient les paroles d\'une chanson appelée la complainte des Bien-nés. Cette chanson même qui fut extraite de l\'éther par un charme de dame Sylvanas.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10378;
UPDATE locales_npc_text SET entry = 10379, Text0_0_loc2 = 'Il y a deux banques à Shattrath, toutes deux situées le long du bord du noyau central de la ville. L\'une est tenue par les draeneï de l\'Aldor, l\'autre par les elfes de sang des Clairvoyants.', Text0_1_loc2 = 'Il y a deux banques à Shattrath, toutes deux situées le long du bord du noyau central de la ville. L\'une est tenue par les draeneï de l\'Aldor, l\'autre par les elfes de sang des Clairvoyants.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10379;
