UPDATE locales_npc_text SET entry = 10407, Text0_0_loc2 = 'Écoute-moi bien, péon. Nous avons une procédure précise pour la livraison et la garde du fragment de la Formule. Le messager remet une missive cachetée de la main du seigneur Illidan. Pas de missive, pas de transfert.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10407;
