UPDATE locales_npc_text SET entry = 10452, Text0_0_loc2 = 'La prospection est la méthode de base pour trouver des gemmes ! Il suffit d\'utiliser la technique de prospection (qui se trouve dans l\'onglet général de votre grimoire) sur un empilement de cinq échantillons de minerai pour en extraire des gemmes. N\'importe quel type de minerais non-précieux fera l\'affaire !$B$BUn minerai de meilleure qualité vous rapportera des gemmes plus précieuses. Par exemple, vous trouverez des gemmes de meilleure qualité dans du minerai de fer que dans du cuivre.', Text0_1_loc2 = 'La prospection est la méthode de base pour trouver des gemmes ! Il suffit d\'utiliser la technique de prospection (qui se trouve dans l\'onglet général de votre grimoire) sur un empilement de cinq échantillons de minerai pour en extraire des gemmes. N\'importe quel type de minerais non-précieux fera l\'affaire !$B$BUn minerai de meilleure qualité vous rapportera des gemmes plus précieuses. Par exemple, vous trouverez des gemmes de meilleure qualité dans du minerai de fer que dans du cuivre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10452;
