UPDATE locales_npc_text SET entry = 10458, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'aimerais avoir le temps d\'enseigner à tous ceux qui se présentent à moi, mais ce n\'est hélas pas l\'ordre des choses. Tant que vous n\'aurez pas assimilé tout ce que Padaar a à vous apprendre, je ne pourrai pas vous aider.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10458;
