UPDATE locales_npc_text SET entry = 10460, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis extrêmement occupée, $R.$B$BNous faisons face à un terrible problème qui doit être réglé sur le champ !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10460;
UPDATE locales_npc_text SET entry = 10465, Text0_0_loc2 = 'L\'étreinte d\'Illidan sur cette terre se desserre. Le sentez-vous, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10465;
UPDATE locales_npc_text SET entry = 10466, Text0_0_loc2 = null, Text0_1_loc2 = 'Quand on chevauche avec les Irréguliers de Razak, on apprend une ou deux règles à suivre quand on reçoit un ordre.$B$BLa première règle, c\'est qu\'on combat jusqu\'à la mort. La deuxième règle, c\'est : voir la première règle.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10466;
UPDATE locales_npc_text SET entry = 10467, Text0_0_loc2 = 'Besoin de faire aiguiser votre arme, ou peut-être d\'en acheter une nouvelle ? Il vaut mieux prévenir que guérir…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10467;
UPDATE locales_npc_text SET entry = 10468, Text0_0_loc2 = 'Si c\'est un marteau que vous cherchez, vous venez au bon endroit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10468;
UPDATE locales_npc_text SET entry = 10469, Text0_0_loc2 = '$GMon fils:Jeune femme;, je ne vais pas vous mentir : la situation n\'est pas jolie-jolie. Nous subissons les assauts continus des insectes, et notre mode de vie, notre liberté même sont menacées !$B$BAllez-vous agir en $gcitoyen:citoyenne; responsable et nous prêter main forte ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10469;
UPDATE locales_npc_text SET entry = 10470, Text0_0_loc2 = 'Teron Fielsang ? Oui… J\'ai entendu parler de lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10470;
UPDATE locales_npc_text SET entry = 10471, Text0_0_loc2 = 'Superbe ! Merveilleux ! Quel numéro sensationnel !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10471;
UPDATE locales_npc_text SET entry = 10473, Text0_0_loc2 = null, Text0_1_loc2 = 'Quand j\'étais jeune, j\'ai travaillé dur pour devenir une prêtresse anachorète. Les femmes exarques n\'existaient quasiment pas.$B$BDe nos jours, peu importe qu\'on soit un homme ou une femme : c\'est le courage et la conviction qui font la différence.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10473;
UPDATE locales_npc_text SET entry = 10474, Text0_0_loc2 = 'Si vous êtes $gprêt:prête; et souhaitez retourner au cours normal du temps, je peux vous emmener auprès d\'Andormu dans les Grottes du temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10474;
UPDATE locales_npc_text SET entry = 10475, Text0_0_loc2 = 'Façonner le cours du temps est une tâche très ardue et délicate, mais vous vous en êtes remarquablement bien $gtiré:tirée; aujourd\'hui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10475;
UPDATE locales_npc_text SET entry = 10491, Text0_0_loc2 = 'Vous venez aider à gérer les troubles ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10491;
UPDATE locales_npc_text SET entry = 10492, Text0_0_loc2 = 'Pour le récompenser de ses années de service, Illidan permit à Kael\'thas d\'envoyer cinq guerriers s\'entraîner auprès de lui. Des elfes seraient pour la première fois autorisés à devenir chasseurs de démons.$B$BL\'entraînement fut terrible. Illidan fit subir aux guerriers de Kael des épreuves qui auraient eu raison de la plupart des chasseurs de démons accomplis.$B$BTrois des elfes de Kael moururent pendant l\'initiation. Un quatrième perdit la raison.$B$BL\'un d\'entre eux en vint à bout.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10492;
UPDATE locales_npc_text SET entry = 10493, Text0_0_loc2 = 'Varedis était son nom. À la surprise d\'Illidan, non seulement il triompha des insoutenables rites de passage imposés par son entraînement, mais il démontrait déjà une meilleure maîtrise de ses pouvoirs que la plupart des chasseurs de démons chevronnés.$B$BIllidan sentit le potentiel de Varedis et lui affecta trois maîtres des chasseurs de démons pour qu\'ils lui enseignent leur art.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10493;
UPDATE locales_npc_text SET entry = 10494, Text0_0_loc2 = 'En moins d\'un an d\'entraînement auprès des trois meilleurs chasseurs de démons d\'Illidan, Varedis les avait surpassés.$B$BInsatisfait de l\'enseignement qu\'ils proposaient, Varedis s\'infiltra dans le Conseil des ombres, au sein duquel il apprit l\'existence d\'une puissante relique, le Livre des noms gangrenés. Il compulsa ce livre en secret et en mémorisa chaque passage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10494;
UPDATE locales_npc_text SET entry = 10495, Text0_0_loc2 = 'La rumeur dit que connaître le vrai nom d\'un démon vous donne un certain ascendant sur lui.$B$BOn raconte que le Livre des noms gangrenés contient les noms de tous les démons qui ont jamais existé. En en mémorisant le contenu, Varedis avait mis la main sur une source de puissance de nature à rivaliser avec celle acquise par Illidan en buvant dans le crâne de Gul\'dan.$B$BÀ la demande d\'Illidan, Varedis dirige maintenant le terrain d\'entraînement situés dans les ruines de Karabor, au côté des trois maîtres qui l\'avaient initié.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10495;
UPDATE locales_npc_text SET entry = 10496, Text0_0_loc2 = 'Hors d\'ici, créature mortelle ! Tes semblables ne sont pas les bienvenus ici.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10496;
UPDATE locales_npc_text SET entry = 10497, Text0_0_loc2 = 'Il est peu surprenant qu\'un elfe de sang possède un tel potentiel pour la chasse de démons. Après tout, les sin\'dorei n\'ont pas cette aversion naturelle pour la magie qu\'ont les elfes de la nuit.$B$BCependant, je ne m\'attends pas à ce qu\'ils soient nombreux à réussir. La plupart d\'entre eux, y compris Varedis, recherchent la puissance pour elle-même. Cette puissance les consumera, car ils ne sont pas animés par cette pure flamme qui brûle dans le cœur d\'un vrai chasseur de démon.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10497;
UPDATE locales_npc_text SET entry = 10500, Text0_0_loc2 = 'On peut quitter les Fils de Lothar, mais jamais l\'esprit des Fils ne vous quitte.$B$B<Le forgeron hausse les épaules.>$B$BC\'est difficile à expliquer, mais je sens que ma place n\'est pas au bastion de l\'Honneur en ce moment, c\'est tout.$B$BPeut-être que je suis destiné à quelque chose de plus important.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10500;
