UPDATE locales_npc_text SET entry = 10530, Text0_0_loc2 = 'Bonjour, $c. Je suis un officier du mérite au service de Lune-d\'argent. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de la Horde.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mon registre, et vos services seront reconnus par Lune-d\'argent.', Text0_1_loc2 = 'Bonjour, $c. Je suis un officier du mérite au service de Lune-d\'argent. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de la Horde.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mon registre, et vos services seront reconnus par Lune-d\'argent.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10530;
UPDATE locales_npc_text SET entry = 10531, Text0_0_loc2 = 'Bonjour, $c. Je suis un officier du mérite au service de l\'Exodar. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de l\'Alliance.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mon registre, et vos services seront reconnus par l\'Exodar.', Text0_1_loc2 = 'Bonjour, $c. Je suis un officier du mérite au service de l\'Exodar. Ma tâche est d\'aider les aventuriers qui ont reçu des chevalières de mérite de l\'Alliance.$B$BVous pouvez m\'en remettre différentes quantités, mais le plus intéressant pour vous est de me les donner par dix. En échange, je noterai vos exploits dans mon registre, et vos services seront reconnus par l\'Exodar.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10531;
UPDATE locales_npc_text SET entry = 10532, Text0_0_loc2 = 'Les Tranchantes offrent de nombreuses opportunités. Il est malheureux que certaines personnes se considèrent supérieures à toutes les autres en ce qui concerne l\'exploitation de ces opportunités.$B$BPour qui se prennent-ils dans l\'Expédition cénarienne, pour nous dire ce que nous pouvons ou ne pouvons pas faire ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10532;
UPDATE locales_npc_text SET entry = 10533, Text0_0_loc2 = 'Des négociations ? DES NÉGOCIATIONS ?!$B$BPermettez-moi de vous exposer très clairement notre position. Nous sommes un peuple libre, nous faisons ce que nous voulons et allons où nous voulons. L\'Expédition cénarienne ne possède pas cette chaîne de montagnes.$B$BIl n\'y aura aucune négociation ! Maintenant, retournez voir les druides, et dites-leur que nous continuerons à faire comme il nous plaît.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10533;
UPDATE locales_npc_text SET entry = 10534, Text0_0_loc2 = 'Nous sommes venus dans la sylve Ruuan pour tenter de rétablir l\'équilibre naturel de ces montagnes.$B$BCependant, les interférences causées par les arakkoa et le culte du Ver rendent cette tâche très difficile.$B$BHeureusement, il y a aussi des gens comme Samia et vous qui acceptent de nous prêter main-forte.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10534;
UPDATE locales_npc_text SET entry = 10535, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10535;
UPDATE locales_npc_text SET entry = 10537, Text0_0_loc2 = 'Je détecte une perturbation dans vos coordonnées paranarbulatriques ! Vous avez dû utiliser le Zephyrium Capacitorium récemment. Vous ne pouvez pas le relancer avant que la charge ne soit dissipée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10537;
UPDATE locales_npc_text SET entry = 10538, Text0_0_loc2 = null, Text0_1_loc2 = 'Je suis ici à la demande de mon maître, le baron Sablecrin, pour aider l\'Expédition cénarienne à régler son problème avec le culte du Ver.$B$BJe suis en quelque sorte une experte du sujet.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10538;
UPDATE locales_npc_text SET entry = 10539, Text0_0_loc2 = 'Nuaar, tu te sens bien ? Tu n\'as pas l\'air toi-même, aujourd\'hui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10539;
UPDATE locales_npc_text SET entry = 10541, Text0_0_loc2 = 'Ah oui, la réunion. D\'accord, mettons-nous au travail.$B$BIl est un fait que la collecte de bois et de bétail en provenance de la sylve Ruuan, dont tu as la responsabilité, a terriblement ralenti.$B$BNous avons besoin de ces ressources, mais tu as permis aux druides de l\'Expédition cénarienne de nous gêner !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10541;
UPDATE locales_npc_text SET entry = 10542, Text0_0_loc2 = 'Ce ne sont pas des excuses que je veux, mais des résultats !$B$BTu crois qu\'on fait tout cela ici pour plaisanter ? Si ce n\'est pas mené correctement, alors non seulement ces prétendus bons samaritains vont rappliquer par ici, mais en plus nous serons jugés indignes de nous élever parmis les rangs de l\'Aile noire.$B$BC\'est ça que tu veux ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10542;
UPDATE locales_npc_text SET entry = 10543, Text0_0_loc2 = 'Tu pourrais au moins essayer d\'avoir l\'air un peu convaincant.$B$BÉcoute, Nuaar, je ne comptais pas te le dire, mais autant le faire puisque ça te concerne aussi. Maxnar prépare une attaque frontale contre les druides à la sylve Ruuan. Et il compte bien les anéantir.$B$BNotre guerre avec les ogres Roche\'mok nous pose déjà assez de problèmes, et nous ne pouvons pas nous permettre de tenir deux fronts à la fois. J\'ai mis en place une trêve provisoire avec les arakkoa.$B$BEh bien, qu\'en penses-tu ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10543;
UPDATE locales_npc_text SET entry = 10544, Text0_0_loc2 = 'Nous faisons les derniers préparatifs en ce moment même. Le reste dépendra de la vitesse à laquelle tu organiseras tes troupes à la sylve Ruuan.$B$BPenses-tu pouvoir gérer cela et achever la mission rapidement ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10544;
UPDATE locales_npc_text SET entry = 10545, Text0_0_loc2 = 'Voilà, ça, c\'est ce que je veux entendre !$B$BJe suis content que nous ayons eu cette petite réunion. Je suis rassuré au sujet de l\'attaque, maintenant. Avec des chefs comme toi sur la ligne de front, comment pourrions-nous perdre ?$B$BBon, tu as tes ordres de marche. Maintenant, retourne à la sylve Ruuan et mets tout en place !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10545;
UPDATE locales_npc_text SET entry = 10546, Text0_0_loc2 = null, Text0_1_loc2 = 'Karazhan n\'a pas toujours été si… sinistre. Son assombrissement n\'est venu qu\'avec la mort de Medivh. Ou ce que nous croyons avoir été la mort de Medivh.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10546;
