UPDATE locales_npc_text SET entry = 10552, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10552;
UPDATE locales_npc_text SET entry = 10553, Text0_0_loc2 = 'Bonjour, surveillant. Tout est prêt pour l\'attaque ?', Text0_1_loc2 = 'Bonjour, surveillant. Tout est prêt pour l\'attaque ?', Text1_0_loc2 = 'Besoin de quelque chose, chef ?', Text1_1_loc2 = 'Besoin de quelque chose, chef ?', Text2_0_loc2 = 'Vous n\'avez pas l\'air dans votre assiette, surveillant.', Text2_1_loc2 = 'Vous n\'avez pas l\'air dans votre assiette, surveillant.', Text3_0_loc2 = 'J\'ai entendu dire que Kolphis Sombrécaille est à votre recherche.', Text3_1_loc2 = 'J\'ai entendu dire que Kolphis Sombrécaille est à votre recherche.', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10553;
UPDATE locales_npc_text SET entry = 10554, Text0_0_loc2 = 'Ces druides exploitent une source d’énergie impressionnante ! Ils peuvent se changer en bêtes… projeter des éclairs et des faisceaux de lumière lunaire… Je dois découvrir comment ils s’y prennent !$B$BSi ça se trouve, ils portent des batteries sous leurs robes…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10554;
UPDATE locales_npc_text SET entry = 10555, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10555;
UPDATE locales_npc_text SET entry = 10556, Text0_0_loc2 = '<L\'arakkoa vous gratifie d\'un regard hagard. Ses yeux pointent tour à tour vers vous et à travers vous.>$B$BQuel fruit ont donc porté nos efforts, autre qu\'une abomination plus grande encore que celle que nous souhaitions empêcher ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10556;
UPDATE locales_npc_text SET entry = 10557, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10557;
UPDATE locales_npc_text SET entry = 10558, Text0_0_loc2 = 'En Outreterre, nos forces combattent la Horde pour le contrôle de l\'Œil du cyclone. Se battre au beau milieu des énergies déchaînées du Néant est dangereux. Avez-vous l\'étoffe pour rejoindre nos rangs ?', Text0_1_loc2 = 'En Outreterre, nos forces combattent la Horde pour le contrôle de l\'Œil du cyclone. Se battre au beau milieu des énergies déchaînées du Néant est dangereux. Avez-vous l\'étoffe pour rejoindre nos rangs ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10558;
UPDATE locales_npc_text SET entry = 10559, Text0_0_loc2 = 'Les cellules de mana de l\'Œil du cyclone sont précieuses. Nous ne pouvons pas nous permettre de laisser les draeneï s\'en emparer ! Vous, là ! Rendez-vous utile, venez combattre avec nous !', Text0_1_loc2 = 'Les cellules de mana de l\'Œil du cyclone sont précieuses. Nous ne pouvons pas nous permettre de laisser les draeneï s\'en emparer ! Vous, là ! Rendez-vous utile, venez combattre avec nous !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10559;
UPDATE locales_npc_text SET entry = 10560, Text0_0_loc2 = '$N ! Venez ici ! Vous avez sans aucun doute entendu parler de la bataille qui fait rage dans la vallée d\'Alterac, en Azeroth. Nous avons besoin de tous les combattants aptes que nous pourrons rassembler. Combattez auprès de la garde Foudrepique et écrasez les Loups-de-givre !', Text0_1_loc2 = '$N ! Venez ici ! Vous avez sans aucun doute entendu parler de la bataille qui fait rage dans la vallée d\'Alterac, en Azeroth. Nous avons besoin de tous les combattants aptes que nous pourrons rassembler. Combattez auprès de la garde Foudrepique et écrasez les Loups-de-givre !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10560;
UPDATE locales_npc_text SET entry = 10561, Text0_0_loc2 = 'Ce vol vous expédie tout au bout de la Crête chantante. C\'est très dangereux !$B$BJ\'insiste pour que vous me remettiez une décharge signée…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10561;
UPDATE locales_npc_text SET entry = 10562, Text0_0_loc2 = 'Nous sommes toujours heureux d\'accumuler de nouvelles informations de vol. Où voulez-vous aller ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10562;
UPDATE locales_npc_text SET entry = 10563, Text0_0_loc2 = 'Qu\'y a-t-il ? Vous ne voyez donc pas que je suis un nain occupé ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10563;
UPDATE locales_npc_text SET entry = 10565, Text0_0_loc2 = 'L\'exploration de ces terres nouvelles est sans nul doute importante, mais il ne faut pas laisser tomber Azeroth ! Nous avons plus que jamais besoin de vous dans notre combat contre les Profanateurs. Nous devons récupérer toutes les ressources !', Text0_1_loc2 = 'L\'exploration de ces terres nouvelles est sans nul doute importante, mais il ne faut pas laisser tomber Azeroth ! Nous avons plus que jamais besoin de vous dans notre combat contre les Profanateurs. Nous devons récupérer toutes les ressources !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10565;
UPDATE locales_npc_text SET entry = 10566, Text0_0_loc2 = 'Vous ! $C ! Nous avons besoin de votre aide ! Les brutaux orcs Chanteguerre détruisent les belles merveilles d\'Orneval, et nous avons besoin que vous nous prêtiez votre puissance sur le champ de bataille. Revenez avec nous en Azeroth et combattez pour l\'honneur des Sentinelles d\'Aile-d\'argent ! Vous joindrez-vous à nous ?', Text0_1_loc2 = 'Vous ! $C ! Nous avons besoin de votre aide ! Les brutaux orcs Chanteguerre détruisent les belles merveilles d\'Orneval, et nous avons besoin que vous nous prêtiez votre puissance sur le champ de bataille. Revenez avec nous en Azeroth et combattez pour l\'honneur des Sentinelles d\'Aile-d\'argent ! Vous joindrez-vous à nous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10566;
UPDATE locales_npc_text SET entry = 10568, Text0_0_loc2 = 'Il y a un vieux proverbe qui dit : « Donne un poisson à un homme, tu le nourriras pour un jour. Apprends-lui à pêcher, tu le nourriras pour toujours. ». Mais évidemment, vous aurez peut-être envie de le cuisiner, ce poisson. C\'est là que j\'interviens.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10568;
UPDATE locales_npc_text SET entry = 10569, Text0_0_loc2 = 'Salut $N ! Comment se passent vos aventures ? Mes expériences progressent bien…$B$BN\'hésitez pas à revenir me voir ! Il se peut que j\'ai d\'autres vols d\'essai à vous faire faire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10569;
UPDATE locales_npc_text SET entry = 10570, Text0_0_loc2 = 'Force et honneur, $c. Nous avons besoin de soldats volontaires tels que vous dans la vallée d\'Alterac. Le territoire des Loup-de-givre doit être défendu ! Retournerez-vous en Azeroth pour nous prêter main forte ?', Text0_1_loc2 = 'Force et honneur, $c. Nous avons besoin de soldats volontaires tels que vous dans la vallée d\'Alterac. Le territoire des Loup-de-givre doit être défendu ! Retournerez-vous en Azeroth pour nous prêter main forte ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10570;
UPDATE locales_npc_text SET entry = 10571, Text0_0_loc2 = '$R ! Oui, vous ! Pourquoi est-ce vous perdez votre temps à déambuler dans l\'épave qu\'est cette ville alors que votre place est dans le bassin d\'Arathi ? Les assauts de la Ligue d\'Arathor sont impitoyables, nous avons besoin de votre aide, et vous serez $grécompensé:récompensée; !', Text0_1_loc2 = '$R ! Oui, vous ! Pourquoi est-ce vous perdez votre temps à déambuler dans l\'épave qu\'est cette ville alors que votre place est dans le bassin d\'Arathi ? Les assauts de la Ligue d\'Arathor sont impitoyables, nous avons besoin de votre aide, et vous serez $grécompensé:récompensée; !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10571;
UPDATE locales_npc_text SET entry = 10572, Text0_0_loc2 = '$C, loin d\'ici, en Azeroth, les Sentinelles d\'Aile-d\'argent ralentissent notre progression dans les bois. Nous avons besoin de vous sur le champ de bataille, pour les repousser ! Vous y trouverez l\'honneur, et votre réputation auprès des Voltigeurs Chanteguerre sera faite. Rejoignez-nous !', Text0_1_loc2 = '$C, loin d\'ici, en Azeroth, les Sentinelles d\'Aile-d\'argent ralentissent notre progression dans les bois. Nous avons besoin de vous sur le champ de bataille, pour les repousser ! Vous y trouverez l\'honneur, et votre réputation auprès des Voltigeurs Chanteguerre sera faite. Rejoignez-nous !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10572;
