UPDATE locales_npc_text SET entry = 10610, Text0_0_loc2 = null, Text0_1_loc2 = 'Le poste Brise-échine est une affectation merveilleuse ! Les aberrations de l\'Orbite-sanglante sont si proches qu\'on sent la puanteur de leur sang d\'ici ! Quel meilleur endroit pour exercer notre vengeance sur un clan qui a trahi ses frères et conclu un pacte avec les démons ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10610;
