UPDATE locales_npc_text SET entry = 10634, Text0_0_loc2 = 'La Fête lunaire bat son plein en Azeroth ! Retournez là-bas, et honorez les anciens ! C\'est une fête merveilleuse. Les festivités sont organisées à Reflet-de-lune, mais nous avons mis en place un transport rapide depuis Hurlevent, Forgefer, Darnassus, les Pitons du tonnerre, Orgrimmar et Fossoyeuse. Nous espérons vous voir là-bas !', Text0_1_loc2 = 'La Fête lunaire bat son plein en Azeroth ! Retournez là-bas, et honorez les anciens ! C\'est une fête merveilleuse. Les festivités sont organisées à Reflet-de-lune, mais nous avons mis en place un transport rapide depuis Hurlevent, Forgefer, Darnassus, les Pitons du tonnerre, Orgrimmar et Fossoyeuse. Nous espérons vous voir là-bas !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10634;
UPDATE locales_npc_text SET entry = 10635, Text0_0_loc2 = 'Hein ? Où suis-je ? Ça ne va pas du tout ! Regardez-moi cet endroit ! Il n\'est pas sur ma liste ! Ça va me donner une de ces paperasses…$B$B<Le gobelin griffone frénétiquement sur un petit carnet.>$B$BÉcoutez, si vous n\'avez rien de mieux à faire qu\'à rester $gplanté:plantée; ici à me regarder, pourquoi n\'allez-vous pas rendre visite à mon cousin, Kalin Kolportecœur, dans l\'une des capitales ? Le papier dit qu\'il s\'occupe d\'Orgrimmar, Hurlevent, les Pitons du tonnerre, Forgefer, Fossoyeuse et Darnassus. Allez, sortez d\'ici !', Text0_1_loc2 = 'Hein ? Où suis-je ? Ça ne va pas du tout ! Regardez-moi cet endroit ! Il n\'est pas sur ma liste ! Ça va me donner une de ces paperasses…$B$B<La gobeline griffone frénétiquement sur un petit carnet.>$B$BÉcoutez, si vous n\'avez rien de mieux à faire qu\'à rester $gplanté:plantée; ici à me regarder, pourquoi n\'allez-vous pas rendre visite à mon cousin, Kalin Kolportecœur, dans l\'une des capitales ? Le papier dit qu\'il s\'occupe d\'Orgrimmar, Hurlevent, les Pitons du tonnerre, Forgefer, Fossoyeuse et Darnassus. Allez, sortez d\'ici !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10635;
UPDATE locales_npc_text SET entry = 10636, Text0_0_loc2 = 'Il y a dans cet endroit des mystères que nous, les orcs, sommes impatients de découvrir. Des mystères de notre passé, des mystères de notre avenir, et des mystères qui nous permettent de voir en nous-mêmes.$B$BJ\'ai l\'espoir de percer tous ces mystères… m\'accompagnerez-vous sur ce chemin ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10636;
UPDATE locales_npc_text SET entry = 10637, Text0_0_loc2 = 'Il y a un problème dans les bois. Il faut en trouver la source avant qu\'il ne soit trop tard !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10637;
UPDATE locales_npc_text SET entry = 10640, Text0_0_loc2 = 'Bientôt, je planterai mes racines et dormirai. Mais avant de pouvoir me reposer, j\'ai quelque chose à terminer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10640;
UPDATE locales_npc_text SET entry = 10641, Text0_0_loc2 = 'Vous avez rapporté la verdure sur ma terre pour une courte période. Cela me suffit. Je suis en paix. Je suis prêt à trouver le grand sommeil.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10641;
UPDATE locales_npc_text SET entry = 10643, Text0_0_loc2 = 'C\'est une réussite ! J\'ai terminé mon harmonisation avec la citadelle des Flammes infernales et la magie qu\'elle abrite. Ah, toute cette puissance !$B$BL\'endroit grouille de gangr\'orcs… mais ce n\'est pas tout !! Je sens également une puissance démoniaque tapie en ces murs...$B$BSi vous m\'ouvrez votre âme, alors je pourrai me saisir de votre esprit - brièvement, cela va sans dire, et porter la plume au parchemin pour vous relater mes découvertes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10643;
UPDATE locales_npc_text SET entry = 10645, Text0_0_loc2 = 'Nous sommes encerclés par nos ennemis. Je préfèrerais offrir une vie meilleure aux Mok\'Nathal, mais les forces que nous combattons en ont décidé autrement.$B$BSi vous souhaitez prouver votre valeur aux Mok\'Nathal, aidez-nous à les éliminer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10645;
UPDATE locales_npc_text SET entry = 10647, Text0_0_loc2 = 'Ce que vous voyez vous étonne, $gétranger:étrangère; ?$B$BNous louons les esprits de nos ancêtres en gardant la flamme de leur mémoire en vie et en y rendant hommage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10647;
