UPDATE locales_npc_text SET entry = 10655, Text0_0_loc2 = 'Je suis heureux de vous revoir, $gmon ami:mon amie;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10655;
UPDATE locales_npc_text SET entry = 10657, Text0_0_loc2 = 'Ça fait du bien de faire une balade et de pouvoir dégourdir tous ses membres. Puis-je vous aider, $c ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10657;
UPDATE locales_npc_text SET entry = 10659, Text0_0_loc2 = 'Salutations, $C. Si j\'osais, je prendrais la liberté de vous rappeler que, cependant que j\'entre en harmonie avec la magie de la citadelle des Flammes infernales, nous verrons sans nul doute des gardes des Flammes infernales venir me houspiller.$B$BJe vous invite donc à assumer votre éminente appartenance à votre Horde et à jouer ici le rôle du héros... Protégez-moi !!', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10659;
UPDATE locales_npc_text SET entry = 10661, Text0_0_loc2 = 'La recherche de mon peuple. Notre lutte contre l\'Alliance. La guerre contre les ogres et leurs maîtres gronns.$B$BTout cela pèse lourd sur mes épaules, $C.$B$BMême moi, je peux avoir besoin d\'aide, de temps en temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10661;
UPDATE locales_npc_text SET entry = 10662, Text0_0_loc2 = 'Auchindoun abrite les esprits de mes ancêtres. Cela ne se voit peut-être pas, mais tous les Roués furent autrefois des draeneï… Nos esprits sont les mêmes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10662;
UPDATE locales_npc_text SET entry = 10664, Text0_0_loc2 = 'La Forgemort est dirigée par le Conseil des ombres, sous la stricte surveillance de la Légion.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10664;
UPDATE locales_npc_text SET entry = 10666, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10666;
UPDATE locales_npc_text SET entry = 10667, Text0_0_loc2 = 'Nous savons que les sectateurs du Ver s\'amassent de l\'autre côté du tunnel. Nous allons tenir cette position.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10667;
UPDATE locales_npc_text SET entry = 10669, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue, $N. Je suis experte dans le travail de l\'étoffe lunaire primordiale. Les tailleurs que cette discipline intéresse me demandent souvent de leur enseigner mon art.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10669;
UPDATE locales_npc_text SET entry = 10670, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'ai bien peur de ne pas avoir grand-chose à vous apprendre, $c. Vous pouvez jeter un œil à mes marchandises, bien sûr, mais elles ne vous seront pas d\'une grande utilité.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10670;
UPDATE locales_npc_text SET entry = 10671, Text0_0_loc2 = null, Text0_1_loc2 = 'Enchantée ! Je suis Gidge, l\'experte locale de la couture du feu-sorcier. Il n\'y a pas vraiment de concurrence, d\'ailleurs. Mais si vous souhaitez apprendre la couture du feu-sorcier et que vous n\'avez pas encore choisi de spécialisation, je peux vous aider.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10671;
UPDATE locales_npc_text SET entry = 10672, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous n\'êtes pas $gtailleur:tailleuse; je ne pense pas que mes patrons vous intéresseront. Mais vous pouvez toujours regarder !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10672;
UPDATE locales_npc_text SET entry = 10673, Text0_0_loc2 = 'Si vous recherchez des patrons ou des renseignements sur le travail du tisse-ombre, je serai heureux de vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10673;
UPDATE locales_npc_text SET entry = 10674, Text0_0_loc2 = 'Je suis désolé, $c, mais mes marchandises conviennent mieux à ceux qui savent dans quel sens tenir une aiguille.$B$BJe peux vous l\'assurer, la couture ne s\'arrête pas à la broderie des mouchoirs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10674;
UPDATE locales_npc_text SET entry = 10675, Text0_0_loc2 = null, Text0_1_loc2 = 'Ces Larmes de la déesse ont reçu la bénédiction d\'Elune, et leur pouvoir vous aidera à combattre la vile magie d\'Archimonde. Faites-en bon usage.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10675;
UPDATE locales_npc_text SET entry = 10676, Text0_0_loc2 = 'Mes mots les effraient. Ils me citent comme mauvais exemple. Tout cela est une parodie du sssseul vrai dieu, Neptulon.$B$B<Skar\'this crache.>$B$BL\'heure du châtiment a ssssonné, $gmortel:mortelle;. La glorieuse main du Chasseur des marées balaiera un jour les infidèles telle une faux moissonnant la nouvelle récolte.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10676;
UPDATE locales_npc_text SET entry = 10677, Text0_0_loc2 = 'Ce désert est balafré par une terrible force magique. Nous devons faire ce qui est en notre pouvoir pour soigner cette profonde blessure.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10677;
UPDATE locales_npc_text SET entry = 10679, Text0_0_loc2 = 'La Porte de la mort est une ouverture sur le monde que la Légion ardente utilise pour l\'élevage de ses gangrechiens et autres horreurs canines. Chacune de ces créatures que vous verrez provient de ce monde, et est arrivée par cette porte.$B$BRécemment, nous avons remarqué que la Légion ardente a accéléré sa cadence d\'invocation des créatures. $B$BIl faut fermer la Porte de la mort avant que cela ne devienne une marée irresistible, sinon tout sera perdu !', Text0_1_loc2 = 'La Porte de la mort est une ouverture sur le monde que la Légion ardente utilise pour l\'élevage de ses gangrechiens et autres horreurs canines. Chacune de ces créatures que vous verrez provient de ce monde, et est arrivée par cette porte.$B$BRécemment, nous avons remarqué que la Légion ardente a accéléré sa cadence d\'invocation des créatures. $B$BIl faut fermer la Porte de la mort avant que cela ne devienne une marée irresistible, sinon tout sera perdu !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10679;
UPDATE locales_npc_text SET entry = 10680, Text0_0_loc2 = 'Je suis venu sur ces terres pour y purifier les esprits troublés, mais après l\'attaque, je crains d\'avoir échoué dans ma mission. Le désert a ravi tellement d\'entre eux, je vous en prie, il faut leur venir en aide.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10680;
UPDATE locales_npc_text SET entry = 10682, Text0_0_loc2 = 'Puisse la Lumière guider vos pas, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10682;
UPDATE locales_npc_text SET entry = 10683, Text0_0_loc2 = 'C\'est avec une grande fierté que j\'accomplis ma mission au bastion de l\'Honneur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10683;
UPDATE locales_npc_text SET entry = 10686, Text0_0_loc2 = null, Text0_1_loc2 = 'Quand j\'ai ouvert mon académie des voleurs à Shattrath, la ville n\'était pas à moitié aussi bondée. Puis les réfugiés ont commencé à débarquer en masse et les naaru ont eu besoin de tous les bâtiments disponibles pour les accueillir.$B$BMon académie a donc fermé ses portes pour l\'instant, mais ce n\'est que temporaire. Je trouverai un nouvel endroit où m\'installer.$B$BAlors une toute nouvelle génération de voleurs pourra commencer à apprendre le b.a.-ba, comme par exemple à manier le surin taquin, à ramasser le butin pendant les combats et à accuser les autres de casser les métamorphoses. Tout cela sans oublier le premier commandement du voleur : tu ne feinteras point.', Text1_0_loc2 = null, Text1_1_loc2 = 'Pour bien utiliser l\'évasion, il faut bien comprendre que les meilleures quinze secondes de votre vie sont souvent les dernières.', Text2_0_loc2 = null, Text2_1_loc2 = 'Épées, griffes, dagues, pas de problème. J\'entraîne toutes les sortes de voleurs. Mais les masses, je refuse d\'y toucher, et je refuse de former un voleur qui se met en tête d\'en utiliser une.$B$BUne masse n\'a rien de discret. Vous allez vous retrouver avec un assassinat bruyant et tout le ménage à faire sur les bras, si vous choisissez cette voie.', Text3_0_loc2 = null, Text3_1_loc2 = 'Les cours pour débutants sont toujours remplis de jeunes aux yeux pleins d\'étoiles. Mais peu d\'entres eux passent la première épreuve.$B$BJ\'en ai eu assez d\'avoir à hurler : « quand je crie Disparition !, il faut disparaître. » Ils n\'écoutent jamais. Désormais, l\'épreuve supprime les étourdis.', Text4_0_loc2 = null, Text4_1_loc2 = 'Ne sortez jamais de chez vous sans une bonne réserve de poudre éclipsante, surtout quand vous devez travailler en groupe. Je peux vous garantir qu\'il y en aura toujours un pour se retrouver à cours.$B$BQuand le choix se pose de partager ses composants ou de laisser le pauvre bougre mourir, je préfère aider... la plupart du temps.', Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10686;
UPDATE locales_npc_text SET entry = 10687, Text0_0_loc2 = 'Je suis Zarevhi, du Consortium. Ma maîtrise des transmutations est connue sur des myriades de mondes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10687;
UPDATE locales_npc_text SET entry = 10689, Text0_0_loc2 = null, Text0_1_loc2 = 'Ah non ! Mon Fei Fei ne prendrait jamais quelque chose qui n\'est pas à lui ! C\'est un bon chien… Il a juste besoin qu\'on s\'occupe de lui, parfois. Donnez-lui une friandise, vous verrez !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10689;
UPDATE locales_npc_text SET entry = 10691, Text0_0_loc2 = 'Avant, je m\'amusais à relever tous les défis, quelle que soit la personne qui les lançait. Mais il y a longtemps que je me suis lassé de ce genre de victoire facile.$B$BMaintenant, je me consacre à mon magasin de composants, le temps que les affaires décollent, et j\'écris un livre sur mon expérience au Marteau du crépuscule.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10691;
UPDATE locales_npc_text SET entry = 10696, Text0_0_loc2 = 'Notre alchimiste local, Lorokeem, a apporté son équipement avec lui. Ce sera peut-être un peu différent que ce à quoi vous êtes $ghabitué:habituée; - c\'est un arakkoa, après tout - mais ça devrait vous convenir. Il perche dans la partie nord-ouest de la ville basse.', Text0_1_loc2 = 'Notre alchimiste local, Lorokeem, a apporté son équipement avec lui. Ce sera peut-être un peu différent que ce à quoi vous êtes $ghabitué:habituée; - c\'est un arakkoa, après tout - mais ça devrait vous convenir. Il perche dans la partie nord-ouest de la ville basse.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10696;
UPDATE locales_npc_text SET entry = 10697, Text0_0_loc2 = 'Nous avons deux marchandes de gemmes ici. La première travaille en collaboration avec les Clairvoyants, et l\'autre reste fidèle à l\'Aldor. Laquelle cherchez-vous ?', Text0_1_loc2 = 'Nous avons deux marchands de gemmes ici. Le premier travaille en collaboration avec les Clairvoyants, et l\'autre reste fidèle à l\'Aldor. Laquelle cherchez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10697;
UPDATE locales_npc_text SET entry = 10698, Text0_0_loc2 = 'Inessera est une femme vive, qui propose une sélection de gemmes ainsi que du matériel de joaillerie. Vous trouverez son échoppe dans la partie nord de l\'Éminence de l\'Aldor.', Text0_1_loc2 = 'Inessera est une femme vive, qui propose une sélection de gemmes ainsi que du matériel de joaillerie. Vous trouverez son échoppe dans la partie nord de l\'Éminence de l\'Aldor.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10698;
UPDATE locales_npc_text SET entry = 10699, Text0_0_loc2 = 'Lisrythe peut être d\'un abord… intimidant, mais elle aura ce que vous cherchez. Elle tient son commerce dans la partie est du Degré des Clairvoyants.', Text0_1_loc2 = 'Lisrythe peut être d\'un abord… intimidant, mais elle aura ce que vous cherchez. Elle tient son commerce dans la partie est du Degré des Clairvoyants.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10699;
