UPDATE locales_npc_text SET entry = 10702, Text0_0_loc2 = '<La créature indique la partie est du Degré.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10702;
UPDATE locales_npc_text SET entry = 10705, Text0_0_loc2 = 'Je ne suis pas disposé à vous enseigner avant que vous ne soyez $gexalté:exaltée; par les draeneï.', Text0_1_loc2 = 'Je ne suis pas disposée à vous enseigner avant que vous ne soyez $gexalté:exaltée; par les draeneï.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10705;
UPDATE locales_npc_text SET entry = 10706, Text0_0_loc2 = 'Oh, merci ! Vous avez scellé le puits de ténèbres qui était en moi ! Je vois à nouveau... et je vois le visage de mon sauveur !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10706;
UPDATE locales_npc_text SET entry = 10707, Text0_0_loc2 = 'Disparais, $c ! Laisse-nous, ou c\'est en toi que nous entrerons ensuite et tu apprendras à connaître le vrai désespoir !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10707;
UPDATE locales_npc_text SET entry = 10717, Text0_0_loc2 = 'Ne vous inquiétez pas. La halte du Fracas est ce qui se fait de plus solide ! Ceci dit, soyez tout de même $gprêt:prête; à bondir sur un griffon au cas où la base partirait en morceaux...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10717;
UPDATE locales_npc_text SET entry = 10718, Text0_0_loc2 = 'Maintenant que vous m\'avez battu, mettez vos compétences à l\'épreuve entre vous ! Prenez le contrôle de l\'un des rois pour commencer une partie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10718;
UPDATE locales_npc_text SET entry = 10720, Text0_0_loc2 = 'Vous souhaitez me parler, $r ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10720;
UPDATE locales_npc_text SET entry = 10721, Text0_0_loc2 = 'J\'ai vu bien des périls dans ce désert. Qui sait quelles créatures y rôdent encore, prêtes à fondre sur les voyageurs trop confiants ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10721;
