UPDATE locales_npc_text SET entry = 10729, Text0_0_loc2 = null, Text0_1_loc2 = 'Les guerres de l\'Outreterre sont les plus meurtrières que j\'aie jamais vues. Et les victimes les plus touchées sont les enfants qu\'elles laissent derrière elles.$B$BAujourd\'hui, avec la menace arakkoa qui pèse sur Shattrath depuis Skettis, le nombre d\'orphelins à recueillir va encore augmenter. Ces enfants vivent souvent sans même l\'espoir d\'un avenir radieux.$B$BMais vous avez le pouvoir de faire quelque chose. C\'est la Semaine des enfants, $c, et je vous implore de vous porter volontaire pour apporter la joie et l\'émerveillement à un enfant.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10729;
UPDATE locales_npc_text SET entry = 10730, Text0_0_loc2 = 'Je me sens seule. J\'aimerais qu\'il y ait plus d\'enfants de mon âge ici. Des fois, l\'orphelinat de Shattrath me manque.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10730;
UPDATE locales_npc_text SET entry = 10732, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est encore loin ?', Text1_0_loc2 = null, Text1_1_loc2 = 'C\'est vrai que tous les démons sont des grands gros gardes ?', Text2_0_loc2 = null, Text2_1_loc2 = 'Je crois qu\'il faut que je prenne un bain. *renifle* Je crois que vous en auriez bien besoin aussi !', Text3_0_loc2 = null, Text3_1_loc2 = 'Est-ce qu\'un tauren paladin, c\'est une vache sacrée ?', Text4_0_loc2 = null, Text4_1_loc2 = 'Est-ce que j\'ai l\'air grosse dans cette robe ?', Text5_0_loc2 = null, Text5_1_loc2 = 'Je suis sûre qu\'au fond Illidan est un gentil garçon. Il a juste besoin d\'une maman, lui aussi.', Text6_0_loc2 = null, Text6_1_loc2 = 'Je suis sûre que la glace, ça serait meilleur si on pouvait la sertir !', Text7_0_loc2 = null, Text7_1_loc2 = 'Les filles ça sent la vanille, les garçons ça sent le cochon !' WHERE entry = 10732;
UPDATE locales_npc_text SET entry = 10733, Text0_0_loc2 = 'Je suis libre ! Mais le secret... est en péril maintenant. Il faut prévenir Akama !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10733;
UPDATE locales_npc_text SET entry = 10734, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10734;
UPDATE locales_npc_text SET entry = 10735, Text0_0_loc2 = '<Morthis lance un coup d\'œil vers le ciel.>$B$BD\'une certaine manière, j\'envie la liberté dont jouissent Freux et Corvax. Être libre des responsabilités terrestres est vraiment une grande chance dans la vie.$B$BJe reprendrai mon vol un jour, mais j\'ai d\'abord une tâche à accomplir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10735;
UPDATE locales_npc_text SET entry = 10736, Text0_0_loc2 = 'Gezhe a vraiment été très gentil de me laisser venir ici pour devenir apprenti et apprendre toutes ces choses amusantes, $gmonsieur:madame;.$B$B<Jheel baisse la tête.>$B$BMais être séparé de ma sœur est vraiment très dur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10736;
