UPDATE locales_npc_text SET entry = 10741, Text0_0_loc2 = 'Aran a été vaincu ? Oui… je sens que son étreinte s’est relâchée, même si cela reste minime. Mais c’est suffisant pour me permettre de faire mon travail de portier ! Voulez-vous que je vous transporte à la bibliothèque du gardien, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10741;
