UPDATE locales_npc_text SET entry = 10744, Text0_0_loc2 = null, Text0_1_loc2 = 'Les conflits en Outreterre sont bien plus meurtriers que tout ce que j’ai pu voir auparavant. Et les victimes les plus tragiques sont les enfants qui restent seuls.$B$BAujourd’hui, avec la menace que les arakkoa de Skettis font peser sur Shattrath, nous avons de plus en plus d’orphelins à recueillir. Ces enfants vivent souvent sans le moindre espoir d’avoir un brillant avenir.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10744;
