UPDATE locales_npc_text SET entry = 10747, Text0_0_loc2 = 'Le tir à l\'arbalète est enseigné par Woo Ping, le maître d\'armes humain de l\'Arsenal de Weller, à Hurlevent ; par Handiir, le maître d\'armes draeneï qui se trouve à l\'étage des Commerces de l\'Exodar ; ou par Bixi Oscillognon, le maître d’armes gnome des Armes de Timberline, à Forgefer.', Text0_1_loc2 = 'Le tir à l\'arbalète est enseigné par Woo Ping, le maître d\'armes humain de l\'Arsenal de Weller, à Hurlevent ; par Handiir, le maître d\'armes draeneï qui se trouve à l\'étage des Commerces de l\'Exodar ; ou par Bixi Oscillognon, le maître d’armes gnome des Armes de Timberline, à Forgefer.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10747;
