UPDATE locales_npc_text SET entry = 10749, Text0_0_loc2 = 'Bixi Oscillognon, qui se trouve dans c\'t\' échoppe, peut vous apprendre l\'maniement des dagues. Si vous partez en vadrouille et qu\'vous cherchez toujours quelques conseils, n\'importe lequel des autres maîtres d\'armes - Woo Ping de Hurlevent, Handiir de l\'Exodar ou Ilyenia Lunéclat de Darnassus, pourra vous apprendre un truc ou deux. Moi, le combat au couteau, ça n\'a jamais été mon dada.', Text0_1_loc2 = 'Bixi Oscillognon, qui se trouve dans c\'t\' échoppe, peut vous apprendre l\'maniement des dagues. Si vous partez en vadrouille et qu\'vous cherchez toujours quelques conseils, n\'importe lequel des autres maîtres d\'armes - Woo Ping de Hurlevent, Handiir de l\'Exodar ou Ilyenia Lunéclat de Darnassus, pourra vous apprendre un truc ou deux. Moi, le combat au couteau, ça n\'a jamais été mon dada.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10749;
UPDATE locales_npc_text SET entry = 10750, Text0_0_loc2 = 'Ilyenia Lunéclat, le maître d\'armes qui réside sur la terrasse des Guerriers à Darnassus, ou Buliwyf, le maître d\'armes nain des Armes de Timberline à Forgefer, peuvent vous enseigner à manier les armes de pugilat.', Text0_1_loc2 = 'Ilyenia Lunéclat, le maître d\'armes qui réside sur la terrasse des Guerriers à Darnassus, ou Buliwyf, le maître d\'armes nain des Armes de Timberline à Forgefer, peuvent vous enseigner à manier les armes de pugilat.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10750;
