UPDATE locales_npc_text SET entry = 10752, Text0_0_loc2 = 'Je suis ici sur ordre du prince-nexus Haramad. Il se passe des choses étranges à la Foudreflèche.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10752;
UPDATE locales_npc_text SET entry = 10754, Text0_0_loc2 = 'Le maître d’armes nain Buliwyf peut vous apprendre à manier la hache à une main ou à deux mains. Vous pouvez le trouver aux Armes de Timberline, à Forgefer.', Text0_1_loc2 = 'Le maître d’armes nain Buliwyf peut vous apprendre à manier la hache à une main ou à deux mains. Vous pouvez le trouver aux Armes de Timberline, à Forgefer.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10754;
UPDATE locales_npc_text SET entry = 10755, Text0_0_loc2 = 'Handiir et Buliwyf peuvent vous montrer comment manier correctement une masse, quelle que soit sa taille. Vous pourrez trouver Buliwyf aux Armes de Timberline, à Forgefer. Handiir passe ses journées à l’Exodar, entraînant les draeneï à l’étage des Commerces.', Text0_1_loc2 = 'Handiir et Buliwyf peuvent vous montrer comment manier correctement une masse, quelle que soit sa taille. Vous pourrez trouver Buliwyf aux Armes de Timberline, à Forgefer. Handiir passe ses journées à l’Exodar, entraînant les draeneï à l’étage des Commerces.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10755;
