UPDATE locales_npc_text SET entry = 10757, Text0_0_loc2 = 'Le maniement des bâtons est enseigné par Woo Ping, le maître d’armes de l’Arsenal de Weller, à Hurlevent, et Ilyenia Lunéclat, qui entraîne les elfes de nuit sur la terrasse des Guerriers, à Darnassus.', Text0_1_loc2 = 'Le maniement des bâtons est enseigné par Woo Ping, le maître d’armes de l’Arsenal de Weller, à Hurlevent, et Ilyenia Lunéclat, qui entraîne les elfes de nuit sur la terrasse des Guerriers, à Darnassus.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10757;
UPDATE locales_npc_text SET entry = 10758, Text0_0_loc2 = 'Il existe deux maîtres de l’épée : Handiir et Woo Ping. Vous pourrez trouver le premier à l’étage des commerces de l’Exodar, et le second à l’Arsenal de Weller, à Hurlevent.', Text0_1_loc2 = 'Il existe deux maîtres de l’épée : Handiir et Woo Ping. Vous pourrez trouver le premier à l’étage des commerces de l’Exodar, et le second à l’Arsenal de Weller, à Hurlevent.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10758;
UPDATE locales_npc_text SET entry = 10759, Text0_0_loc2 = 'Ilyenia Lunéclat, sur la terrasse des Guerriers, à Darnassus, et Bixi Oscillognon des Armes de Timberline, à Forgefer, pourront vous montrer comment manier correctement les armes de jet.', Text0_1_loc2 = 'Ilyenia Lunéclat, sur la terrasse des Guerriers, à Darnassus, et Bixi Oscillognon des Armes de Timberline, à Forgefer, pourront vous montrer comment manier correctement les armes de jet.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10759;
UPDATE locales_npc_text SET entry = 10760, Text0_0_loc2 = 'Ilyenia Lunéclat, le maître d’armes elfe de la nuit qui se trouve à la terrasse des Guerriers de Darnassus, pourra vous montrer comment on s’sert d’un arc.', Text0_1_loc2 = 'Ilyenia Lunéclat, le maître d’armes elfe de la nuit qui se trouve à la terrasse des Guerriers de Darnassus, pourra vous montrer comment on s’sert d’un arc.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10760;
UPDATE locales_npc_text SET entry = 10761, Text0_0_loc2 = 'Ma brave camarade Bixi Oscillognon, là-bas, peut vous apprendre l’tir à l’arbalète. Si vous voulez d’autres professeurs, vous pouvez toujours essayer Ilyenia Lunéclat, à la terrasse des Guerriers de Darnassus, ou peut-être l’maître d’armes draeneï, Handiir, à l’étage des Commerces de l’Exodar.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10761;
UPDATE locales_npc_text SET entry = 10762, Text0_0_loc2 = 'Bixi pourra vous apprendre ici à balancer des armes de jet. Si Darnassus est plus votre truc, allez causer à Ilyenia Lunéclat sur la terrasse des Guerriers…', Text0_1_loc2 = 'Bixi pourra vous apprendre ici à balancer des armes de jet. Si Darnassus est plus votre truc, allez causer à Ilyenia Lunéclat sur la terrasse des Guerriers…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10762;
UPDATE locales_npc_text SET entry = 10763, Text0_0_loc2 = 'Il n’y a pas de meilleur expert sur les haches que Buliwyf, là-bas ! À une main, à deux mains… Il les maîtrise toutes !', Text0_1_loc2 = 'Il n’y a pas de meilleur expert sur les haches que Buliwyf, là-bas ! À une main, à deux mains… Il les maîtrise toutes !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10763;
UPDATE locales_npc_text SET entry = 10764, Text0_0_loc2 = 'Mon copain Buliwyf, là-bas, peut vous montrer comment utiliser les armes de pugilat… Ou, si vous préférez, vous pouvez aller voir Ilyenia Lunéclat. C’est le maître d’armes elfe de la nuit qui se trouve sur la terrasse des Guerriers, à Darnassus.', Text0_1_loc2 = 'Mon copain Buliwyf, là-bas, peut vous montrer comment utiliser les armes de pugilat… Ou, si vous préférez, vous pouvez aller voir Ilyenia Lunéclat. C’est le maître d’armes elfe de la nuit qui se trouve sur la terrasse des Guerriers, à Darnassus.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10764;
UPDATE locales_npc_text SET entry = 10765, Text0_0_loc2 = 'Buliwyf, qui se trouve ici même, vous apprendra tout ce qu\'il faut savoir sur les armes à feu !', Text0_1_loc2 = 'Buliwyf, qui se trouve ici même, vous apprendra tout ce qu\'il faut savoir sur les armes à feu !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10765;
UPDATE locales_npc_text SET entry = 10766, Text0_0_loc2 = 'Les masses sont la spécialité de Buliwyf ! Il se promène dans sa boutique, toujours en train de rouspéter. Si ses techniques d’entraînement ne vous conviennent pas, j’ai entendu dire que le maître d’armes draeneï de l’Exodar, Handiir, savait bien se débrouiller avec cette arme, lui aussi ! Il se trouve là-bas, à l’étage des Commerces.', Text0_1_loc2 = 'Les masses sont la spécialité de Buliwyf ! Il se promène dans sa boutique, toujours en train de rouspéter. Si ses techniques d’entraînement ne vous conviennent pas, j’ai entendu dire que le maître d’armes draeneï de l’Exodar, Handiir, savait bien se débrouiller avec cette arme, lui aussi ! Il se trouve là-bas, à l’étage des Commerces.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10766;
UPDATE locales_npc_text SET entry = 10767, Text0_0_loc2 = 'Si vous souhaitez apprendre à manier une arme, je peux vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10767;
UPDATE locales_npc_text SET entry = 10768, Text0_0_loc2 = 'Ah, vous voulez vous entraîner ailleurs ? Je peux vous donner des indications. Dites-moi quelle arme a éveillé votre intérêt.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10768;
UPDATE locales_npc_text SET entry = 10770, Text0_0_loc2 = 'Hanashi et Savoc peuvent tous les deux vous enseigner le maniement des haches à une main et à deux mains. Vous pouvez les trouver dans la vallée de l’Honneur, à Orgrimmar.', Text0_1_loc2 = 'Hanashi et Savoc peuvent tous les deux vous enseigner le maniement des haches à une main et à deux mains. Vous pouvez les trouver dans la vallée de l’Honneur, à Orgrimmar.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10770;
UPDATE locales_npc_text SET entry = 10771, Text0_0_loc2 = 'Les maîtres d’armes de la vallée de l’Honneur, à Orgrimmar, Savoc et Hanashi, peuvent vous apprendre à tirer à l’arc. Ileda, le maître d’armes de Lune-d’argent, qui se trouve à la place des Pérégrins, est aussi une experte.', Text0_1_loc2 = 'Les maîtres d’armes de la vallée de l’Honneur, à Orgrimmar, Savoc et Hanashi, peuvent vous apprendre à tirer à l’arc. Ileda, le maître d’armes de Lune-d’argent, qui se trouve à la place des Pérégrins, est aussi une experte.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10771;
