UPDATE locales_npc_text SET entry = 10777, Text0_0_loc2 = 'Le maître d’armes réprouvé, Archibald, sait parfaitement se servir des armes d’hast. Il enseigne son art dans le quartier de la Guerre de Fossoyeuse. Ileda, le maître d’armes elfe de sang de la place des Pérégrins, à Lune-d’argent, pourra aussi vous montrer les techniques appropriées.', Text0_1_loc2 = 'Le maître d’armes réprouvé, Archibald, sait parfaitement se servir des armes d’hast. Il enseigne son art dans le quartier de la Guerre de Fossoyeuse. Ileda, le maître d’armes elfe de sang de la place des Pérégrins, à Lune-d’argent, pourra aussi vous montrer les techniques appropriées.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10777;
