UPDATE locales_npc_text SET entry = 10779, Text0_0_loc2 = 'Ileda des elfes de sang, qui se trouve sur la place des Pérégrins à Lune-d’argent, apprend à ses élèves le maniement des épées à une main et à deux mains. Archibald, le maître d’armes de Fossoyeuse, peut aussi vous enseigner ; il se trouve dans le quartier de la Guerre.', Text0_1_loc2 = 'Ileda des elfes de sang, qui se trouve sur la place des Pérégrins à Lune-d’argent, apprend à ses élèves le maniement des épées à une main et à deux mains. Archibald, le maître d’armes de Fossoyeuse, peut aussi vous enseigner ; il se trouve dans le quartier de la Guerre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10779;
UPDATE locales_npc_text SET entry = 10780, Text0_0_loc2 = 'Sayoc et Hanashi, les maîtres d’armes d’Orgrimmar, sont de véritables experts dans le maniement des armes de jet. Ils entraînent leurs élèves dans la vallée de l’Honneur. Mais pour l’enseignement de toutes les finesses de cet art, Ileda de la place des Pérégrins de Lune-d’argent est sans rival.', Text0_1_loc2 = 'Sayoc et Hanashi, les maîtres d’armes d’Orgrimmar, sont de véritables experts dans le maniement des armes de jet. Ils entraînent leurs élèves dans la vallée de l’Honneur. Mais pour l’enseignement de toutes les finesses de cet art, Ileda de la place des Pérégrins de Lune-d’argent est sans rival.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10780;
