UPDATE locales_npc_text SET entry = 10800, Text0_0_loc2 = 'Il faut vaincre les fils de Gruul si nous voulons nous débarrasser de nos chaînes et vivre à nouveau en ogres libres !$B$BNous devons agir discrètement, mais rapidement, si notre résistance clandestine veut avoir une chance de succès.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10800;
