UPDATE locales_npc_text SET entry = 10807, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10807;
UPDATE locales_npc_text SET entry = 10808, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous en connaissez p\'têt\' un bout sur le dressage des animaux, mais est-ce vous avez d\'jà pris votre envol un jour ? Nous autres, les Marteaux-hardis, on chevauche les vents avec les plus belles bêtes qui soient. Pour une somme modique, si vous avez l\'estomac pour, j\'vous mitonnerai un bel entraînement.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10808;
UPDATE locales_npc_text SET entry = 10809, Text0_0_loc2 = 'Et si on vous dit Marteaux-hardis, vous pensez à quoi, hein ? Chevaucheurs de griffons, si vous avez un peu de jugeote ! Ombrelune, c\'est pas le Nid-de-l\'Aigle, mais on a quand même de beaux spécimens pour vous. Ouais, de belles bêtes. Regardez ces beautés et voyez s\'il y en a une qui vous tape dans l\'œil !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10809;
UPDATE locales_npc_text SET entry = 10817, Text0_0_loc2 = 'Vous avez l\'air de pouvoir vous débrouiller avec une bête qui se déplace au sol, mais si vous voulez vous envoler, il vous faudra un entraînement acharné. Et de l\'or. Vous avez de l\'or, hein ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10817;
UPDATE locales_npc_text SET entry = 10818, Text0_0_loc2 = null, Text0_1_loc2 = 'De belles bêtes, hein ? Les wyvernes que nous élevons ici sont dressées à endurer les conditions climatiques les plus étranges et hostiles de Draenor. Vous verrez qu\'elles sont fiables et résistantes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10818;
UPDATE locales_npc_text SET entry = 10819, Text0_0_loc2 = 'Je me fiche de qui vous êtes ou de ce que vous faites quand vous n\'êtes pas avec la Garde-ciel, mais tant que vous êtes ici, vous obéissez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10819;
UPDATE locales_npc_text SET entry = 10821, Text0_0_loc2 = 'Quand je serai grand, je veux être tanneur !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10821;
UPDATE locales_npc_text SET entry = 10830, Text0_0_loc2 = 'Il y a eu des plaintes à propos de cet… individu. Je me suis laissé dire que c\'était un type douteux. Je suis là pour m\'assurer que son commerce est bien légitime… et pour prendre les mesures qui s\'imposent si ce n\'est pas le cas.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10830;
UPDATE locales_npc_text SET entry = 10831, Text0_0_loc2 = 'Si vous parvenez à rassembler toutes les cartes de la suite de Tempêtes, vous recevrez en récompense une carte majeure de Sombrelune de Courroux. Elle augmentera vos chances de faire une réussite critique avec vos sorts et vos attaques physiques, jusqu\'à ce que cela se produise.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10831;
UPDATE locales_npc_text SET entry = 10832, Text0_0_loc2 = 'Si vous parvenez à rassembler toutes les cartes de la suite de Déraison, vous recevrez en récompense une carte majeure de Sombrelune de Folie. Une puissance démentielle s\'emparera de vous chaque fois que vous tuerez un ennemi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10832;
UPDATE locales_npc_text SET entry = 10833, Text0_0_loc2 = 'Si vous parvenez à rassembler toutes les cartes de la suite de Bénédictions, vous recevrez en récompense une carte majeure de Sombrelune de Croisade. Elle vous permettra d\'augmenter les dégâts de vos sorts et de vos attaques au cours d\'un combat.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10833;
UPDATE locales_npc_text SET entry = 10834, Text0_0_loc2 = 'Si vous parvenez à rassembler toutes les cartes de la suite de Furies, vous recevrez en récompense une carte majeure de Sombrelune de Vengeance. Lorsque vous porterez cette carte, celui qui oserait vous frapper risquerait de subir une vengeance sacrée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10834;
UPDATE locales_npc_text SET entry = 10835, Text0_0_loc2 = 'Prenez garde, mes amis. Le Traître ressasse ses pensées dans la cour devant nous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10835;
