UPDATE locales_npc_text SET entry = 10841, Text0_0_loc2 = null, Text0_1_loc2 = 'Tu ferais bien de te réveiller très vite, la bleusaille. On est pas dans notre petit jardin à l\'extérieur de Skettis, ici. On est dans la chaîne des Tranchantes ! Un faux mouvement et c\'est le grand plongeon !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10841;
UPDATE locales_npc_text SET entry = 10842, Text0_0_loc2 = 'Ah… eh bien… vous trouverez le long-voyant Javad à la Grande forge, du côté est, un poil vers le nord.', Text0_1_loc2 = 'Ah… eh bien… vous trouverez le long-voyant Javad à la Grande forge, du côté est, un poil vers le nord.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10842;
UPDATE locales_npc_text SET entry = 10843, Text0_0_loc2 = 'Vous trouverez maître Pyreanor parmi les conseillers de Thrall à Fort Grommash, dans la vallée de la Sagesse.', Text0_1_loc2 = 'Vous trouverez maître Pyreanor parmi les conseillers de Thrall à Fort Grommash, dans la vallée de la Sagesse.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10843;
UPDATE locales_npc_text SET entry = 10846, Text0_0_loc2 = 'Je peux vous apprendre la joaillerie. $gIntéressé:Intéressée; ?', Text0_1_loc2 = 'Je peux vous apprendre la joaillerie. $gIntéressé:Intéressée; ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10846;
UPDATE locales_npc_text SET entry = 10847, Text0_0_loc2 = 'Quoi toi vouloir ? Odeur bizarre pour un gangr\'orc...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10847;
UPDATE locales_npc_text SET entry = 10848, Text0_0_loc2 = 'Ici ça pas devenir ta maison. Toi vouloir manger un truc ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10848;
UPDATE locales_npc_text SET entry = 10849, Text0_0_loc2 = 'Parfois ces maudits elfes de sang s\'arrêtent pour réclamer une cochonnerie de composant ou de poison. Je dois veiller à avoir en permanence une bonne réserve de ce genre d\'objets, pour ne pas que le seigneur Illidan entende dire que les Gueules-de-dragon se montrent peu coopératifs...$B$B<Rumpus déglutit avec difficulté.>$B$BIl vaut mieux éviter ça...', Text0_1_loc2 = 'Parfois ces maudits elfes de sang s\'arrêtent pour réclamer une cochonnerie de composant ou de poison. Je dois veiller à avoir en permanence une bonne réserve de ce genre d\'objets, pour ne pas que le seigneur Illidan entende dire que les Gueules-de-dragon se montrent peu coopératifs...$B$B<Rumpus déglutit avec difficulté.>$B$BIl vaut mieux éviter ça...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10849;
UPDATE locales_npc_text SET entry = 10850, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10850;
UPDATE locales_npc_text SET entry = 10851, Text0_0_loc2 = '<Xi\'ri est entouré par des pulsations de lumière bleutée. Le naaru vous accueille comme $gun allié:une alliée; sur le champ de bataille.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10851;
UPDATE locales_npc_text SET entry = 10854, Text0_0_loc2 = '$gFrère:Sœur;, vous êtes maintenant $glié:liée; par le sang à l\'Aile-du-Néant. Toute l\'Aile sera avec vous ! L\'heure est venue de frapper nos ennemis au cœur, mon $gami:amie; ! Nous devons reprendre notre héritage perdu !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10854;
UPDATE locales_npc_text SET entry = 10855, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10855;
UPDATE locales_npc_text SET entry = 10860, Text0_0_loc2 = 'Vous avez dû vous demander comment je faisais pour parler aussi clairement pour un ogre. C\'est un effet secondaire des cristaux qui poussent sur la terrasse autour d\'Ogri\'la. Les ogres qui y demeurent m\'ont offert ce bâton, et son cristal m\'a conféré ce don intellectuel.$B$BGrâce à vos hauts faits répétés à Ogri\'la, j\'espère un jour pouvoir partager ce don avec l\'ensemble de mes frères ogres. Ma reconnaissance vous est acquise, $gmon roi:ma reine;, pour tous vos exploits passés et à venir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10860;
UPDATE locales_npc_text SET entry = 10862, Text0_0_loc2 = 'Hein, encore $gun:une; ? Vous n\'y arriverez jamais...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10862;
UPDATE locales_npc_text SET entry = 10863, Text0_0_loc2 = 'Souviens-toi que tu ne peux choisir qu\'un seul boulot de récolte par jour. Et à part ça, on est toujours à la recherche de cristaux. Tous les minables de péons peuvent en rapporter au campement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10863;
UPDATE locales_npc_text SET entry = 10864, Text0_0_loc2 = 'J\'ai rien de nouveau pour toi, $ggamin:gamine;. T\'es au-dessus de ce genre de basses besognes...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10864;
