UPDATE locales_npc_text SET entry = 10910, Text0_0_loc2 = 'La cristalforge gangrenée peut servir à transformer vos éclats apogides en flacons instables de la bête, qui rendent celui qui les boit plus agile, plus fort et plus robuste.$B$BFaites votre choix ci-dessous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10910;
UPDATE locales_npc_text SET entry = 10911, Text0_0_loc2 = '<Lorsque vous placez les éclats apogides dans la cristalforge gangrenée et que vous actionnez le levier, elle émet un son surnaturel en les réduisant en poudre. Quelques secondes plus tard, votre flacon est éjecté par son « bec ».>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10911;
