UPDATE locales_npc_text SET entry = 10913, Text0_0_loc2 = 'La cristalforge bash\'ir peut servir à transformer vos éclats apogides en flacons instables du sorcier, qui rendent celui qui les boit plus intelligent et plus robuste, en plus d\'améliorer l\'efficacité de ses sorts de dégâts et de soins.$B$BFaites votre choix ci-dessous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10913;
UPDATE locales_npc_text SET entry = 10914, Text0_0_loc2 = '<Lorsque vous placez les éclats apogides dans le creux de la cristalforge bash\'ir et que vous actionnez le levier, elle les désintègre littéralement. Quelques secondes plus tard, votre flacon apparaît à vos pieds.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10914;
UPDATE locales_npc_text SET entry = 10916, Text0_0_loc2 = 'Nous espérons que vous parviendrez à soulager nos maux de têtes. Essayez, vous, d\'avoir dix fils, et qui veulent tous suivre un destin qui n\'est pas fait pour eux !$B$BEnfin, en tant que père, nous faisons notre possible.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10916;
UPDATE locales_npc_text SET entry = 10917, Text0_0_loc2 = 'Avez-vous des fils, $C ?$B$BNous allons vous dire : nous étions en fait bien plus heureux avant de venir à Ogri\'la et d\'être éclairés par les cristaux. Avant, nous aurions pu nous contenter de leur coller une grosse baffe pour les faire taire. Mais maintenant, ils n\'arrêtent pas de nous embêter avec la Garde-ciel sha\'tari.$B$BHeureux les simples d\'esprit !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10917;
UPDATE locales_npc_text SET entry = 10918, Text0_0_loc2 = 'Le petit reste trop idiot pour vous le dire - il manque encore d\'exposition aux cristaux, mais il vous remercie de lui avoir rapporté ces flacons.$B$BBon, maintenant, plus que neuf fils. Rah !$B$B<Les deux têtes de Torku soupirent.>$B$BVous ne voudriez pas nous en prendre un ? On vous le vendrait pas cher...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10918;
UPDATE locales_npc_text SET entry = 10919, Text0_0_loc2 = 'En tant que $gnouvel arrivant:nouvelle arrivante; à Ogri\'la, vous n\'avez pas encore été assez $gexposé:exposée; aux cristaux pour en tirer pleinement bénéfice. De plus, vous n\'avez qu\'une tête.$B$BEn revanche, nous sommes là depuis très longtemps. Dès lors, vous imaginez bien que nous maîtrisons plutôt bien un grand nombre de domaines.$B$BCes derniers temps, nous nous intéressons de près aux démons des camps de forge. Plus précisément, nous avons mis au point un moyen de nous débarrasser définitivement d\'eux.$B$BEn théorie, tout du moins.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10919;
UPDATE locales_npc_text SET entry = 10920, Text0_0_loc2 = 'Nous aimerions bien être en mesure de créer plus d\'une sombrerune par jour, mais ce serait une perte de temps. Ce n\'est tout simplement pas physiquement possible.$B$BCroyez-nous, s\'il y avait ici une autre personne assez intelligente pour nous prêter assistance, nous l\'enrôlerions sur le champ et pourrions forger autant de sombrerunes que nécessaire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10920;
UPDATE locales_npc_text SET entry = 10921, Text0_0_loc2 = 'La cristalforge bash\'ir peut servir à transformer vos éclats apogides en flacons instables du sorcier, qui rendent celui qui les boit plus intelligent et plus robuste, en plus d\'améliorer l\'efficacité de ses sorts de dégâts et de soins.$B$BIl semble cependant que vous n\'ayez pas les dix éclats nécessaires pour en acheter ne serait-ce qu\'un.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10921;
UPDATE locales_npc_text SET entry = 10922, Text0_0_loc2 = 'La cristalforge gangrenée peut servir à transformer vos éclats apogides en flacons instables de la bête, qui rendent celui qui les boit plus agile, plus fort et plus robuste.$B$BIl semble cependant que vous n\'ayez pas les dix éclats nécessaires pour en acheter ne serait-ce qu\'un.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10922;
UPDATE locales_npc_text SET entry = 10923, Text0_0_loc2 = 'Gahk nouveau à Ogri\'la comme minuscule $r. Ogri\'la être paradis comme les ogres d\'en bas disent. Mais Gahk a mal aux têtes à cause des cristaux qui rendent Gahk plus telligent.$B$B<Les deux têtes de Gahk échangent un hochement d\'approbation.>$B$BNous apprendre à faire sombrune cristalformée. Quand nous savoir faire ça, nous casser portail dimensionnel des démons.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10923;
