UPDATE locales_npc_text SET entry = 10978, Text0_0_loc2 = null, Text0_1_loc2 = 'Bonjour, $N. Vous avez besoin d\'arriver rapidement au Raie\'odrome de Noirbois, notre base dans les monts Skethyl ? J\'ai une jolie petite raie du Néant prête à partir.$B$BVous voulez y aller maintenant ? Vous n\'avez qu\'un mot à dire, $gmon grand:poupée; !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10978;
UPDATE locales_npc_text SET entry = 10979, Text0_0_loc2 = 'Salut, $gm\'sieur:m\'dame; ! Si vous voulez, j\'ai une raie du Néant qu\'attend pour vous emporter à l\'avant-poste de la Garde-ciel, en haut des Tranchantes.$B$BT\'nez-moi au courant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10979;
