UPDATE locales_npc_text SET entry = 10988, Text0_0_loc2 = 'Z\'avez donc rien d\'mieux à faire que d\'venir pérorer, $gmon p\'tit:ma p\'tite; ?$B$BAllez donc à la filoche et montrez-nous d\'quoi vous êtes capable !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10988;
