UPDATE locales_npc_text SET entry = 10992, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vois que vous avez mis à profit le temps que vous passez par ici, $c.$B$BPassez-en plus ! Les démons sont encore là, recrue !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10992;
UPDATE locales_npc_text SET entry = 10994, Text0_0_loc2 = null, Text0_1_loc2 = 'Votre nom est sur toutes les lèvres dans l\'avant-poste. Mais ne prenez pas la grosse tête, je sais que vous pouvez faire mieux !$B$BJe vous ai à l\'œil, recrue.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10994;
UPDATE locales_npc_text SET entry = 10995, Text0_0_loc2 = null, Text0_1_loc2 = 'Très bien, je l\'admets, vous avez fait vos preuves, $N, et même plus que nécessaire.$B$BSi j\'étais aussi dure avec vous avant, c\'est que j\'avais vu l\'étincelle d\'un vrai Garde-ciel en vous. J\'ose dire que vous êtes $gle meilleur:la meilleure; de nos as !$B$BSi vous voulez encore nous donner un coup de main, je serai fière de vous confier un autre train de bombes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10995;
