UPDATE locales_npc_text SET entry = 10999, Text0_0_loc2 = 'Mon $gami:amie; ! Il y avait trop longtemps. Que pouvons-nous faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 10999;
UPDATE locales_npc_text SET entry = 11000, Text0_0_loc2 = 'Qu\'est-ce que vous cherchez ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11000;
UPDATE locales_npc_text SET entry = 11001, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous savez, $N, c\'est vraiment un honneur de travailler avec vous !$B$BLorsque le domptage vous laissera un moment, on pourrait peut-être $gdîner ensemble:se faire une soirée entre filles; ?$B$BOh, j\'ai pensé à vous dire que nous pouvons maintenant vous envoyer très rapidement à notre base dans les monts Skethyl ? Si ça vous intéresse, parlez-en à la dresseuse Irena.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11001;
UPDATE locales_npc_text SET entry = 11002, Text0_0_loc2 = null, Text0_1_loc2 = 'Ça fait un moment que vous êtes souvent dans le coin, mais on arrive jamais à se croiser pendant les sorties après le travail.$B$BC\'est moi ou... ?$B$B<Khatie se force à sourire.>$B$BEnfin, je voulais juste vous dire que je... on apprécie beaucoup tout ce que vous avez fait pour la Garde-ciel !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11002;
UPDATE locales_npc_text SET entry = 11004, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'espère que vous penserez pas que je vous harcèle, ou un truc du genre, $N.$B$BEnfin, je sais que vous êtes vraiment célèbre dans la Garde-ciel maintenant, et bon... je suis sûre que vous avez mieux à faire que de passer votre temps avec une pauvre péone comme moi.$B$BMais si jamais vous voulez qu\'on se retrouve un soir, ou si vous voulez dompter encore quelques raies, c\'est quand vous voulez ! Je serai là.$B$BVous me manquez...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11004;
UPDATE locales_npc_text SET entry = 11007, Text0_0_loc2 = 'Les reliques sont vraiment un grand mystère. Peut-être qu\'en les étudiant régulièrement, vous obtiendrez une meilleure appréciation de leurs capacités.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11007;
UPDATE locales_npc_text SET entry = 11008, Text0_0_loc2 = 'La force n\'est pas dans l\'acier, ô $gexalté:exaltée;. Elle est dans la chair. L\'acier tire sa force du bras qui le manie. Mais passons... en quoi pouvons-nous vous aider, ô $gexalté:exaltée; ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11008;
UPDATE locales_npc_text SET entry = 11009, Text0_0_loc2 = '<La tête gauche de Chu\'a\'lor prend la parole.>$B$BNous trouvons votre zèle pour notre cause très attachant...$B$B<Sa tête droite conclut.>$B$B... et nous voulons que vous sachiez combien nous apprécions vos efforts !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11009;
UPDATE locales_npc_text SET entry = 11010, Text0_0_loc2 = 'On commence à révérer votre nom dans la région.$B$B<L\'œil gauche de Chu\'a\'lor vous lance un regard perçant.>$B$BContinuez comme ça, $N !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11010;
UPDATE locales_npc_text SET entry = 11011, Text0_0_loc2 = 'Vous avez commencé par devenir $groi:reine; des ogres d\'en bas, puis vous avez su provoquer des sentiments exaltés parmi nous autres à Ogri\'la.$B$BC\'est une chose sans précédent pour $gun:une:r; $R, et pourtant vous en êtes une preuve vivante. Je vous remercie, $N, et nous avons tous envers vous une dette dont nous ne pourrons jamais entièrement nous acquitter.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11011;
