UPDATE locales_npc_text SET entry = 11126, Text0_0_loc2 = 'Il est hors de question de laisser ce crime sans solution. L’emplacement de l’auberge et la violence de sa destruction rendent l’affaire fort délicate. Faites preuve de beaucoup de respect en inspectant les lieux, $gmon ami:mon amie;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11126;
UPDATE locales_npc_text SET entry = 11128, Text0_0_loc2 = 'J’accepte votre aide pour cette inspection, $C. Cependant, je me réserve de mener mes propres vérifications sur vos trouvailles… et sur vos motivations.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11128;
