UPDATE locales_npc_text SET entry = 11189, Text0_0_loc2 = 'Je passe mes journées à récupérer des pièces détachées pour le zeppelin, et Freeza n\'a même pas la décence de me verser un salaire horaire. Mais vous n\'êtes pas là à ce sujet, n\'est-ce pas ? Je parie que vous voulez savoir où se trouve le zeppelin en ce moment. Eh bien, vous avez de la chance : j\'ai mon zeppelinomètre avec moi !', Text0_1_loc2 = 'Je passe mes journées à récupérer des pièces détachées pour le zeppelin, et Freeza n\'a même pas la décence de me verser un salaire horaire. Mais vous n\'êtes pas là à ce sujet, n\'est-ce pas ? Je parie que vous voulez savoir où se trouve le zeppelin en ce moment. Eh bien, vous avez de la chance : j\'ai mon zeppelinomètre avec moi !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11189;
