UPDATE locales_npc_text SET entry = 11211, Text0_0_loc2 = 'Hein ?!? Vous voulez vraiment acheter à nous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11211;
UPDATE locales_npc_text SET entry = 11215, Text0_0_loc2 = 'Vous ne voyez pas que j\'ai des gens à voir et des choses à faire ? Faites place, misérable !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11215;
UPDATE locales_npc_text SET entry = 11217, Text0_0_loc2 = 'J’imagine que vous savez qui je suis, le contraire serait trop étonnant. Et vous insistez quand même pour m’importuner…$B$BQue pouvez-vous bien avoir de si important à dire qui justifierait que je m’y intéresse ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11217;
UPDATE locales_npc_text SET entry = 11219, Text0_0_loc2 = 'Oh allons, ne soyez pas ridicules. Je suis Budd Truducou, le seul, l\'unique !$B$BJ\'ai exploré le monde du sommet du mont Hyjal aux profondeurs du Temple englouti. En fait, c\'est même moi qui est trouvé le nom « Temple englouti ».$B$BOui, c\'est moi. Ça m\'est venu comme ça, d\'un coup.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11219;
