UPDATE locales_npc_text SET entry = 11221, Text0_0_loc2 = 'Hé oh, écoutez-moi bien, $C. Je suis plus vieux que j\'en ai l\'air, et de toute façon, chez moi, la valeur n\'a pas attendu le nombre des années.$B$BVous n\'avez pas besoin de me croire sur parole. Vous trouverez des affiches à ma gloire dans toutes les villes. Je suis partout !$B$BAh, une chose quand même… Ignorez celles qui commencent par « ON RECHERCHE ». Simples malentendus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 11221;
